CREATE FUNCTION [dbo].[UDF_GetTotalBudgetSimplified]
(
	@scenarioId AS VARCHAR(6)
)
RETURNS @Output TABLE (TotalDollars MONEY NULL
	,EnergyUsage DECIMAL(38,2) NULL
	,DemandUsage DECIMAL(38,2) NULL)
AS
BEGIN
	DECLARE @fy AS INT, @hasBudget AS BIT, @IsMidYearBudget AS BIT, @actualStartMonth AS INT = 0, @publishedBillingPeriod AS INT = 0;

    SELECT @fy = FY,
        @IsMidYearBudget = CASE WHEN ActualDataEndMonth <> 0 THEN 1 ELSE 0 END,
        @actualStartMonth = (FY - 1) * 100 + 7,
        @publishedBillingPeriod = ActualDataEndMonth
    FROM budget.Scenario
    WHERE ID = @scenarioId;

	IF EXISTS (SELECT 1
		FROM budget.StagingBudget a
		WHERE a.BudgetStageID IN (2,3,4,5,6,7,8,10) AND a.ScenarioId = @scenarioId
		GROUP BY a.ScenarioId
		HAVING COUNT(DISTINCT a.BudgetStageID) = 8)
	BEGIN
		SET	@hasBudget = 1;
	END;
	ELSE
	BEGIN
		SET @hasBudget = 0;
	END;

	IF (@hasBudget = 0)
	BEGIN
		INSERT INTO @Output VALUES (0,0,0);
	END;
	ELSE
	BEGIN
		IF (@IsMidYearBudget = 0)
		BEGIN
			INSERT INTO @Output
			SELECT SUM(TotalDollars) AS TotalDollars ,SUM(EnergyUsage) AS EnergyUsage ,SUM(DemandUsage) AS DemandUsage
			FROM budget.StagingBudget
			WHERE ScenarioId = @scenarioId AND BudgetStageID IN (2, 3, 4, 5, 6, 7, 8, 10);
		END;
		ELSE
		BEGIN
			INSERT INTO	@Output (TotalDollars,  EnergyUsage, DemandUsage)
			SELECT ISNULL(bgt.TotalDollars, 0) + ISNULL(ep.TotalDollars, 0) AS TotalDollars,
				ISNULL(bgt.EnergyUsage, 0) + ISNULL(ep.EnergyUsage, 0) AS EnergyUsage,
				ISNULL(bgt.DemandUsage, 0) + ISNULL(ep.DemandUsage, 0) AS DemandUsage
			FROM (SELECT SUM(ISNULL(TotalDollars, 0)) AS TotalDollars
					,SUM(ISNULL(EnergyUsage, 0)) AS EnergyUsage
					,SUM(ISNULL(DemandUsage, 0)) AS DemandUsage
				FROM budget.StagingBudget AS a
				WHERE a.ScenarioId = @scenarioId
					AND BudgetStageID IN (2, 3, 4, 5, 6, 7, 8, 10)
					AND a.BudgetBillingPeriod > CAST(@publishedBillingPeriod AS INT)) AS bgt,
				(SELECT SUM(ISNULL(b.TotalDollars, 0)) AS TotalDollars
					,SUM(ISNULL(b.EnergyUsage, 0)) AS EnergyUsage
					,SUM(ISNULL(b.DemandUsage, 0)) AS DemandUsage
				FROM dbo.UDF_GetActualDataFromEC3(CAST(@publishedBillingPeriod AS VARCHAR(6)), CAST(@actualStartMonth AS VARCHAR(6)), @scenarioId) AS b
				WHERE b.BillingPeriod < =  CAST(@publishedBillingPeriod AS INT)
					AND b.AgencyCodeOEC IN  (SELECT a.AgencyCodeOEC
						FROM Preload.Agency AS a
							INNER JOIN Preload.AgencyByFY AS c ON a.AgencyId = c.AgencyId WHERE c.FY = @fy)
					AND b.BudgetGroupID IN  (SELECT BudgetGroupID FROM preload.BudgetGroupByFY WHERE FY = @fy)
			) AS ep;
		END;
	END;
 
	RETURN;
END;
