CREATE PROCEDURE [Published].[usp_AfterPublishingDashboardAndReportTablesRefresh]
AS
BEGIN	
	PRINT 'Started at: ' + CAST(GETDATE() AS VARCHAR(23));
	PRINT 'Loading Report.EC3Dashboard_AgencyDashboard_CostPerUnitUsageRetrieveCitywideRolling12Months ...................';

	TRUNCATE TABLE Report.EC3Dashboard_AgencyDashboard_CostPerUnitUsageRetrieveCitywideRolling12Months;

	INSERT INTO Report.EC3Dashboard_AgencyDashboard_CostPerUnitUsageRetrieveCitywideRolling12Months
		(AgencyCodeOEC
		,FiscalYear
		,BillingPeriod
		,[Label]
		,CalendarYear
		,[MonthName]
		,MonthYear
		,EnergyType
		,EnergyUsage
		,DemandUsage
		,BilledAmount
		,CostPerUnitUsage
		,BTU
		,CurrentPeriod)
	EXEC Report.USP_AgencyDashboard_CostPerUnitUsageRetrieveCitywideRolling12Months @publishedBillingPeriod = DEFAULT;

	-----------------------------------------------------------
	PRINT 'Started at: ' + CAST(GETDATE() AS VARCHAR(23));
	PRINT 'Loading Report.EC3Dashboard_AgencyDashboard_EstimatedAccountsPercentageRetrieveCitywideRolling12Months ...................';

	TRUNCATE TABLE Report.EC3Dashboard_AgencyDashboard_EstimatedAccountsPercentageRetrieveCitywideRolling12Months;

	INSERT INTO Report.EC3Dashboard_AgencyDashboard_EstimatedAccountsPercentageRetrieveCitywideRolling12Months
		(AgencyCodeOEC
		,BillingPeriod
		,Category
		,[Label]
		,[Count]
		,TotalCount
		,[Percentage])
	EXEC Report.USP_AgencyDashboard_EstimatedAccountsPercentageRetrieveCitywideRolling12Months @publishedBillingPeriod = DEFAULT;

	-----------------------------------------------------------
	PRINT 'Started at: ' + CAST(GETDATE() AS VARCHAR(23));
	PRINT 'Loading Report.EC3Dashboard_AgencyDashboard_AnnualEnergyUsageRetrieveCitywideCurrentVsPriorFY ...................';

	TRUNCATE TABLE Report.EC3Dashboard_AgencyDashboard_AnnualEnergyUsageRetrieveCitywideCurrentVsPriorFY;

	INSERT INTO Report.EC3Dashboard_AgencyDashboard_AnnualEnergyUsageRetrieveCitywideCurrentVsPriorFY
		(AgencyCodeOEC
		,FiscalYear
		,BillingPeriod
		,CalendarYear
		,[MonthName]
		,MonthYear
		,EnergyUsage
		,DemandUsage
		,BilledAmount
		,BTU
		,CurrentPeriod)
	EXEC Report.USP_AgencyDashboard_AnnualEnergyUsageRetrieveCitywideCurrentVsPriorFY;

	-----------------------------------------------------------
	PRINT 'Started at: ' + CAST(GETDATE() AS VARCHAR(23));
	PRINT 'Loading Report.EC3Dashboard_AgencyDashboard_HeatingCoolingDegreeDaysCounts ...................';

	TRUNCATE TABLE Report.EC3Dashboard_AgencyDashboard_HeatingCoolingDegreeDaysCounts;

	INSERT INTO Report.EC3Dashboard_AgencyDashboard_HeatingCoolingDegreeDaysCounts
		(FiscalYear
		,Category
		,BaseHeatingDegreeDays
		,BaseCoolingDegreeDays
		,NormalCoolingDegreeDays
		,NormalHeatingDegreeDays
		,SortOrder)
	EXEC Report.USP_AgencyDashboard_HeatingCoolingDegreeDaysCounts;

	-----------------------------------------------------------
	PRINT 'Started at: ' + CAST(GETDATE() AS VARCHAR(23));
	PRINT 'Loading Report.EC3Dashboard_AgencyDashboard_EstimatedAccountsPercentageRetrieveAgencyRolling12Months ...................';

	TRUNCATE TABLE Report.EC3Dashboard_AgencyDashboard_EstimatedAccountsPercentageRetrieveAgencyRolling12Months;

	DECLARE @Agency0 AS VARCHAR(6);
	DECLARE AgencyCursor CURSOR FOR SELECT AgencyCodeOEC FROM Billing.AgencyDivision WHERE Inactive = 'N' AND AgencyCodeOEC IS NOT NULL;
	OPEN AgencyCursor;
	FETCH NEXT FROM AgencyCursor INTO @Agency0;
	WHILE (@@FETCH_STATUS = 0)
	BEGIN
		INSERT INTO Report.EC3Dashboard_AgencyDashboard_EstimatedAccountsPercentageRetrieveAgencyRolling12Months
			(AgencyCodeOEC, BillingPeriod, category, [Label], [Count], TotalCount, [Percentage])
		EXEC Report.USP_AgencyDashboard_EstimatedAccountsPercentageRetrieveAgencyRolling12Months @agencyCode = @Agency0,@publishedBillingPeriod = DEFAULT;
		FETCH NEXT FROM AgencyCursor INTO @Agency0;
	END; 
	CLOSE AgencyCursor;
	DEALLOCATE AgencyCursor;

	-----------------------------------------------------------
	PRINT 'Started at: ' + CAST(GETDATE() AS VARCHAR(23));
	PRINT 'Loading Report.EC3Dashboard_AgencyDashboard_AgencyEnergyUsageByFacilityStatistics ...................';

	TRUNCATE TABLE Report.EC3Dashboard_AgencyDashboard_AgencyEnergyUsageByFacilityStatistics;

	DECLARE @Agency1 AS VARCHAR(6);
	DECLARE AgencyCursor CURSOR FOR SELECT AgencyCodeOEC FROM Billing.AgencyDivision WHERE Inactive = 'N' AND AgencyCodeOEC IS NOT NULL;
	OPEN AgencyCursor;
	FETCH NEXT FROM AgencyCursor INTO @Agency1;
	WHILE (@@FETCH_STATUS = 0)
	BEGIN
		INSERT INTO Report.EC3Dashboard_AgencyDashboard_AgencyEnergyUsageByFacilityStatistics
			(AgencyCodeOEC, BillingPeriod, Category, [Label], [Count], TotalCount, [Percent])
		EXEC Report.USP_AgencyDashboard_AgencyEnergyUsageByFacilityStatistics @agencyCode = @Agency1,@countThreshold = 10, @percentThreshold = 80.0;
		FETCH NEXT FROM AgencyCursor INTO @Agency1;
	END; 
	CLOSE AgencyCursor;
	DEALLOCATE AgencyCursor;

	-----------------------------------------------------------
	PRINT 'Started at: ' + CAST(GETDATE() AS VARCHAR(23));
	PRINT 'Loading Report.EC3Dashboard_AgencyDashboard_MonthlyEnergyUsageRetrieveAgencyCurrentFY ...................';

	TRUNCATE TABLE Report.EC3Dashboard_AgencyDashboard_MonthlyEnergyUsageRetrieveAgencyCurrentFY;

	DECLARE @Agency2 AS VARCHAR(6); 
	DECLARE AgencyCursor CURSOR FOR SELECT AgencyCodeOEC FROM Billing.AgencyDivision WHERE Inactive = 'N' AND AgencyCodeOEC IS NOT NULL;
	OPEN AgencyCursor;
	FETCH NEXT FROM AgencyCursor INTO @Agency2;
	WHILE (@@FETCH_STATUS = 0)
	BEGIN
		INSERT INTO Report.EC3Dashboard_AgencyDashboard_MonthlyEnergyUsageRetrieveAgencyCurrentFY
			(AgencyCodeOEC, FiscalYear, BillingPeriod, CalendarYear, [MonthName], MonthYear, EnergyType, EnergyUsage, DemandUsage, BilledAmount, CostPerUnitUsage, BTU, CurrentPeriod)
		EXEC Report.USP_AgencyDashboard_MonthlyEnergyUsageRetrieveAgencyCurrentFY @agencyCode = @Agency2;
		FETCH NEXT FROM AgencyCursor INTO @Agency2;
	END; 
	CLOSE AgencyCursor;
	DEALLOCATE AgencyCursor;

	-----------------------------------------------------------
	PRINT 'Started at: ' + CAST(GETDATE() AS VARCHAR(23));
	PRINT 'Loading Report.EC3Dashboard_AgencyDashboard_CostPerUnitUsageRetrieveAgencyRolling12Months ...................';

	TRUNCATE TABLE Report.EC3Dashboard_AgencyDashboard_CostPerUnitUsageRetrieveAgencyRolling12Months;

	DECLARE @Agency3 AS VARCHAR(6);
	DECLARE AgencyCursor CURSOR FOR SELECT AgencyCodeOEC FROM Billing.AgencyDivision WHERE Inactive = 'N' AND AgencyCodeOEC IS NOT NULL;
	OPEN AgencyCursor;
	FETCH NEXT FROM AgencyCursor INTO @Agency3;
	WHILE (@@FETCH_STATUS = 0)
	BEGIN
		INSERT INTO Report.EC3Dashboard_AgencyDashboard_CostPerUnitUsageRetrieveAgencyRolling12Months
		(AgencyCodeOEC, FiscalYear, BillingPeriod, [Label], CalendarYear, [MonthName], MonthYear, EnergyType, EnergyUsage, DemandUsage, BilledAmount, CostPerUnitUsage, BTU, CurrentPeriod)
		EXEC Report.USP_AgencyDashboard_CostPerUnitUsageRetrieveAgencyRolling12Months @agencyCode = @Agency3,@publishedBillingPeriod = DEFAULT;
		FETCH NEXT FROM AgencyCursor INTO @Agency3;
	END; 
	CLOSE AgencyCursor;
	DEALLOCATE AgencyCursor;

	-----------------------------------------------------------
	PRINT 'Started at: ' + CAST(GETDATE() AS VARCHAR(23));
	PRINT 'Loading Report.EC3Dashboard_AgencyDashboard_AnnualEnergyUsageRetrieveAgencyCurrentVsPriorFY ...................';

	TRUNCATE TABLE Report.EC3Dashboard_AgencyDashboard_AnnualEnergyUsageRetrieveAgencyCurrentVsPriorFY;

	DECLARE @Agency4 AS VARCHAR(6);
	DECLARE AgencyCursor CURSOR FOR SELECT AgencyCodeOEC FROM Billing.AgencyDivision WHERE Inactive = 'N' AND AgencyCodeOEC IS NOT NULL;
	OPEN AgencyCursor;
	FETCH NEXT FROM AgencyCursor INTO @Agency4;
	WHILE (@@FETCH_STATUS = 0)
	BEGIN
		INSERT INTO Report.EC3Dashboard_AgencyDashboard_AnnualEnergyUsageRetrieveAgencyCurrentVsPriorFY
		(AgencyCodeOEC, FiscalYear, BillingPeriod, CalendarYear, [MonthName], MonthYear, EnergyUsage, DemandUsage, BilledAmount, BTU, CurrentPeriod)
		EXEC Report.USP_AgencyDashboard_AnnualEnergyUsageRetrieveAgencyCurrentVsPriorFY @agencyCode = @Agency4;
		FETCH NEXT FROM AgencyCursor INTO @Agency4;
	END; 
	CLOSE AgencyCursor;
	DEALLOCATE AgencyCursor;

	-----------------------------------------------------------
	PRINT 'Started at: ' + CAST(GETDATE() AS VARCHAR(23));
	PRINT 'Loading eport.EC3Dashboard_AgencyDashboard_UsageDemandDeviationCurrentVsPriorPeriod ...................';

	TRUNCATE TABLE Report.EC3Dashboard_AgencyDashboard_UsageDemandDeviationCurrentVsPriorPeriod;

	DECLARE @Agency5 AS VARCHAR(6);
	DECLARE AgencyCursor CURSOR FOR SELECT AgencyCodeOEC FROM Billing.AgencyDivision WHERE Inactive = 'N' AND AgencyCodeOEC IS NOT NULL;
	OPEN AgencyCursor;
	FETCH NEXT FROM AgencyCursor INTO @Agency5;
	WHILE (@@FETCH_STATUS = 0)
	BEGIN
		INSERT INTO Report.EC3Dashboard_AgencyDashboard_UsageDemandDeviationCurrentVsPriorPeriod
		(AgencyCodeOEC, BillingPeriod, Category, [Label], EnergyType, [Count], TotalCount, [Percentage])
		EXEC Report.USP_AgencyDashboard_UsageDemandDeviationCurrentVsPriorPeriod @agencyCode = @Agency5,@usageThreshold = 25.0,@demandThreshold = 25.0;
		FETCH NEXT FROM AgencyCursor INTO @Agency5;
	END; 
	CLOSE AgencyCursor;
	DEALLOCATE AgencyCursor;

	-----------------------------------------------------------
	PRINT 'Started at: ' + CAST(GETDATE() AS VARCHAR(23));
	PRINT 'Loading Report.EC3Dashboard_AgencyDashboard_MonthlyEnergyChangesComparedToSameMonth...................';

	TRUNCATE TABLE Report.EC3Dashboard_AgencyDashboard_MonthlyEnergyChangesComparedToSameMonth;

	DECLARE @Agency6 AS VARCHAR(6);
	DECLARE AgencyCursor CURSOR FOR SELECT AgencyCodeOEC FROM Billing.AgencyDivision WHERE Inactive = 'N' AND AgencyCodeOEC IS NOT NULL;
	OPEN AgencyCursor;
	FETCH NEXT FROM AgencyCursor INTO @Agency6;
	WHILE (@@FETCH_STATUS = 0)
	BEGIN
		INSERT INTO  Report.EC3Dashboard_AgencyDashboard_MonthlyEnergyChangesComparedToSameMonth
		(Agency
		,[Month]
		,AgencyChange
		,CityChange)
		EXEC Report.USP_AgencyDashboard_MonthlyEnergyChangesComparedToSameMonth @AgencyOEC = @Agency6;
		FETCH NEXT FROM AgencyCursor INTO @Agency6;
	END; 
	CLOSE AgencyCursor;
	DEALLOCATE AgencyCursor;

	-----------------------------------------------------------
	PRINT 'Started at: ' + CAST(GETDATE() AS VARCHAR(23));
	PRINT 'Loading Report.EC3Dashboard_AgencyDashboard_AnnualEnergyUseComparedtoCity ...................';

	TRUNCATE TABLE Report.EC3Dashboard_AgencyDashboard_AnnualEnergyUseComparedtoCity;

	DECLARE @Agency7 AS VARCHAR(6); 
	DECLARE AgencyCursor CURSOR FOR SELECT AgencyCodeOEC FROM Billing.AgencyDivision WHERE Inactive = 'N' AND AgencyCodeOEC IS NOT NULL;
	OPEN AgencyCursor;
	FETCH NEXT FROM AgencyCursor INTO @Agency7;
	WHILE (@@FETCH_STATUS = 0)
	BEGIN
		INSERT INTO Report.EC3Dashboard_AgencyDashboard_AnnualEnergyUseComparedtoCity (Agency, FiscalYear, AgencyRatio, CitywideRatio)
		EXEC Report.USP_AgencyDashboard_AnnualEnergyUseComparedtoCity @AgencyOEC = @Agency7;
		FETCH NEXT FROM AgencyCursor INTO @Agency7;
	END; 
	CLOSE AgencyCursor;
	DEALLOCATE AgencyCursor;

	-----------------------------------------------------------
	PRINT 'Started at: ' + CAST(GETDATE() AS VARCHAR(23));
	PRINT 'Loading Report.EC3Dashboard_AgencyDashboard_FY_UtilityEnergyAgencyVsCW ...................';
	
	TRUNCATE TABLE Report.EC3Dashboard_AgencyDashboard_FY_UtilityEnergyAgencyVsCW;

	DECLARE @CurrentPublishedBillingPeriodx AS VARCHAR(6) = (SELECT MAX(PublishedBillingPeriod) FROM Published.MonthlyPublishedSummaryData);
	DECLARE @currentFiscalYeary AS VARCHAR(4) = (SELECT dbo.CalculateFiscalYear(@CurrentPublishedBillingPeriodx));
	DECLARE @StartFiscalYearx AS INT = SUBSTRING(dbo.JulyPeriodFiveFiscalYearsAgo(@currentFiscalYeary), 1, 4);

	SELECT talsbA.FiscalYear, SUM(talsbA.BTU) AS CWBTU
	INTO #CityWideBTUs
	FROM Published.TemporalAccountLevelSummaryByAgency AS talsba
	WHERE talsbA.EffectiveStartPeriod < =  @CurrentPublishedBillingPeriodx
		AND talsbA.EffectiveEndPeriod > @CurrentPublishedBillingPeriodx
		AND talsbA.FiscalYear> = @StartFiscalYearx AND talsbA.FiscalYear <@currentFiscalYeary
	GROUP BY talsbA.FiscalYear;

	INSERT INTO Report.EC3Dashboard_AgencyDashboard_FY_UtilityEnergyAgencyVsCW
		(AgencyOECID
		,FiscalYear
		,CitywideMMBTUs
		,AgencyMMBTUs)
	SELECT 
		talsbA.AgencyCodeOEC
		,talsbA.FiscalYear
		,0
		,SUM(talsbA.BTU) 
	FROM Published.TemporalAccountLevelSummaryByAgency AS talsba
	WHERE talsbA.EffectiveStartPeriod < =  @CurrentPublishedBillingPeriodx
		AND talsbA.EffectiveEndPeriod > @CurrentPublishedBillingPeriodx
		AND talsbA.FiscalYear> = @StartFiscalYearx AND talsbA.FiscalYear <@currentFiscalYeary
	GROUP BY talsbA.FiscalYear, talsbA.AgencyCodeOEC
	ORDER BY talsbA.FiscalYear;

	UPDATE t
	SET CitywideMMBTUs = cwbu.CWBTU
	FROM #CityWideBTUs AS cwbu
		INNER JOIN Report.EC3Dashboard_AgencyDashboard_FY_UtilityEnergyAgencyVsCW AS t ON t.Fiscalyear = cwbu.FiscalYear;

	-- insert parent agencies
	INSERT INTO Report.EC3Dashboard_AgencyDashboard_FY_UtilityEnergyAgencyVsCW
		(AgencyOECID
		,FiscalYear
		,CitywideMMBTUs
		,AgencyMMBTUs)
	SELECT ad2.AgencyCodeOEC ParentAgOEC,t.FiscalYear, MAX(t.CitywideMMBTUs), SUM(t.AgencyMMBTUs)
	FROM Billing.AgencyDivision AS adp
		INNER JOIN Report.EC3Dashboard_AgencyDashboard_FY_UtilityEnergyAgencyVsCW t ON adp.AgencyCodeOEC = t.AgencyOECID
		INNER JOIN Billing.AgencyDivision AS ad2 ON ad2.AgencyDivisionSeqid = adp.ParentAgencyDivisionSeqid
	GROUP BY ad2.AgencyCodeOEC, t.FiscalYear;

	-----------------------------------------------------------
	PRINT 'Started at: ' + CAST(GETDATE() AS VARCHAR(23));
	PRINT 'Loading Report.EC3Dashboard_AgencyDashboard_FY_CO2EmissionsAgencyVsCW ...................';

	TRUNCATE TABLE Report.EC3Dashboard_AgencyDashboard_FY_CO2EmissionsAgencyVsCW;

	DECLARE @CurrentPublishedBillingPeriody AS VARCHAR(6) = (SELECT MAX(PublishedBillingPeriod) FROM Published.MonthlyPublishedSummaryData)
	DECLARE @currentFiscalYearx AS VARCHAR(4) = (SELECT dbo.CalculateFiscalYear(@CurrentPublishedBillingPeriody));
	DECLARE @StartFiscalYeary AS INT = SUBSTRING(dbo.JulyPeriodFiveFiscalYearsAgo(@currentFiscalYearx), 1, 4);

	SELECT talsbA.FiscalYear, SUM(talsbA.CO2) CWC2
	INTO #CityWideCO2
	FROM Published.TemporalAccountLevelSummaryByAgency AS talsba
	WHERE talsbA.EffectiveStartPeriod < =  @CurrentPublishedBillingPeriody
		AND talsbA.EffectiveEndPeriod > @CurrentPublishedBillingPeriody
		AND talsbA.FiscalYear> = @StartFiscalYeary AND talsbA.FiscalYear <@currentFiscalYearx
	GROUP BY talsbA.FiscalYear;

	INSERT INTO Report.EC3Dashboard_AgencyDashboard_FY_CO2EmissionsAgencyVsCW
		(AgencyOECID
		,FiscalYear
		,CitywideCO2Emissions
		,AgencyCO2Emissions)
	SELECT 
		talsbA.AgencyCodeOEC
		,talsbA.FiscalYear
		,0
		,SUM(talsbA.CO2) 
	FROM Published.TemporalAccountLevelSummaryByAgency AS talsba
	WHERE talsbA.EffectiveStartPeriod < =  @CurrentPublishedBillingPeriody
		AND talsbA.EffectiveEndPeriod > @CurrentPublishedBillingPeriody
		AND talsbA.FiscalYear> = @StartFiscalYeary AND talsbA.FiscalYear < @currentFiscalYearx
	GROUP BY talsbA.FiscalYear, talsbA.AgencyCodeOEC
	ORDER BY talsbA.FiscalYear;

	UPDATE t
	SET t.CitywideCO2Emissions = cwco.CWC2
	FROM Report.EC3Dashboard_AgencyDashboard_FY_CO2EmissionsAgencyVsCW AS t
	INNER JOIN #CityWideCO2 AS cwco ON t.Fiscalyear = cwco.FiscalYear;

	INSERT INTO Report.EC3Dashboard_AgencyDashboard_FY_CO2EmissionsAgencyVsCW
		(AgencyOECID
		,FiscalYear
		,CitywideCO2Emissions
		,AgencyCO2Emissions)
	SELECT ad2.AgencyCodeOEC AS ParentAgOEC, t.FiscalYear, MAX(t.CitywideCO2Emissions), SUM(t.AgencyCO2Emissions)
	FROM Billing.AgencyDivision AS adp
		INNER JOIN Report.EC3Dashboard_AgencyDashboard_FY_CO2EmissionsAgencyVsCW AS t ON adp.AgencyCodeOEC = t.AgencyOECID
		INNER JOIN Billing.AgencyDivision AS ad2 ON ad2.AgencyDivisionSeqid = adp.ParentAgencyDivisionSeqid
	GROUP BY ad2.AgencyCodeOEC, t.FiscalYear;

	-------------------------------------------
	DECLARE @PBP AS VARCHAR(6) = (SELECT MAX(PublishedBillingPeriod) FROM Published.MonthlyPublishedSummaryData);
	EXEC Billing.usp_Load_EstimatedActualCounts @PublishedBillingPeriod = @PBP, @EmailAddress = 'zdurmaz@dcas.nyc.gov';

	-------------------------------------------------------------
	PRINT 'Started at: ' + CAST(GETDATE() AS VARCHAR(23));
	PRINT 'Loading Published.CustomReportsData ...................';

	DROP TABLE Published.CustomReportsData;

	SELECT DISTINCT AA.AgencyDivisionSeqid AS AgencyDivisionSeqId
		,AA.AgencyCodeOEC
		,AA.AgencyName + ' (' + AD.AgencyShortDesc + ') ' AS AgencyName
		,AA.FacilitySeqid AS FacilitySeqId
		,AA.OecFacilityNumber
		,AA.FacilityName
		,AA.UtilityCompanySeqid AS UtilityCompanySeqId
		,AA.UtilityCompany
		,AA.EnergyType
		,AA.InvoiceAccountBillingGroupDesc AS InvoiceBillingGroup
		,AA.CurrentInvoiceAccountBillingGroup AS InvoiceBillingGroupSeqId
		,AA.UniqueAccountSeqID AS UniqueAccountSeqId
		,AA.CurrentAccountNumber
		,A.UtilityServiceAccountName
		,AM.UniqueMeterSeqid AS UniqueMeterSeqId
		,AM.CurrentMeterNumber
		,UTC.Id AS TariffClassSeqId
		,UTC.[Name] AS TariffClassDescription
		,A.Tension
		,AD.IsSelfFunded AS FundedType
		,CASE WHEN IAG.BillingType = 'E' THEN 'Electrical' ELSE 'Manual' END AS BillingType
		INTO Published.CustomReportsData
	FROM Published.AccountLevelRawDataForCurrentPeriod AS AA
		INNER JOIN Billing.AgencyDivision AS AD ON AD.AgencyCodeOEC = AA.AgencyCodeOEC
		INNER JOIN Billing.Account AS A ON A.AccountSeqid = AA.AccountSeqid
		INNER JOIN Billing.UtilityTariffRateInformation AS utri ON utri.UtilityTariffRateInformationSeqid = AA.UtilityTariffRateInformationSeqid
		INNER JOIN Billing.UtilityTariffClass AS UTC ON UTC.[Name] = utri.ShortDesc
		INNER JOIN Billing.InvoiceAccountGroup AS IAG ON IAG.InvoiceAccountGroupSeqid = A.CurrentInvoiceAccountGroup
		LEFT JOIN Published.AccountMeterLevelRawDataForCurrentPeriod AS AM ON AM.AccountSeqid = AA.AccountSeqid
	WHERE AA.BillingPeriod = (SELECT MAX(PublishedBillingPeriod) FROM Published.AccountLevelRawDataForCurrentPeriod);

	UPDATE Published.CustomReportsData SET UtilityServiceAccountName = REPLACE(UtilityServiceAccountName,'  ',' ') WHERE CHARINDEX('  ', UtilityServiceAccountName) > 0;
	UPDATE Published.CustomReportsData SET FacilityName = REPLACE(FacilityName,'  ',' ') WHERE CHARINDEX('  ', FacilityName) > 0;

	CREATE CLUSTERED INDEX PK_CustomReportsData	ON Published.CustomReportsData (UniqueAccountSeqId, UniqueMeterSeqId);

	EXEC sp_updatestats;
END;
