CREATE PROCEDURE [Invoice].[USP_InvoiceGroupContractAdd]
(
	@invoiceId AS INT,@fiscalYear AS VARCHAR(4),@groupId AS INT,@contractId AS INT,@correlationId AS VARCHAR(128) = NULL,@machineName AS VARCHAR(128) = NULL,@processName AS VARCHAR(128) = NULL,@domain AS VARCHAR(25) = NULL,@username AS VARCHAR(25) = NULL,@userId AS INT = NULL,@exceptionDetails AS VARCHAR(MAX) = NULL OUTPUT
)
AS
BEGIN
	SET NOCOUNT ON;

	SELECT @invoiceId = NULLIF(@invoiceId, 0), @fiscalYear = NULLIF(@fiscalYear, ''), @groupId = NULLIF(@groupId, 0), @contractId = NULLIF(@contractId, 0),@correlationId = ISNULL(@correlationId, CONVERT(VARCHAR(128), NEWID())), @machineName = COALESCE(@machineName, @@SERVERNAME, 'NA'),@processName = COALESCE(@processName, QUOTENAME(OBJECT_SCHEMA_NAME(@@PROCID)) + '.' + QUOTENAME(OBJECT_NAME(@@PROCID)), 'NA'), @userId = ISNULL(@userId, 0);

	DECLARE @action AS VARCHAR(25) = 'CREATE', @severity AS VARCHAR(25) = 'INFO', @message AS VARCHAR(255) = 'Added invoice billing group and contract',@messageDetails AS VARCHAR(MAX), @startTime AS DATETIME = GETDATE(), @elapsedTime AS INT, @sendEmail AS BIT = 0, @tranName VARCHAR(128) = CONVERT(VARCHAR(128), NEWID())
	
	SELECT @messageDetails = CONVERT(VARCHAR(MAX), (SELECT @invoiceId AS INVID, @fiscalYear AS FY, @groupId AS GRPID, @contractId AS CTRCTID FOR XML PATH('INPUTS')));

	DECLARE @billingGroupContractId INT, @noteFiller VARCHAR(500), @isElectronicSummaryBilling BIT, @billingPeriod VARCHAR(6), @invoiceGroupId INT, @summaryAcctNumber VARCHAR(15), @invoiceNumAction VARCHAR(30);

	SELECT @billingGroupContractId = BillingGroupContractID
	FROM Billing.BillingGroupContract
	WHERE ContractID = @contractId AND GroupID = @groupId AND FiscalYear = @fiscalYear;

	SELECT @noteFiller = (GroupName + ',' + FiscalYear + ',' + ContractNumber)
	FROM Billing.vwBillingGroupContracts
	WHERE ContractID = @contractId AND GroupID = @groupId AND FiscalYear = @fiscalYear;
	SET @noteFiller = ISNULL(@noteFiller, CONVERT(VARCHAR(10), @groupId) + ',' + @fiscalYear + ',NA');

	SET @isElectronicSummaryBilling = 0;
	
	IF EXISTS (SELECT 1 FROM Billing.vwBillingGroups WHERE GroupID = @groupId AND BillingType = 'E')
	BEGIN
 SET @isElectronicSummaryBilling = 1;
	END;

	SET @invoiceNumAction = '';
	SELECT @summaryAcctNumber = SummaryAccountNumber FROM Billing.vwBillingGroups WHERE GroupID = @groupId;

	SELECT @billingPeriod = InvoiceBillingPeriod FROM [Invoice].[vwInvoices] WHERE InvoiceID = @invoiceId;

	BEGIN TRY
 DECLARE @invoiceGroupTable TABLE(InvoiceGroupID INT);
 DECLARE @billingTable TABLE(InvoiceBillingID INT);

 BEGIN TRAN @tranName;

 -- Add invoice group
 INSERT INTO Invoice.InvoiceGroupContract(InvoiceID, BillingGroupContractID, CreatedBy)
 OUTPUT INSERTED.InvoiceGroupContractID INTO @invoiceGroupTable
 VALUES (@invoiceId, @billingGroupContractId, @userId);

 -- Determine inserted invoice group identifier
 SELECT @invoiceGroupId = CONVERT(INT, InvoiceGroupID) FROM @invoiceGroupTable;

 -- Add associated accounts / billings if electronic billing and summary billing type
 IF (@isElectronicSummaryBilling = 1)
 BEGIN
 INSERT INTO Invoice.InvoiceAccountBilling(InvoiceGroupContractID, InvoiceID, GroupID, ContractID, BillingID,AccountID, AccountNumber, BillingPeriod,BillingPeriodDays, FromDate, ToDate,SplitFY,BillingPeriodDaysPriorFY,BillingPeriodDaysCurrentFY,IsManualEntry, IsEstimated, DateBilled, DateDue,EnergyUnit,EnergyUsage,EnergyUsagePriorFY,EnergyUsageCurrentFY,DemandUsage,DemandUsagePriorFY,DemandUsageCurrentFY,AmountBilled,AmountPaid,AmountPaidPriorFY,AmountPaidCurrentFY,CreatedBy)
 OUTPUT INSERTED.InvoiceBillingID INTO @billingTable
 SELECT @invoiceGroupId, @invoiceId, @groupId, @contractId, B.BillingID,B.AccountID, B.AccountNumber, B.BillingPeriod, B.BillingPeriodDays, B.FromDate, B.ToDate,Billing.UDF_IsSplitFYBilling(@invoiceId, B.FromDate, B.ToDate),CASE WHEN Billing.UDF_IsBillingInPreviousFY(@invoiceId, B.FromDate, B.ToDate) = 1 THEN B.BillingPeriodDays ELSE 0 END BillingPeriodDaysPriorFY,CASE WHEN Billing.UDF_IsBillingInPreviousFY(@invoiceId, B.FromDate, B.ToDate) = 1 THEN 0 ELSE B.BillingPeriodDays END BillingPeriodDaysCurrrentFY,B.IsManualEntry, B.IsEstimated, B.DateBilled, B.DateDue,B.EnergyUnit,B.AccountEnergyUsage,CASE WHEN Billing.UDF_IsBillingInPreviousFY(@invoiceId, B.FromDate, B.ToDate) = 1 THEN B.AccountEnergyUsage ELSE 0 END EnergyUsagePriorFY,CASE WHEN Billing.UDF_IsBillingInPreviousFY(@invoiceId, B.FromDate, B.ToDate) = 1 THEN 0 ELSE B.AccountEnergyUsage END EnergyUsageCurrentFY,B.AccountDemandUsage,CASE WHEN Billing.UDF_IsBillingInPreviousFY(@invoiceId, B.FromDate, B.ToDate) = 1 THEN B.AccountDemandUsage ELSE 0 END DemandUsagePriorFY,CASE WHEN Billing.UDF_IsBillingInPreviousFY(@invoiceId, B.FromDate, B.ToDate) = 1 THEN 0 ELSE B.AccountDemandUsage END DemandUsageCurrentFY,CONVERT(DECIMAL(18, 2), B.AmountBilled),CONVERT(DECIMAL(18, 2), B.AmountPaid),CASE WHEN Billing.UDF_IsBillingInPreviousFY(@invoiceId, B.FromDate, B.ToDate) = 1 THEN CONVERT(DECIMAL(18, 2), B.AmountPaid) ELSE 0 END AmountPaidPriorFY,CASE WHEN Billing.UDF_IsBillingInPreviousFY(@invoiceId, B.FromDate, B.ToDate) = 1 THEN 0 ELSE CONVERT(DECIMAL(18, 2), B.AmountPaid) END AmountPaidCurrentFY,@userId
 FROM Billing.vwAccountBillings AS B
 WHERE B.GroupID = @groupId AND B.BillingPeriod = @billingPeriod AND B.IsLockedForEdit='N'
 AND B.BillingID NOT IN (SELECT DISTINCT BillingID FROM Invoice.InvoiceAccountBilling WHERE GroupID = @groupId);

 -- Update number of billing days in prior FY if there is a difference in FY between from and to dates
 UPDATE BILL
 SET BILL.BillingPeriodDaysPriorFY = BILL.BillingPeriodDays - DATEDIFF(d, CONVERT(DATETIME, CONVERT(VARCHAR(4), YEAR(ToDate)) + '0630', 112), ToDate),BILL.BillingPeriodDaysCurrentFY = DATEDIFF(d, CONVERT(DATETIME, CONVERT(VARCHAR(4), YEAR(ToDate)) + '0630', 112), ToDate)
 FROM Invoice.InvoiceAccountBilling AS BILL
 INNER JOIN @billingTable T ON T.InvoiceBillingID = BILL.InvoiceBillingID
 WHERE BILL.SplitFY = 1;

 IF EXISTS (SELECT 1 FROM [Invoice].[InvoiceAccountBilling] BILL JOIN @billingTable T ON T.InvoiceBillingID = BILL.InvoiceBillingID WHERE BILL.SplitFY = 1)
 BEGIN
 -- Update current FY data if there is a difference in FY between from and to dates
 UPDATE BILL
 SET BILL.AmountPaidCurrentFY = (BILL.BillingPeriodDaysCurrentFY * BILL.AmountPaid) / BILL.BillingPeriodDays,BILL.EnergyUsageCurrentFY = (BILL.BillingPeriodDaysCurrentFY * BILL.EnergyUsage) / BILL.BillingPeriodDays,BILL.DemandUsageCurrentFY = (BILL.BillingPeriodDaysCurrentFY * BILL.DemandUsage) / BILL.BillingPeriodDays
 FROM Invoice.InvoiceAccountBilling AS BILL
 INNER JOIN @billingTable T ON T.InvoiceBillingID = BILL.InvoiceBillingID
 WHERE BILL.SplitFY = 1;

 -- Update current FY data if there is a difference in FY between from and to dates
 UPDATE BILL
 SET BILL.AmountPaidPriorFY = BILL.AmountPaid - BILL.AmountPaidCurrentFY,BILL.EnergyUsagePriorFY = BILL.EnergyUsage - BILL.EnergyUsageCurrentFY,BILL.DemandUsagePriorFY = BILL.DemandUsage - BILL.DemandUsageCurrentFY
 FROM Invoice.InvoiceAccountBilling AS BILL
 INNER JOIN @billingTable T ON T.InvoiceBillingID = BILL.InvoiceBillingID
 WHERE BILL.SplitFY = 1;
 END;

 -- Lock the parent records in EC3 (for electronic billing only), to prevent edits
 IF (@isElectronicSummaryBilling = 1) AND EXISTS (SELECT 1 FROM @billingTable)
 BEGIN
 UPDATE EB
 SET EB.InvoiceTrackingSeqid	= @invoiceId,EB.VoucherBillingStatus = 'VC',EB.VoucherBillingStatusPeriod = @billingPeriod,EB.IsLockedForEdit = 'Y',EB.LastUpdate = GETDATE(),EB.AuthenticatedUserID = @userId
 FROM Billing.EC3_ManualBill_AccountManualBillingHeader_ElectronicBilling AS EB
 INNER JOIN Invoice.InvoiceAccountBilling AS ACCT ON ACCT.BillingID = EB.AccountManualBillingHeaderSeqid
 INNER JOIN @billingTable AS BILL ON BILL.InvoiceBillingID = ACCT.InvoiceBillingID
 END;
 END;

 --added on 11/28/2018 by XL for updating invoiceNum
 IF ISNULL(@summaryAcctNumber, '')<>''
 BEGIN
 UPDATE Invoice.Invoice
 SET	InvoiceNumber = @summaryAcctNumber + '_' + RIGHT(InvoiceNumber, 9), ModifiedDate = GETDATE(), ModifiedBy = @userId
 WHERE InvoiceID = @invoiceId; 

 SET @invoiceNumAction = ' - Invoice Number Updated';
 END;

 -- Create audit note
 INSERT INTO [Common].[Note](ParentType, ParentID, [Action], Note, CreatedBy)
 VALUES ('INVOICE', @invoiceId, 'ADD GROUP', 'Billing group (' + @noteFiller + ') added' + @invoiceNumAction, @userId);

 COMMIT TRAN @tranName;
	END TRY
	BEGIN CATCH
 IF (XACT_STATE() <> 0)
 BEGIN
 ROLLBACK TRAN @tranName;
 END;

 SELECT @exceptionDetails = ERROR_MESSAGE(), @severity = 'ERROR', @message = 'Failed to add invoice billing group and contract', @sendEmail = 1;
	END CATCH;

	SET @elapsedTime = DATEDIFF(MILLISECOND, GETDATE(), @startTime);

	EXEC dbo.USP_AppLogCreate @correlationId = @correlationId,@machineName = @machineName,@processName = @processName,@action = @action,@severity = @severity,@message = @message,@messageDetails = @messageDetails,@exceptionDetails = @exceptionDetails,@domain = @domain,@username = @username,@userId = @userId,@sendEmail = @sendEmail,@elapsedTime = @elapsedTime;
END;
