CREATE PROCEDURE [Billing].[USP_AccountBillingRetrieve]
(
	@providerId AS INT = NULL,@groupId AS INT = NULL,@accountId	AS INT = NULL,@billingPeriod AS VARCHAR(6) = NULL,@correlationId AS VARCHAR(128) = NULL,@machineName AS VARCHAR(128) = NULL,@processName AS VARCHAR(128) = NULL,@domain AS VARCHAR(25) = NULL,@username AS VARCHAR(25) = NULL,@userId AS INT = NULL,@exceptionDetails AS VARCHAR(MAX) = NULL OUTPUT
)
AS
BEGIN
	SET NOCOUNT ON;

	SELECT @providerId = NULLIF(@providerId, 0), @groupId = NULLIF(@groupId, 0), @accountId = NULLIF(@accountId, 0), @billingPeriod = NULLIF(@billingPeriod, '');

	DECLARE @action AS VARCHAR(25), @severity AS VARCHAR(25), @message AS VARCHAR(255), @messageDetails AS VARCHAR(MAX),@startTime AS DATETIME, @elapsedTime AS INT, @sendEmail AS BIT;

	SELECT @startTime = GETDATE(), @correlationId = ISNULL(@correlationId, CONVERT(VARCHAR(128), NEWID())), @sendEmail = 0,@machineName = COALESCE(@machineName, @@SERVERNAME, 'NA'),@processName = COALESCE(@processName, QUOTENAME(OBJECT_SCHEMA_NAME(@@PROCID)) + '.' + QUOTENAME(OBJECT_NAME(@@PROCID)), 'NA'),@action = 'RETRIEVE', @severity = 'INFO', @message = 'Retrieved account billing(s)', @userId = ISNULL(@userId, 0),@messageDetails = CONVERT(VARCHAR(MAX), (SELECT @providerId AS PRVID, @groupId AS GRPID, @accountId AS ACCTID, @billingPeriod AS BILLPRD FOR XML PATH('INPUTS')));

	BEGIN TRY
 SELECT BillingID,AmountBilled,AmountPaid,DateBilled,DateDue,BillingPeriod,BillingPeriodDays,FromDate,ToDate,IsEstimated,IsManualEntry,AccountID,AccountNumber,FormattedAccountNumber,EnergySource,EnergyUsage,DemandUsage,GroupID,GroupName,ProviderID,ProviderName,CreatedDate,CreatedBy,ModifiedDate,ModifiedBy,Active
 FROM Billing.vwAccountBillings
 WHERE ProviderID = ISNULL(@providerId, ProviderID)
 AND GroupID = ISNULL(@groupId, GroupID)
 AND AccountID = ISNULL(@accountId, AccountID)
 AND BillingPeriod = ISNULL(@billingPeriod, BillingPeriod)
 ORDER BY ProviderName ASC, GroupName ASC, BillingPeriod DESC, AccountNumber ASC;
	END TRY
	BEGIN CATCH
 SELECT @exceptionDetails = ERROR_MESSAGE(), @severity = 'ERROR', @message = 'Failed to retrieve account billing(s)', @sendEmail = 1;
	END CATCH;
	
	SET @elapsedTime = DATEDIFF(MILLISECOND, GETDATE(), @startTime);

	EXEC dbo.USP_AppLogCreate @correlationId = @correlationId,@machineName = @machineName,@processName = @processName,@action = @action,@severity = @severity,@message = @message,@messageDetails = @messageDetails,@exceptionDetails = @exceptionDetails,@domain = @domain,@username = @username,@userId = @userId,@sendEmail = @sendEmail,@elapsedTime = @elapsedTime;
END;

CREATE PROCEDURE [Billing].[USP_BillingGroupRetrieve]
(
	@providerId AS INT = NULL,@groupId AS INT = NULL,@summaryAccountNumber AS VARCHAR(15) = NULL,@billingType AS CHAR(1) = NULL,@correlationId AS VARCHAR(128) = NULL,@machineName AS VARCHAR(128) = NULL,@processName AS VARCHAR(128) = NULL,@domain AS VARCHAR(25) = NULL,@username AS VARCHAR(25) = NULL,@userId AS INT = NULL,@exceptionDetails AS VARCHAR(MAX) = NULL OUTPUT
)
AS
BEGIN
	SET NOCOUNT ON;

	DECLARE @action AS VARCHAR(25) = 'RETRIEVE', @severity AS VARCHAR(25) = 'INFO', @message AS VARCHAR(255) = 'Retrieved utility provider billing groups',@messageDetails AS VARCHAR(MAX), @startTime AS DATETIME = GETDATE(), @elapsedTime AS INT, @sendEmail AS BIT = 0;
	
	SELECT @providerId = NULLIF(@providerId, 0), @groupId = NULLIF(@groupId, 0), @summaryAccountNumber = NULLIF(@summaryAccountNumber, 0),@billingType = NULLIF(@billingType, ''), @correlationId = ISNULL(@correlationId, CONVERT(VARCHAR(128), NEWID())),@machineName = COALESCE(@machineName, @@SERVERNAME, 'NA'), @userId = ISNULL(@userId, 0),@processName = COALESCE(@processName, QUOTENAME(OBJECT_SCHEMA_NAME(@@PROCID)) + '.' + QUOTENAME(OBJECT_NAME(@@PROCID)), 'NA');
	
	SELECT @messageDetails = CONVERT(VARCHAR(MAX), (SELECT @providerId AS PRVID, @groupId AS GRPID, @billingType AS BILLTYPE, @summaryAccountNumber AS SUMACCTNUM FOR XML PATH('INPUTS')));
	
	BEGIN TRY
 SELECT GroupID,GroupName,GroupDescription,SummaryAccountNumber,IsSummaryBilling,AccountCount,ProviderID,BillingType,CreatedDate,CreatedBy,ModifiedDate,ModifiedBy,Active
 FROM Billing.vwBillingGroups
 WHERE GroupID = ISNULL(@groupId, GroupID)
 AND ProviderID = ISNULL(@providerId, ProviderID)
 AND ISNULL(SummaryAccountNumber, '') = ISNULL(@summaryAccountNumber, ISNULL(SummaryAccountNumber, ''))
 AND BillingType = ISNULL(@billingType, BillingType)
 ORDER BY GroupName ASC;
	END TRY
	BEGIN CATCH
 SELECT @exceptionDetails = ERROR_MESSAGE(), @severity = 'ERROR', @message = 'Failed to retrieve utility provider billing groups', @sendEmail = 1;
	END CATCH;

	SET @elapsedTime = DATEDIFF(MILLISECOND, GETDATE(), @startTime);

	EXEC dbo.USP_AppLogCreate @correlationId = @correlationId,@machineName = @machineName,@processName = @processName,@action = @action,@severity = @severity,@message = @message,@messageDetails = @messageDetails,@exceptionDetails = @exceptionDetails,@domain = @domain,@username = @username,@userId = @userId,@sendEmail = @sendEmail,@elapsedTime = @elapsedTime;
END;

CREATE PROCEDURE [Billing].[USP_BillingPeriodByBillingTypeRetrieve]
(
	@billingType AS CHAR(1) = NULL,@billingPeriod AS VARCHAR(6) = NULL,@currentOnly AS BIT = NULL,@correlationId AS VARCHAR(128) = NULL,@machineName AS VARCHAR(128) = NULL,@processName AS VARCHAR(128) = NULL,@domain AS VARCHAR(25) = NULL,@username AS VARCHAR(25) = NULL,@userId AS INT = NULL,@exceptionDetails AS VARCHAR(MAX) = NULL OUTPUT
)
AS
BEGIN
	SET NOCOUNT ON;

	SELECT @billingType = NULLIF(@billingType, ''), @billingPeriod = NULLIF(@billingPeriod, ''), @currentOnly = NULLIF(@currentOnly, 0), @userId = ISNULL(@userId, 0),@correlationId = ISNULL(@correlationId, CONVERT(VARCHAR(128), NEWID())), @machineName = COALESCE(@machineName, @@SERVERNAME, 'NA'),@processName = COALESCE(@processName, QUOTENAME(OBJECT_SCHEMA_NAME(@@PROCID)) + '.' + QUOTENAME(OBJECT_NAME(@@PROCID)), 'NA');

	DECLARE @action AS VARCHAR(25) = 'RETRIEVE', @severity AS VARCHAR(25) = 'INFO', @message AS VARCHAR(255) = 'Retrieved billing periods', @messageDetails AS VARCHAR(MAX),@startTime AS DATETIME = GETDATE(), @elapsedTime AS INT, @sendEmail AS BIT = 0;
	
	SELECT @messageDetails = CONVERT(VARCHAR(MAX), (SELECT @billingType AS BILLTYPE, @billingPeriod AS BILLPRD, @currentOnly AS CURR FOR XML PATH('INPUTS')));

	BEGIN TRY
 SELECT DISTINCT BillingType,BillingPeriod,BillingPeriodDescription,IsCurrentProcessingPeriod,PublishedBillingPeriod,FiscalYear,FiscalYearStart,FiscalYearEnd,bp.Active
 FROM Billing.vwBillingPeriodsByBillingType bp
 INNER JOIN Invoice.vwInvoices invoice ON bp.BillingPeriod = invoice.InvoiceBillingPeriod
 WHERE BillingType = ISNULL(@billingType, BillingType)
 AND BillingPeriod = ISNULL(@billingPeriod, BillingPeriod)
 AND IsCurrentProcessingPeriod = ISNULL(@currentOnly, IsCurrentProcessingPeriod)
 ORDER BY BillingType ASC, BillingPeriod ASC, IsCurrentProcessingPeriod DESC;
	END TRY
	BEGIN CATCH
 SELECT @exceptionDetails = ERROR_MESSAGE(), @severity = 'ERROR', @message = 'Failed to retrieve billing periods', @sendEmail = 1;
	END CATCH;

	SET @elapsedTime = DATEDIFF(MILLISECOND, GETDATE(), @startTime);

	EXEC dbo.USP_AppLogCreate @correlationId = @correlationId,@machineName = @machineName,@processName = @processName,@action = @action,@severity = @severity,@message = @message,@messageDetails = @messageDetails,@exceptionDetails = @exceptionDetails,@domain = @domain,@username = @username,@userId = @userId,@sendEmail = @sendEmail,@elapsedTime = @elapsedTime;
END;

CREATE PROCEDURE [Billing].[USP_BillingPeriodRetrieve]
(
	@billingPeriod AS VARCHAR(6) = NULL,@currentOnly AS BIT = 0,@correlationId AS VARCHAR(128) = NULL,@machineName AS VARCHAR(128) = NULL,@processName AS VARCHAR(128) = NULL,@domain AS VARCHAR(25) = NULL,@username AS VARCHAR(25) = NULL,@userId AS INT = NULL,@exceptionDetails AS VARCHAR(MAX) = NULL OUTPUT
)
AS
BEGIN
	SET NOCOUNT ON;

	SELECT @billingPeriod = NULLIF(@billingPeriod, ''), @currentOnly = ISNULL(@currentOnly, 0), @userId = ISNULL(@userId, 0),@correlationId = ISNULL(@correlationId, CONVERT(VARCHAR(128), NEWID())), @machineName = COALESCE(@machineName, @@SERVERNAME, 'NA'),@processName = COALESCE(@processName, QUOTENAME(OBJECT_SCHEMA_NAME(@@PROCID)) + '.' + QUOTENAME(OBJECT_NAME(@@PROCID)), 'NA');

	DECLARE @action AS VARCHAR(25) = 'RETRIEVE', @severity AS VARCHAR(25) = 'INFO', @message AS VARCHAR(255) = 'Retrieved billing periods', @messageDetails AS VARCHAR(MAX),@startTime AS DATETIME = GETDATE(), @elapsedTime AS INT, @sendEmail AS BIT = 0;
	
	SET @messageDetails = CONVERT(VARCHAR(MAX), (SELECT @billingPeriod AS BILLPRD FOR XML PATH('INPUTS')));

	BEGIN TRY
 DECLARE @minPeriod AS VARCHAR(6), @maxPeriod AS VARCHAR(6);
 
 SET @minPeriod = CONVERT(VARCHAR(4), YEAR(DATEADD(M, -6, GETDATE())));

 IF (MONTH(DATEADD(M, -6, GETDATE())) < 10)
 BEGIN
 SET @minPeriod = @minPeriod + '0';
 END;

 SET @minPeriod = @minPeriod + CONVERT(VARCHAR(2), MONTH(DATEADD(M, -6, GETDATE())));
 SET @maxPeriod = CONVERT(VARCHAR(4), YEAR(DATEADD(M, 2, GETDATE())));

 IF (MONTH(DATEADD(M, 2, GETDATE())) < 10)
 BEGIN
 SET @maxPeriod = @maxPeriod + '0';
 END;

 SET @maxPeriod = @maxPeriod + CONVERT(VARCHAR(2), MONTH(DATEADD(M, 2, GETDATE())));

 IF (@currentOnly = 1)
 BEGIN
 SELECT DISTINCT B.BillingPeriod, B.BillingPeriodDescription, B.Active
 FROM Billing.vwBillingPeriods AS B
 LEFT JOIN Invoice.vwInvoices AS I ON B.BillingPeriod = I.InvoiceBillingPeriod
 WHERE B.BillingPeriod >= @minPeriod AND B.BillingPeriod <= @maxPeriod
 ORDER BY B.BillingPeriod ASC;
 END;
 ELSE
 BEGIN
 SELECT DISTINCT B.BillingPeriod, B.BillingPeriodDescription, B.Active
 FROM Billing.vwBillingPeriods AS B
 INNER JOIN Invoice.vwInvoices AS I ON B.BillingPeriod = I.InvoiceBillingPeriod
 WHERE B.BillingPeriod	= ISNULL(@billingPeriod, B.BillingPeriod)
 ORDER BY B.BillingPeriod ASC;
 END;
	END TRY
	BEGIN CATCH
 SELECT @exceptionDetails = ERROR_MESSAGE(), @severity = 'ERROR', @message = 'Failed to retrieve billing periods', @sendEmail = 1;
	END CATCH;

	SET @elapsedTime = DATEDIFF(MILLISECOND, GETDATE(), @startTime);

	EXEC dbo.USP_AppLogCreate @correlationId = @correlationId,@machineName = @machineName,@processName = @processName,@action = @action,@severity = @severity,@message = @message,@messageDetails = @messageDetails,@exceptionDetails = @exceptionDetails,@domain = @domain,@username = @username,@userId = @userId,@sendEmail = @sendEmail,@elapsedTime = @elapsedTime;
END;

CREATE PROCEDURE [Billing].[USP_ContractCreate]
(
	@providerId AS INT,@contractNumber AS VARCHAR(25),@name AS VARCHAR(100),@description AS VARCHAR(500) = NULL,@fiscalYearStart AS VARCHAR(8),@fiscalYearEnd AS VARCHAR(8),@actualAmount AS DECIMAL(18, 2),@closeAmount AS DECIMAL(18, 2),@openAmount AS DECIMAL(18, 2),@capacityAmount AS DECIMAL(18, 2) = NULL,@correlationId AS VARCHAR(128) = NULL,@machineName AS VARCHAR(128) = NULL,@processName AS VARCHAR(128) = NULL,@domain AS VARCHAR(25) = NULL,@username AS VARCHAR(25) = NULL,@userId AS INT = NULL,@exceptionDetails AS VARCHAR(MAX) = NULL OUTPUT
)
AS
BEGIN
	SET NOCOUNT ON;

	DECLARE @action AS VARCHAR(25) = 'CREATE', @severity AS VARCHAR(25) = 'INFO', @message AS VARCHAR(255) = 'Added contract', @messageDetails AS VARCHAR(MAX),@startTime AS DATETIME = GETDATE(), @elapsedTime AS INT, @sendEmail AS BIT = 0, @tranName AS VARCHAR(128) = CONVERT(VARCHAR(128), NEWID());
	
	SELECT @correlationId = ISNULL(@correlationId, CONVERT(VARCHAR(128), NEWID())), @machineName = COALESCE(@machineName, @@SERVERNAME, 'NA'), @description = NULLIF(@description, ''),@processName = COALESCE(@processName, QUOTENAME(OBJECT_SCHEMA_NAME(@@PROCID)) + '.' + QUOTENAME(OBJECT_NAME(@@PROCID)), 'NA'), @userId = ISNULL(@userId, 0);
	
	SET @messageDetails = CONVERT(VARCHAR(MAX), (SELECT @providerId AS PRVID, @contractNumber AS CTRTNUM, @name AS NAME, @description AS DSC,@fiscalYearStart AS FYSTART, @fiscalYearEnd AS FYEND, @actualAmount AS ACTAMT, @closeAmount AS CLSAMT, @openAmount AS OPENAMT,@capacityAmount AS CAPAMT FOR XML PATH('INPUTS')));

	BEGIN TRY
 BEGIN TRAN @tranName;

 -- Create contract
 INSERT INTO Billing.[Contract](ContractNumber, [Name], [Description], ProviderID, FiscalYearStart, FiscalYearEnd, ActualAmount, CloseAmount, OpenAmount, capacityAmount, CreatedBy)
 VALUES (@contractNumber, @name, @description, @providerId, @fiscalYearStart, @fiscalYearEnd, @actualAmount, @closeAmount, @openAmount, ISNULL(@capacityAmount,0), @userId);

 COMMIT TRAN @tranName;
	END TRY
	BEGIN CATCH
 IF (XACT_STATE() <> 0)
 BEGIN
 ROLLBACK TRAN @tranName;
 END;

 SELECT @exceptionDetails = ERROR_MESSAGE(), @severity = 'ERROR', @message = 'Failed to add contract', @sendEmail = 1;
	END CATCH;

	SET @elapsedTime = DATEDIFF(MILLISECOND, GETDATE(), @startTime);

	EXEC dbo.USP_AppLogCreate @correlationId = @correlationId,@machineName = @machineName,@processName = @processName,@action = @action,@severity = @severity,@message = @message,@messageDetails = @messageDetails,@exceptionDetails = @exceptionDetails,@domain = @domain,@username = @username,@userId = @userId,@sendEmail = @sendEmail,@elapsedTime = @elapsedTime;
END;

CREATE PROCEDURE [Billing].[USP_ContractDelete]
(
	@contractId AS INT,@correlationId AS VARCHAR(128) = NULL,@machineName AS VARCHAR(128) = NULL,@processName AS VARCHAR(128) = NULL,@domain AS VARCHAR(25) = NULL,@username AS VARCHAR(25) = NULL,@userId AS INT = NULL,@exceptionDetails AS VARCHAR(MAX) = NULL OUTPUT
)
AS
BEGIN
	SET NOCOUNT ON;

	DECLARE @action AS VARCHAR(25) = 'DELETE', @severity AS VARCHAR(25) = 'INFO', @message AS VARCHAR(255) = 'Deleted Contract', @messageDetails AS VARCHAR(MAX),@startTime AS DATETIME = GETDATE(), @elapsedTime AS INT, @sendEmail AS BIT = 0, @tranName AS VARCHAR(128) = CONVERT(VARCHAR(128), NEWID());
	
	SELECT @correlationId = ISNULL(@correlationId, CONVERT(VARCHAR(128), NEWID())), @machineName = COALESCE(@machineName, @@SERVERNAME, 'NA'),@processName = COALESCE(@processName, QUOTENAME(OBJECT_SCHEMA_NAME(@@PROCID)) + '.' + QUOTENAME(OBJECT_NAME(@@PROCID)), 'NA'), @userId = ISNULL(@userId, 0);
	
	SELECT @messageDetails = CONVERT(VARCHAR(MAX), (SELECT @contractId AS CTRTID FOR XML PATH('INPUTS')));

	BEGIN TRY
 BEGIN TRAN @tranName;

 IF NOT EXISTS (SELECT * FROM Invoice.vwInvoiceGroupContracts WHERE ContractID = @contractId)
 BEGIN
 -- Delete BillingGroup Contract
 DELETE FROM Billing.BillingGroupContract WHERE ContractID = @contractId;

 -- Delete ContractLines History
 DELETE FROM Billing.HistoricalContractLine WHERE ContractLineID IN (SELECT ContractLineID FROM Billing.ContractLine WHERE ContractID = @contractId);

 -- Delete ContractLines
 DELETE FROM Billing.ContractLine WHERE ContractID = @contractId;

 -- Delete contract
 DELETE FROM Billing.[Contract] WHERE ContractID = @contractId;
 END;

 COMMIT TRAN @tranName;
	END TRY
	BEGIN CATCH
 IF (XACT_STATE() <> 0)
 BEGIN
 ROLLBACK TRAN @tranName;
 END;

 SELECT @exceptionDetails = ERROR_MESSAGE(), @severity = 'ERROR', @message = 'Failed to delete contract', @sendEmail = 1;
	END CATCH;

	SET @elapsedTime = DATEDIFF(MILLISECOND, GETDATE(), @startTime);

	EXEC dbo.USP_AppLogCreate @correlationId = @correlationId,@machineName = @machineName,@processName = @processName,@action = @action,@severity = @severity,@message = @message,@messageDetails = @messageDetails,@exceptionDetails = @exceptionDetails,@domain = @domain,@username = @username,@userId = @userId,@sendEmail = @sendEmail,@elapsedTime = @elapsedTime;
END;

CREATE PROCEDURE [Billing].[USP_ContractLineCreate]
(
	@contractId AS INT,@fiscalYear AS VARCHAR(4),@commodityLine AS INT,@accountingLine AS INT,@budgetCode AS VARCHAR(4),@objectCode AS VARCHAR(3),@uoa AS VARCHAR(3),@actualAmount AS DECIMAL(18, 2),@closeAmount AS DECIMAL(18, 2),@openAmount AS DECIMAL(18, 2),@correlationId AS VARCHAR(128) = NULL,@machineName AS VARCHAR(128) = NULL,@processName AS VARCHAR(128) = NULL,@domain AS VARCHAR(25) = NULL,@username AS VARCHAR(25) = NULL,@userId AS INT = NULL,@exceptionDetails AS VARCHAR(MAX) = NULL OUTPUT
)
AS
BEGIN
	SET NOCOUNT ON;

	DECLARE @action AS VARCHAR(25) = 'CREATE', @severity AS VARCHAR(25) = 'INFO', @message AS VARCHAR(255) = 'Added contract line', @messageDetails AS VARCHAR(MAX),@startTime AS DATETIME = GETDATE(), @elapsedTime AS INT, @sendEmail AS BIT = 0, @tranName AS VARCHAR(128) = CONVERT(VARCHAR(128), NEWID());
	
	SELECT @correlationId = ISNULL(@correlationId, CONVERT(VARCHAR(128), NEWID())), @machineName = COALESCE(@machineName, @@SERVERNAME, 'NA'),@processName = COALESCE(@processName, QUOTENAME(OBJECT_SCHEMA_NAME(@@PROCID)) + '.' + QUOTENAME(OBJECT_NAME(@@PROCID)), 'NA'), @userId = ISNULL(@userId, 0);
	
	SELECT @messageDetails = CONVERT(VARCHAR(MAX), (SELECT @contractId AS CTRTID, @fiscalYear AS FY, @commodityLine AS COMMLINE, @accountingLine AS ACCTLINE,@budgetCode AS BCODE, @objectCode AS OCODE, @uoa AS UA, @actualAmount AS ACTAMT, @closeAmount AS CLSAMT, @openAmount AS OPENAMT FOR XML PATH('INPUTS')));

	BEGIN TRY
 BEGIN TRAN @tranName;

 -- Create contract line
 INSERT INTO Billing.ContractLine(ContractID, FiscalYear, CommodityLine, AccountingLine, BudgetCode, ObjectCode, UoA,ActualAmount, CloseAmount, OpenAmount, CreatedBy)
 VALUES (@contractId, @fiscalYear, @commodityLine, @accountingLine, @budgetCode, @objectCode, @uoa,@actualAmount, @closeAmount, @actualAmount, @userId);
 
 UPDATE Billing.[Contract]
 SET	ActualAmount = ISNULL(b.ActualAmount, 0),CloseAmount = ISNULL(b.CloseAmount, 0),OpenAmount = ISNULL(b.OpenAmount, 0),ModifiedDate = GETDATE(),ModifiedBy = @userId
 FROM (SELECT SUM(ISNULL(ActualAmount, 0)) AS ActualAmount,SUM(ISNULL(CloseAmount, 0)) AS CloseAmount,SUM(ISNULL(OpenAmount, 0)) AS OpenAmount
 FROM Billing.ContractLine
 WHERE ContractID = @contractId) AS b
 WHERE ContractID = @contractId;

 -- insert into Billing.historicalcontract
 EXEC Billing.USP_HistoricalContractLineInsert @@IDENTITY, 1, @username, 0, @actualAmount, 0;

 COMMIT TRAN @tranName;
	END TRY
	BEGIN CATCH
 IF (XACT_STATE() <> 0)
 BEGIN
 ROLLBACK TRAN @tranName;
 END;

 SELECT @exceptionDetails = ERROR_MESSAGE(), @severity = 'ERROR', @message = 'Failed to add contract line', @sendEmail = 1;
	END CATCH;

	SET @elapsedTime = DATEDIFF(MILLISECOND, GETDATE(), @startTime);

	EXEC dbo.USP_AppLogCreate @correlationId = @correlationId,@machineName = @machineName,@processName = @processName,@action = @action,@severity = @severity,@message = @message,@messageDetails = @messageDetails,@exceptionDetails = @exceptionDetails,@domain = @domain,@username = @username,@userId = @userId,@sendEmail = @sendEmail,@elapsedTime = @elapsedTime;
END;

CREATE PROCEDURE [Billing].[USP_ContractLineDelete]
(
	@contractLineId	AS INT,@correlationId AS VARCHAR(128) = NULL,@machineName AS VARCHAR(128) = NULL,@processName AS VARCHAR(128) = NULL,@domain AS VARCHAR(25) = NULL,@username AS VARCHAR(25) = NULL,@userId AS INT = NULL,@exceptionDetails AS VARCHAR(MAX) = NULL OUTPUT
)
AS
BEGIN
	SET NOCOUNT ON;

	DECLARE @contractId AS INT, @action AS VARCHAR(25) = 'DELETE', @severity AS VARCHAR(25) = 'INFO', @message AS VARCHAR(255) = 'Deleted contract line',@messageDetails AS VARCHAR(MAX), @startTime AS DATETIME = GETDATE(), @elapsedTime AS INT, @sendEmail AS BIT = 0, @tranName AS VARCHAR(128) = CONVERT(VARCHAR(128), NEWID());

	SELECT @contractId = ContractID
	FROM Billing.ContractLine
	WHERE ContractLineID = @contractLineId;
	
	SELECT @correlationId = ISNULL(@correlationId, CONVERT(VARCHAR(128), NEWID())), @machineName = COALESCE(@machineName, @@SERVERNAME, 'NA'),@processName = COALESCE(@processName, QUOTENAME(OBJECT_SCHEMA_NAME(@@PROCID)) + '.' + QUOTENAME(OBJECT_NAME(@@PROCID)), 'NA'), @userId = ISNULL(@userId, 0);

	SELECT @messageDetails = CONVERT(VARCHAR(MAX), (SELECT @contractId AS CTRTID, @contractLineId AS CTRTLINEID FOR XML PATH('INPUTS')));

	BEGIN TRY
 BEGIN TRAN @tranName;
 
 IF NOT EXISTS (SELECT * FROM Invoice.InvoiceContractLine WHERE ContractLineID = @contractLineId)
 BEGIN
 -- Update history contract line
 DELETE FROM Billing.HistoricalContractLine WHERE ContractLineID = @contractLineId;

 -- Update contract line
 DELETE FROM Billing.ContractLine WHERE ContractLineID = @contractLineId;

 -- update contract table since contract amounts are from contract lines
 UPDATE Billing.[Contract]
 SET	ActualAmount = ISNULL(b.ActualAmount, 0),CloseAmount = ISNULL(b.CloseAmount, 0),OpenAmount = ISNULL(b.OpenAmount, 0),ModifiedDate = GETDATE(),ModifiedBy = @userId
 FROM (SELECT SUM(ISNULL(ActualAmount, 0)) AS ActualAmount,SUM(ISNULL(CloseAmount, 0)) AS CloseAmount,SUM(ISNULL(OpenAmount, 0)) AS OpenAmount
 FROM Billing.ContractLine
 WHERE ContractID = @contractId) AS b
 WHERE ContractID = @contractId;
 END;

 COMMIT TRAN @tranName;
	END TRY
	BEGIN CATCH
 IF (XACT_STATE() <> 0)
 BEGIN
 ROLLBACK TRAN @tranName;
 END;

 SELECT @exceptionDetails = ERROR_MESSAGE(), @severity	= 'ERROR', @message	= 'Failed to delete contract line', @sendEmail	= 1;
	END CATCH;

	SET @elapsedTime = DATEDIFF(MILLISECOND, GETDATE(), @startTime);

	EXEC dbo.USP_AppLogCreate @correlationId = @correlationId,@machineName = @machineName,@processName = @processName,@action = @action,@severity = @severity,@message = @message,@messageDetails = @messageDetails,@exceptionDetails = @exceptionDetails,@domain = @domain,@username = @username,@userId = @userId,@sendEmail = @sendEmail,@elapsedTime = @elapsedTime;
END;

CREATE PROCEDURE [Billing].[USP_ContractLineRetrieve]
(
	@contractLineId AS INT = NULL,@contractId AS INT = NULL,@contractNumber AS VARCHAR(25) = NULL,@fiscalYear AS VARCHAR(4) = NULL,@correlationId AS VARCHAR(128) = NULL,@machineName AS VARCHAR(128) = NULL,@processName AS VARCHAR(128) = NULL,@domain AS VARCHAR(25) = NULL,@username AS VARCHAR(25) = NULL,@userId AS INT = NULL,@exceptionDetails AS VARCHAR(MAX) = NULL OUTPUT
)
AS
BEGIN
	SET NOCOUNT ON;

	SELECT @contractLineId = NULLIF(@contractLineId, 0), @contractId = NULLIF(@contractId, 0), @contractNumber = NULLIF(@contractNumber, ''),@fiscalYear = NULLIF(@fiscalYear, ''), @userId = ISNULL(@userId, 0), @correlationId = ISNULL(@correlationId, CONVERT(VARCHAR(128), NEWID())),@machineName = COALESCE(@machineName, @@SERVERNAME, 'NA'),@processName = COALESCE(@processName, QUOTENAME(OBJECT_SCHEMA_NAME(@@PROCID)) + '.' + QUOTENAME(OBJECT_NAME(@@PROCID)), 'NA');

	DECLARE @action AS VARCHAR(25) = 'RETRIEVE', @severity AS VARCHAR(25) = 'INFO', @message AS VARCHAR(255) = 'Retrieved utility provider contract line(s)',@messageDetails AS VARCHAR(MAX), @startTime AS DATETIME = GETDATE(), @elapsedTime AS INT, @sendEmail AS BIT = 0;
	
	SELECT @messageDetails = CONVERT(VARCHAR(MAX), (SELECT @contractLineId AS CLID, @contractId AS CID, @contractNumber AS NUM, @fiscalYear AS FY FOR XML PATH('INPUTS')));

	BEGIN TRY
 SELECT ContractLineID,FiscalYear,ContractID,ContractNumber,CommodityLine,AccountingLine,BudgetCode,ObjectCode,UoA,ActualAmount,Billing.UDF_GetContractLineAmountPaid(ContractLineID, GETDATE()) AS CloseAmount,ActualAmount - Billing.UDF_GetContractLineAmountPaid(ContractLineID, GETDATE()) AS OpenAmount,AvailableAmount,CreatedDate,CreatedBy,ModifiedDate,ModifiedBy,Active,Billing.UDF_GetContractLineAmountPaid(ContractLineID, GETDATE()) AS AmountPaid
 FROM Billing.vwContractLines
 WHERE ContractLineID = ISNULL(@contractLineId, ContractLineID)
 AND ContractID = ISNULL(@contractId, ContractID)
 AND ContractNumber = ISNULL(@contractNumber, ContractNumber)
 AND FiscalYear = ISNULL(@fiscalYear, FiscalYear)
 ORDER BY FiscalYear, CommodityLine ASC, AccountingLine ASC;

 -- For History Popup
 SELECT TransactionType,ContractLineID,ActualAmount,StartDate,EndDate,ModifiedBy,ActualAmount + ChangeAmount AS TotalAmount,ChangeAmount
 FROM Billing.HistoricalContractLine
 WHERE ContractLineId = @ContractLineID AND TransactionType = 1;
	END TRY
	BEGIN CATCH
 SELECT @exceptionDetails = ERROR_MESSAGE(), @severity = 'ERROR', @message = 'Failed to retrieve utility provider contract line(s)', @sendEmail = 1;
	END CATCH;

	SET @elapsedTime = DATEDIFF(MILLISECOND, GETDATE(), @startTime);

	EXEC dbo.USP_AppLogCreate @correlationId = @correlationId,@machineName = @machineName,@processName = @processName,@action = @action,@severity = @severity,@message = @message,@messageDetails = @messageDetails,@exceptionDetails = @exceptionDetails,@domain = @domain,@username = @username,@userId = @userId,@sendEmail = @sendEmail,@elapsedTime = @elapsedTime;
END;

CREATE PROCEDURE [Billing].[USP_ContractLineUpdate]
(
	@contractLineId AS INT,@contractId AS INT,@fiscalYear AS VARCHAR(4),@commodityLine AS INT,@accountingLine AS INT,@budgetCode AS VARCHAR(4),@objectCode AS VARCHAR(3),@uoa AS VARCHAR(3),@actualAmount AS DECIMAL(18, 2),@closeAmount AS DECIMAL(18, 2),@openAmount AS DECIMAL(18, 2),@changeAmount AS DECIMAL(18, 2),@active AS BIT,@correlationId AS VARCHAR(128) = NULL,@machineName AS VARCHAR(128) = NULL,@processName AS VARCHAR(128) = NULL,@domain AS VARCHAR(25) = NULL,@username AS VARCHAR(25) = NULL,@userId AS INT = NULL,@exceptionDetails AS VARCHAR(MAX) = NULL OUTPUT
)
AS
BEGIN
	SET NOCOUNT ON;

	DECLARE @action AS VARCHAR(25), @severity AS VARCHAR(25), @message AS VARCHAR(255), @messageDetails AS VARCHAR(MAX),@startTime AS DATETIME = GETDATE(), @elapsedTime AS INT, @sendEmail AS BIT,@tranName AS VARCHAR(128) = CONVERT(VARCHAR(128), NEWID());
	
	SELECT @correlationId = ISNULL(@correlationId, CONVERT(VARCHAR(128), NEWID())), @machineName = COALESCE(@machineName, @@SERVERNAME, 'NA'),@processName = COALESCE(@processName, QUOTENAME(OBJECT_SCHEMA_NAME(@@PROCID)) + '.' + QUOTENAME(OBJECT_NAME(@@PROCID)), 'NA'),@action = 'UPDATE', @severity = 'INFO', @message = 'Updated contract line', @sendEmail = 0, @userId = ISNULL(@userId, 0),@messageDetails = CONVERT(VARCHAR(MAX), (SELECT @contractLineId AS CLINEID, @contractId AS CTRTID, @fiscalYear AS FY,@commodityLine AS COMMLINE, @accountingLine AS ACCTLINE, @budgetCode AS BCODE, @objectCode AS OCODE, @uoa AS UA,@actualAmount AS ACTAMT, @closeAmount AS CLSAMT, @openAmount AS OPENAMT, @active AS ACT FOR XML PATH('INPUTS')));

	BEGIN TRY
 BEGIN TRAN @tranName;

 -- Update contract line
 UPDATE Billing.ContractLine
 SET ContractID = @contractId,FiscalYear = @fiscalYear,CommodityLine = @commodityLine,AccountingLine	= @accountingLine,BudgetCode	= @budgetCode,ObjectCode = @objectCode,UoA = @uoa,ActualAmount = @actualAmount + @changeAmount,CloseAmount = @closeAmount,OpenAmount = @openAmount + @changeAmount,ModifiedDate = GETDATE(),ModifiedBy	= @userId
 WHERE ContractLineID = @contractLineId;

 UPDATE C
 SET	C.ActualAmount = ISNULL(B.ActualAmount, 0),C.CloseAmount = ISNULL(B.CloseAmount, 0),C.OpenAmount = ISNULL(B.OpenAmount, 0),C.ModifiedDate = GETDATE(),C.ModifiedBy = @userId
 FROM Billing.[Contract] AS C
 INNER JOIN (SELECT ContractID, SUM(ISNULL(ActualAmount, 0)) AS ActualAmount,SUM(ISNULL(CloseAmount, 0)) AS CloseAmount,SUM(ISNULL(OpenAmount, 0)) AS OpenAmount
 FROM Billing.ContractLine
 GROUP BY ContractID) AS B ON C.ContractID = B.ContractID
 WHERE C.ContractID = @contractId;

 EXEC Billing.USP_HistoricalContractLineInsert @contractLineId, 1, @username, @actualAmount, @changeAmount, 0;

 COMMIT TRAN @tranName;
	END TRY
	BEGIN CATCH
 IF (XACT_STATE() <> 0)
 BEGIN
 ROLLBACK TRAN @tranName;
 END;

 SELECT @exceptionDetails = ERROR_MESSAGE(), @severity = 'ERROR', @message = 'Failed to update contract line', @sendEmail = 1;
	END CATCH;

	SET @elapsedTime = DATEDIFF(MILLISECOND, GETDATE(), @startTime);

	EXEC dbo.USP_AppLogCreate @correlationId = @correlationId,@machineName = @machineName,@processName = @processName,@action = @action,@severity = @severity,@message = @message,@messageDetails = @messageDetails,@exceptionDetails = @exceptionDetails,@domain = @domain,@username = @username,@userId = @userId,@sendEmail = @sendEmail,@elapsedTime = @elapsedTime;
END;

CREATE PROCEDURE [Billing].[USP_ContractRetrieve]
(
	@providerId AS INT = NULL,@contractId AS INT = NULL,@contractNumber AS VARCHAR(25) = NULL,@groupId AS INT = NULL,@fiscalYear AS VARCHAR(4) = NULL,@activeOnly AS BIT = NULL,@correlationId AS VARCHAR(128) = NULL,@machineName AS VARCHAR(128) = NULL,@processName AS VARCHAR(128) = NULL,@domain AS VARCHAR(25) = NULL,@username AS VARCHAR(25) = NULL,@userId AS INT = NULL,@exceptionDetails AS VARCHAR(MAX) = NULL OUTPUT
)
AS
BEGIN
	SET NOCOUNT ON;

	SELECT @providerId = NULLIF(@providerId, 0), @contractId = NULLIF(@contractId, 0), @contractNumber = NULLIF(@contractNumber, ''),@fiscalYear = NULLIF(@fiscalYear, ''), @activeOnly = ISNULL(@activeOnly, 0), @userId = ISNULL(@userId, 0),@correlationId = ISNULL(@correlationId, CONVERT(VARCHAR(128), NEWID())), @machineName = COALESCE(@machineName, @@SERVERNAME, 'NA'),@processName = COALESCE(@processName, QUOTENAME(OBJECT_SCHEMA_NAME(@@PROCID)) + '.' + QUOTENAME(OBJECT_NAME(@@PROCID)), 'NA');

	DECLARE @action AS VARCHAR(25) = 'RETRIEVE', @severity AS VARCHAR(25) = 'INFO', @message AS VARCHAR(255) = 'Retrieved utility provider contracts', @messageDetails AS VARCHAR(MAX),@startTime AS DATETIME = GETDATE(), @elapsedTime AS INT, @sendEmail AS BIT = 0;
	
	SELECT @messageDetails = CONVERT(VARCHAR(MAX), (SELECT @providerId AS PRVID, @fiscalYear AS FY, @groupId AS GRPID, @contractId AS ID, @contractNumber AS NUM,@activeOnly AS ACT FOR XML PATH('INPUTS')));

	BEGIN TRY
 DECLARE @fiscalYearStart AS VARCHAR(8);

 IF (ISNULL(@fiscalYear, '') <> '')
 BEGIN
 SET @fiscalYearStart = CONVERT(VARCHAR(4), (CONVERT(INT, @fiscalYear) - 1)) + '0701';
 END;

 DECLARE @tmpContracts AS TABLE (ContractID INT NOT NULL);

 IF (ISNULL(@groupId, 0) > 0)
 BEGIN
 INSERT INTO @tmpContracts (ContractID)
 SELECT DISTINCT ContractID
 FROM Billing.vwBillingGroupContracts
 WHERE GroupID = ISNULL(@groupId, GroupID)
 AND FiscalYear = CASE WHEN ISNULL(@groupId, 0) > 0 THEN ISNULL(@fiscalYear, FiscalYear) ELSE FiscalYear END
 AND Active = @activeOnly;
 END;
 ELSE
 BEGIN
 INSERT INTO @tmpContracts (ContractID)
 SELECT DISTINCT ContractID
 FROM Billing.vwContracts;
 END;

 SELECT C.ContractID,C.ContractNumber,C.[Name],C.[Description],C.ProviderID,C.FiscalYearStart,C.FiscalYearEnd,C.ActualAmount,Billing.UDF_GetContractAmountPaid(C.ContractID, GETDATE()) AS CloseAmount,C.ActualAmount - Billing.UDF_GetContractAmountPaid(C.ContractID, GETDATE()) AS OpenAmount,C.AvailableAmount,C.CreatedDate,C.CreatedBy,C.ModifiedDate,C.ModifiedBy,C.Active,C.CapacityAmount,P.ProviderName
 FROM Billing.vwContracts AS C
 INNER JOIN @tmpContracts AS GC ON GC.ContractID = C.ContractID
 INNER JOIN Billing.vwProviders AS P ON C.ProviderID = P.ProviderID
 WHERE C.ContractID = ISNULL(@contractId, C.ContractID)
 AND C.ContractNumber = ISNULL(@contractNumber, C.ContractNumber)
 AND C.ProviderID = ISNULL(@providerId, C.ProviderID)
 AND CASE WHEN ISNULL(@fiscalYear, '') <> '' THEN CASE WHEN @fiscalYearStart BETWEEN C.FiscalYearStart AND C.FiscalYearEnd THEN 1 ELSE 0 END ELSE 1 END = 1
 AND C.Active = CASE WHEN @activeOnly = 1 THEN 1 ELSE C.Active END
 ORDER BY C.FiscalYearStart DESC, C.ContractNumber DESC;
	END TRY
	BEGIN CATCH
 SELECT @exceptionDetails = ERROR_MESSAGE(), @severity = 'ERROR', @message = 'Failed to retrieve utility provider contracts', @sendEmail	= 1;
	END CATCH;

	SET @elapsedTime = DATEDIFF(MILLISECOND, GETDATE(), @startTime);

	EXEC dbo.USP_AppLogCreate @correlationId = @correlationId,@machineName = @machineName,@processName = @processName,@action = @action,@severity = @severity,@message = @message,@messageDetails = @messageDetails,@exceptionDetails = @exceptionDetails,@domain = @domain,@username = @username,@userId = @userId,@sendEmail = @sendEmail,@elapsedTime = @elapsedTime;
END;

CREATE PROCEDURE [Billing].[USP_ContractUpdate]
(
	@contractId AS INT,@providerId AS INT,@contractNumber AS VARCHAR(25),@name AS VARCHAR(100),@description AS VARCHAR(500) = NULL,@fiscalYearStart AS VARCHAR(8),@fiscalYearEnd AS VARCHAR(8),@actualAmount AS DECIMAL(18, 2),@closeAmount AS DECIMAL(18, 2),@openAmount AS DECIMAL(18, 2),@active AS BIT,@capacityAmount AS DECIMAL(18, 2) = NULL,@correlationId AS VARCHAR(128) = NULL,@machineName AS VARCHAR(128) = NULL,@processName AS VARCHAR(128) = NULL,@domain AS VARCHAR(25) = NULL,@username AS VARCHAR(25) = NULL,@userId AS INT = NULL,@exceptionDetails AS VARCHAR(MAX) = NULL OUTPUT
)
AS
BEGIN
	SET NOCOUNT ON;

	SELECT @description = NULLIF(@description, ''), @correlationId = ISNULL(@correlationId, CONVERT(VARCHAR(128), NEWID())),@machineName = COALESCE(@machineName, @@SERVERNAME, 'NA'), @userId = ISNULL(@userId, 0),@processName = COALESCE(@processName, QUOTENAME(OBJECT_SCHEMA_NAME(@@PROCID)) + '.' + QUOTENAME(OBJECT_NAME(@@PROCID)), 'NA');

	DECLARE @action AS VARCHAR(25) = 'UPDATE', @severity AS VARCHAR(25) = 'INFO', @message AS VARCHAR(255) = 'Updated contract', @messageDetails AS VARCHAR(MAX),@startTime AS DATETIME = GETDATE(), @elapsedTime AS INT, @sendEmail AS BIT = 0, @tranName AS VARCHAR(128) = CONVERT(VARCHAR(128), NEWID());
	
	SELECT @messageDetails = CONVERT(VARCHAR(MAX), (SELECT @contractId AS CTRTID, @providerId AS PRVID, @contractNumber AS CTRTNUM, @name AS NAME, @description AS DSC,@fiscalYearStart AS FYSTART, @fiscalYearEnd AS FYEND, @actualAmount AS ACTAMT, @closeAmount AS CLSAMT, @openAmount AS OPENAMT, @capacityAmount AS CAPAMT,@active AS ACT FOR XML PATH('INPUTS')));

	BEGIN TRY
 BEGIN TRAN @tranName;

 -- Update contract
 UPDATE Billing.[Contract]
 SET ContractNumber = @contractNumber,[Name] = @name,[Description] = @description,ProviderID = @providerId,FiscalYearStart = @fiscalYearStart,FiscalYearEnd = @fiscalYearEnd,CapacityAmount	= ISNULL(@capacityAmount, CapacityAmount),ModifiedDate = GETDATE(),ModifiedBy = @userId
 WHERE ContractID = @contractId;

 COMMIT TRAN @tranName;
	END TRY
	BEGIN CATCH
 IF (XACT_STATE() <> 0)
 BEGIN
 ROLLBACK TRAN @tranName;
 END;

 SELECT @exceptionDetails = ERROR_MESSAGE(), @severity = 'ERROR', @message = 'Failed to update contract', @sendEmail	= 1;
	END CATCH;

	SET @elapsedTime = DATEDIFF(MILLISECOND, GETDATE(), @startTime);

	EXEC dbo.USP_AppLogCreate @correlationId = @correlationId,@machineName = @machineName,@processName = @processName,@action = @action,@severity = @severity,@message = @message,@messageDetails = @messageDetails,@exceptionDetails = @exceptionDetails,@domain = @domain,@username = @username,@userId = @userId,@sendEmail = @sendEmail,@elapsedTime = @elapsedTime;
END;

CREATE PROCEDURE [Billing].[USP_FiscalYearRetrieve]
AS
BEGIN
	SET NOCOUNT ON;
	SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;

	SELECT FiscalYear,FiscalYearDescription,FiscalYearStart,FiscalYearEnd,Active
	FROM Billing.vwFiscalYears
	ORDER BY FiscalYear DESC;
END;

CREATE PROCEDURE [Billing].[USP_UtilityProviderRetrieve]
(
	@providerId AS INT = NULL,@correlationId AS VARCHAR(128) = NULL,@machineName AS VARCHAR(128) = NULL,@processName AS VARCHAR(128) = NULL,@domain AS VARCHAR(25) = NULL,@username AS VARCHAR(25) = NULL,@userId AS INT = NULL,@exceptionDetails AS VARCHAR(MAX) = NULL OUTPUT
)
AS
BEGIN
	SET NOCOUNT ON;

	DECLARE @action AS VARCHAR(25) = 'RETRIEVE', @severity AS VARCHAR(25) = 'INFO', @message AS VARCHAR(255) = 'Retrieved utility providers', @messageDetails AS VARCHAR(MAX),@startTime AS DATETIME = GETDATE(), @elapsedTime AS INT, @sendEmail AS BIT = 0;
	
	SELECT @correlationId = ISNULL(@correlationId, CONVERT(VARCHAR(128), NEWID())), @machineName = COALESCE(@machineName, @@SERVERNAME, 'NA'), @userId = ISNULL(@userId, 0),@processName = COALESCE(@processName, QUOTENAME(OBJECT_SCHEMA_NAME(@@PROCID)) + '.' + QUOTENAME(OBJECT_NAME(@@PROCID)), 'NA'), @providerId = NULLIF(@providerId, 0);
	
	SELECT @messageDetails = CONVERT(VARCHAR(MAX), (SELECT @providerId AS PRVID FOR XML PATH('INPUTS')));
	
	BEGIN TRY
 SELECT ProviderID,ProviderName,ProviderDescription,FMSIdNumber,GroupCount,CreatedDate,CreatedBy,ModifiedDate,ModifiedBy,Active
 FROM Billing.vwProviders
 WHERE ProviderID = ISNULL(@providerId, ProviderID)
 ORDER BY ProviderName ASC;
	END TRY
	BEGIN CATCH
 SELECT @exceptionDetails = ERROR_MESSAGE(), @severity = 'ERROR', @message = 'Failed to retrieve utility providers', @sendEmail = 1;
	END CATCH;

	SET @elapsedTime = DATEDIFF(MILLISECOND, GETDATE(), @startTime);

	EXEC dbo.USP_AppLogCreate @correlationId = @correlationId,@machineName = @machineName,@processName = @processName,@action = @action,@severity = @severity,@message = @message,@messageDetails = @messageDetails,@exceptionDetails = @exceptionDetails,@domain = @domain,@username = @username,@userId = @userId,@sendEmail = @sendEmail,@elapsedTime = @elapsedTime;
END;

CREATE PROCEDURE [Common].[USP_NoteCreate]
(
	@parentId AS INT,@parentType AS VARCHAR(25),@actionType AS VARCHAR(25),@comments AS VARCHAR(500),@correlationId AS VARCHAR(128) = NULL,@machineName AS VARCHAR(128) = NULL,@processName AS VARCHAR(128) = NULL,@domain AS VARCHAR(25) = NULL,@username AS VARCHAR(25) = NULL,@userId AS INT = NULL,@exceptionDetails AS VARCHAR(MAX) = NULL OUTPUT
)
AS
BEGIN
	SET NOCOUNT ON;

	SELECT @parentId = NULLIF(@parentId, 0), @parentType = NULLIF(@parentType, ''), @actionType = NULLIF(@actionType, ''), @comments = NULLIF(@comments, ''),@correlationId = ISNULL(@correlationId, CONVERT(VARCHAR(128), NEWID())), @machineName = COALESCE(@machineName, @@SERVERNAME, 'NA'),@processName = COALESCE(@processName, QUOTENAME(OBJECT_SCHEMA_NAME(@@PROCID)) + '.' + QUOTENAME(OBJECT_NAME(@@PROCID)), 'NA'), @userId = ISNULL(@userId, 0);

	DECLARE @action AS VARCHAR(25) = 'CREATE', @severity AS VARCHAR(25) = 'INFO', @message AS VARCHAR(255) = 'Added note', @messageDetails AS VARCHAR(MAX),@startTime AS DATETIME = GETDATE(), @elapsedTime AS INT, @sendEmail AS BIT = 0, @tranName AS VARCHAR(128) = CONVERT(VARCHAR(128), NEWID());
	
	SELECT @messageDetails = CONVERT(VARCHAR(MAX), (SELECT @parentId AS PID, @parentType AS PTYPE, @actionType AS ACT, @comments AS CMT FOR XML PATH('INPUTS')));

	BEGIN TRY
 BEGIN TRAN @tranName;

 -- Create audit note
 INSERT INTO Common.Note(ParentType, ParentID, [Action], Note, CreatedBy)
 VALUES (@parentType, @parentId, @actionType, @comments, @userId);

 COMMIT TRAN @tranName;
	END TRY
	BEGIN CATCH
 IF (XACT_STATE() <> 0)
 BEGIN
 ROLLBACK TRAN @tranName;
 END;

 SELECT @exceptionDetails = ERROR_MESSAGE(), @severity = 'ERROR', @message = 'Failed to add note', @sendEmail = 1;
	END CATCH;

	SET @elapsedTime = DATEDIFF(MILLISECOND, GETDATE(), @startTime);

	EXEC dbo.USP_AppLogCreate @correlationId = @correlationId,@machineName = @machineName,@processName = @processName,@action = @action,@severity = @severity,@message = @message,@messageDetails = @messageDetails,@exceptionDetails = @exceptionDetails,@domain = @domain,@username = @username,@userId = @userId,@sendEmail = @sendEmail,@elapsedTime = @elapsedTime;
END;

CREATE PROCEDURE [Common].[USP_NoteRetrieve]
(
	@noteId AS INT = NULL,@parentId AS INT = NULL,@parentType AS VARCHAR(25) = NULL,@actionType AS VARCHAR(25) = NULL,@correlationId AS VARCHAR(128) = NULL,@machineName AS VARCHAR(128) = NULL,@processName AS VARCHAR(128) = NULL,@domain AS VARCHAR(25) = NULL,@username AS VARCHAR(25) = NULL,@userId AS INT = NULL,@exceptionDetails AS VARCHAR(MAX) = NULL OUTPUT
)
AS
BEGIN
	SET NOCOUNT ON;

	SELECT @noteId = NULLIF(@noteId, 0), @parentId = NULLIF(@parentId, 0), @parentType = NULLIF(@parentType, ''), @actionType = NULLIF(@actionType, ''),@correlationId = ISNULL(@correlationId, CONVERT(VARCHAR(128), NEWID())), @machineName = COALESCE(@machineName, @@SERVERNAME, 'NA'),@processName = COALESCE(@processName, QUOTENAME(OBJECT_SCHEMA_NAME(@@PROCID)) + '.' + QUOTENAME(OBJECT_NAME(@@PROCID)), 'NA'), @userId = ISNULL(@userId, 0);

	DECLARE @action AS VARCHAR(25) = 'RETRIEVE', @severity AS VARCHAR(25) = 'INFO', @message AS VARCHAR(255) = 'Retrieved note(s)', @messageDetails AS VARCHAR(MAX),@startTime AS DATETIME = GETDATE(), @elapsedTime AS INT, @sendEmail AS BIT = 0;
	
	SELECT @messageDetails = CONVERT(VARCHAR(MAX), (SELECT @noteId AS NID, @actionType AS ACT, @parentId AS PID, @parentType AS PTYPE FOR XML PATH('INPUTS')));

	BEGIN TRY
 SELECT NoteID,[Action],Note,CreatedDate,CreatedBy
 FROM Common.vwNotes
 WHERE NoteID = ISNULL(@noteId, NoteID)
 AND [Action] = ISNULL(@actionType, [Action])
 AND ParentID = ISNULL(@parentId, ParentID)
 AND ParentType = ISNULL(@parentType, ParentType)
 ORDER BY NoteID ASC;
	END TRY
	BEGIN CATCH
 SELECT @exceptionDetails = ERROR_MESSAGE(), @severity = 'ERROR', @message = 'Failed to retrieve note(s)', @sendEmail = 1;
	END CATCH;

	SET @elapsedTime = DATEDIFF(MILLISECOND, GETDATE(), @startTime);

	EXEC dbo.USP_AppLogCreate @correlationId = @correlationId,@machineName = @machineName,@processName = @processName,@action = @action,@severity = @severity,@message = @message,@messageDetails = @messageDetails,@exceptionDetails = @exceptionDetails,@domain = @domain,@username = @username,@userId = @userId,@sendEmail = @sendEmail,@elapsedTime = @elapsedTime;
END;

CREATE PROCEDURE [dbo].[USP_AppLogCreate]
(
	@correlationId AS VARCHAR(128),@machineName AS VARCHAR(128),@processName AS VARCHAR(128),@action AS VARCHAR(25) = 'NA',@severity AS VARCHAR(25) = 'INFO',@message AS VARCHAR(255),@messageDetails AS VARCHAR(MAX) = NULL,@exceptionDetails AS VARCHAR(MAX) = NULL OUTPUT,@domain AS VARCHAR(25) = NULL,@username AS VARCHAR(25) = NULL,@userId AS INT = NULL,@sendEmail AS BIT = 0,@elapsedTime AS INT = NULL
)
AS
BEGIN
	SET NOCOUNT ON;

	BEGIN TRY
 DECLARE @logLevel AS VARCHAR(20) = 'ERROR', @writeLog AS BIT;
 SET @writeLog = CONVERT(BIT, CASE WHEN @action = 'ACCESS' THEN 1
 WHEN @action = 'AUDIT' THEN 1
 WHEN @action = 'TIMER' THEN 1
 WHEN @logLevel = 'FATAL' AND @severity = 'FATAL' THEN 1
 WHEN @logLevel = 'ERROR' AND (@severity = 'FATAL' OR @severity = 'ERROR') THEN 1
 WHEN @logLevel = 'WARN' AND (@severity = 'FATAL' OR @severity = 'ERROR' OR @severity = 'WARN') THEN 1
 WHEN @logLevel = 'INFO' AND (@severity = 'FATAL' OR @severity = 'ERROR' OR @severity = 'WARN' OR @severity = 'INFO') THEN 1
 WHEN @logLevel = 'DEBUG' THEN 1
 ELSE 0 END);

 IF (@writeLog = 1)
 BEGIN
 INSERT INTO [dbo].[AppLog] (CorrelationID, MachineName, ProcessName, [Action], Severity, [Message], MessageDetails,ExceptionDetails, Domain, Username, UserId, ElapsedTime, SendEmail)
 VALUES (@correlationId, @machineName, @processName, @action, @severity, @message, @messageDetails,@exceptionDetails, @domain, @username, @userId, @elapsedTime, @sendEmail);
 END;
	END TRY
	BEGIN CATCH
 SELECT @exceptionDetails = ERROR_MESSAGE();
	END CATCH;
END;

CREATE PROCEDURE [fms].[USP_ReconcileEC3InvoicePaymentsFromFMS]
(
	@correlationId AS VARCHAR(128) = NULL,@machineName AS VARCHAR(128) = NULL,@processName AS VARCHAR(128) = NULL,@domain AS VARCHAR(25) = NULL,@username AS VARCHAR(25) = NULL,@userId AS INT = NULL,@exceptionDetails AS VARCHAR(MAX) = NULL OUTPUT
)
AS
BEGIN
	SET NOCOUNT ON;

	TRUNCATE TABLE fms.Disbursement;
	TRUNCATE TABLE fms.PaymentRequest;

	INSERT INTO fms.Disbursement(ContractNumber,DisbursementID,DisbursementNumber,DisbursementVersionNumber,InvoiceNumber,PaymentType,BANK_ACCT_CD,CHK_EFT_STA,CHK_EFT_NO,CHK_EFT_AM,CHK_EFT_ISS_DT,CHK_EFT_REC_DT,DISB_CAT,CLR_DT)
 SELECT ContractNumber,DisbursementID,DisbursementNumber,DisbursementVersionNumber,InvoiceNumber,PaymentType,BANK_ACCT_CD,CHK_EFT_STA,CHK_EFT_NO,CHK_EFT_AM,CHK_EFT_ISS_DT,CHK_EFT_REC_DT,DISB_CAT,CLR_DT	
 FROM Invoice.vwDisbursements;

	INSERT INTO fms.PaymentRequest(ContractNumber,PaymentRequestID,PaymentRequestNumber,PaymentRequestVersionNumber,InvoiceNumber,DOC_ACTU_AM,PYMT_AM,DOC_APPL_CREA_DT,DOC_APPL_LAST_DT)
 SELECT ContractNumber,PaymentRequestID,PaymentRequestNumber,PaymentRequestVersionNumber,InvoiceNumber,DOC_ACTU_AM,PYMT_AM,DOC_APPL_CREA_DT,DOC_APPL_LAST_DT
 FROM Invoice.vwPaymentRequests;

	EXEC fms.USP_ReconcileInvoicePaymentsUsingFMSDisbursements @correlationId = @correlationId,@machineName = @machineName,@processName = @processName,@domain = @domain,@username = @username,@userId = @userId,@exceptionDetails = @exceptionDetails
END;

CREATE PROCEDURE [fms].[USP_ReconcileInvoicePaymentsUsingFMSDisbursements]
(
	@correlationId AS VARCHAR(128) = NULL,@machineName AS VARCHAR(128) = NULL,@processName AS VARCHAR(128) = NULL,@domain AS VARCHAR(25) = NULL,@username AS VARCHAR(25) = NULL,@userId AS INT = NULL,@exceptionDetails AS VARCHAR(MAX) = NULL
)
AS
BEGIN
	SET NOCOUNT ON;

	DECLARE @action AS VARCHAR(25) = 'RETRIEVE', @severity AS VARCHAR(25) = 'INFO', @message AS VARCHAR(255) = 'Reconciled invoice payments using FMS disbursements(s)',@messageDetails AS VARCHAR(MAX) = NULL, @startTime AS DATETIME = GETDATE(), @elapsedTime AS INT, @sendEmail AS BIT = 0, @tranName AS VARCHAR(128) = CONVERT(VARCHAR(128), NEWID());
	
	SELECT @correlationId = ISNULL(@correlationId, CONVERT(VARCHAR(128), NEWID())), @machineName = COALESCE(@machineName, @@SERVERNAME, 'NA'),@processName = COALESCE(@processName, QUOTENAME(OBJECT_SCHEMA_NAME(@@PROCID)) + '.' + QUOTENAME(OBJECT_NAME(@@PROCID)), 'NA'), @userId = ISNULL(@userId, 0);

	BEGIN TRY
 DECLARE @paymentRequests AS TABLE (InvoiceID INT NOT NULL, InvoiceGroupContractID INT NOT NULL, ContractNumber VARCHAR(30) NOT NULL);
 DECLARE @paidInvoiceGroups AS TABLE (InvoiceID INT NOT NULL, InvoiceGroupContractID INT NOT NULL);

 BEGIN TRAN @tranName;

 -- Find only new payment requests
 INSERT INTO @paymentRequests (InvoiceID, InvoiceGroupContractID, ContractNumber)
 SELECT IGC.InvoiceID, IGC.InvoiceGroupContractID, IGC.ContractNumber
 FROM Invoice.vwInvoiceGroupContracts AS IGC
 INNER JOIN fms.PaymentRequest AS FMSPR ON FMSPR.InvoiceNumber = IGC.InvoiceNumber AND FMSPR.ContractNumber = IGC.ContractNumber
 INNER JOIN Invoice.vwInvoices AS INV ON INV.InvoiceID = IGC.InvoiceID
 WHERE INV.StatusID IN (4, 5, 6);

 -- Update status to payment initiated
 UPDATE INV
 SET INV.StatusID = 7
 FROM Invoice.Invoice AS INV
 INNER JOIN @paymentRequests AS PR ON PR.InvoiceID = INV.InvoiceID;

 -- Create audit note for all invoices that have payment requests initiated
 INSERT INTO Common.Note(ParentType, ParentID, [Action], Note, CreatedBy)
 SELECT 'INVOICE', T.InvoiceID, 'FMS PAYMENT REQUEST', 'Payment request initiated in FMS', 0
 FROM (SELECT DISTINCT InvoiceID FROM @paymentRequests) AS T;

 -- Insert only new payment records
 INSERT INTO Invoice.InvoicePayment(InvoiceGroupContractID, InvoiceID, BillingGroupContractID, GroupID, ContractID, FiscalYear,PaymentType, PaymentNumber, PaymentAmount, DateIssued, DateCleared, CreatedBy)
 OUTPUT INSERTED.InvoiceID, INSERTED.InvoiceGroupContractID INTO @paidInvoiceGroups
 SELECT IGC.InvoiceGroupContractID, IGC.InvoiceID, IGC.BillingGroupContractID, IGC.GroupID, IGC.ContractID, IGC.FiscalYear,FMSDB.PaymentType, FMSDB.CHK_EFT_NO, FMSDB.CHK_EFT_AM, FMSDB.CHK_EFT_ISS_DT, FMSDB.CLR_DT, 0
 FROM Invoice.vwInvoiceGroupContracts AS IGC
 INNER JOIN fms.Disbursement AS FMSDB ON FMSDB.InvoiceNumber = IGC.InvoiceNumber AND FMSDB.ContractNumber = IGC.ContractNumber
 WHERE (IGC.ContractNumber + '-' + IGC.InvoiceNumber) NOT IN (SELECT DISTINCT (ContractNumber + '-' + InvoiceNumber) FROM Invoice.vwInvoicePayments);

 -- Do we need to update existing records?

 -- Update invoice group contracts to paid
 UPDATE IGC
 SET IGC.Paid = 1, ModifiedDate = GETDATE(), ModifiedBy = 0
 FROM Invoice.InvoiceGroupContract AS IGC
 INNER JOIN @paidInvoiceGroups AS T ON T.InvoiceGroupContractID = IGC.InvoiceGroupContractID;

 -- Update invoices to paid if all associated groups are paid
 UPDATE INV
 SET INV.StatusID = 8, ModifiedDate = GETDATE(), ModifiedBy = 0
 FROM Invoice.Invoice AS INV
 WHERE (INV.StatusID <> 8 AND INV.StatusID <> 9)
 AND INV.InvoiceID IN (SELECT DISTINCT InvoiceID
 FROM Invoice.InvoiceGroupContract
 WHERE Paid = 1
 GROUP BY InvoiceID
 HAVING COUNT(1) = COUNT(PAID));

 -- Create audit note for all paid invoices
 INSERT INTO Common.Note(ParentType, ParentID, [Action], Note, CreatedBy)
 SELECT 'INVOICE', T.InvoiceID, 'FMS PAYMENT ISSUED', 'Payment issued in FMS', 0
 FROM (SELECT DISTINCT InvoiceID FROM @paidInvoiceGroups) AS T;

 COMMIT TRAN @tranName;
	END TRY
	BEGIN CATCH
 IF (XACT_STATE() <> 0)
 BEGIN
 ROLLBACK TRAN @tranName;
 END;

 SELECT @exceptionDetails = ERROR_MESSAGE(), @severity = 'ERROR', @message = 'Failed to reconcile invoice payments using FMS disbursements(s)', @sendEmail = 1;
	END CATCH;

	SET @elapsedTime = DATEDIFF(MILLISECOND, GETDATE(), @startTime);

	EXEC dbo.USP_AppLogCreate @correlationId = @correlationId,@machineName = @machineName,@processName = @processName,@action = @action,@severity = @severity,@message = @message,@messageDetails = @messageDetails,@exceptionDetails = @exceptionDetails,@domain = @domain,@username = @username,@userId = @userId,@sendEmail = @sendEmail,@elapsedTime = @elapsedTime;
END;

CREATE PROCEDURE [fms].[USP_ReconcileInvoicePaymentsUsingITSPayments]
(
	@correlationId AS VARCHAR(128) = NULL,@machineName AS VARCHAR(128) = NULL,@processName AS VARCHAR(128) = NULL,@domain AS VARCHAR(25) = NULL,@username AS VARCHAR(25) = NULL,@userId AS INT = NULL,@exceptionDetails AS VARCHAR(MAX) = NULL
)
AS
BEGIN
	SET NOCOUNT ON;

	DECLARE @action AS VARCHAR(25) = 'RETRIEVE', @severity AS VARCHAR(25) = 'INFO', @message AS VARCHAR(255) = 'Reconciled invoice payments using ITS payment(s)',@messageDetails AS VARCHAR(MAX), @startTime AS DATETIME = GETDATE(), @elapsedTime AS INT, @sendEmail AS BIT = 0, @tranName AS VARCHAR(128) = CONVERT(VARCHAR(128), NEWID());

	SELECT @correlationId = ISNULL(@correlationId, CONVERT(VARCHAR(128), NEWID())), @machineName = COALESCE(@machineName, @@SERVERNAME, 'NA'),@processName = COALESCE(@processName, QUOTENAME(OBJECT_SCHEMA_NAME(@@PROCID)) + '.' + QUOTENAME(OBJECT_NAME(@@PROCID)), 'NA'),@messageDetails	= NULL, @userId = ISNULL(@userId, 0);

	BEGIN TRY
 DECLARE @aaProcessing TABLE(InvoiceID INT NOT NULL, InvoiceGroupContractID INT NOT NULL, ContractNumber VARCHAR(30) NOT NULL);
 DECLARE @aaProcessed TABLE(InvoiceID INT NOT NULL, InvoiceGroupContractID INT NOT NULL, ContractNumber VARCHAR(30) NOT NULL);

 BEGIN TRAN @tranName;

 -- Find only new payment requests being processed by A&A
 INSERT INTO @aaProcessing (InvoiceID, InvoiceGroupContractID, ContractNumber)
 SELECT DISTINCT IGC.InvoiceID, IGC.InvoiceGroupContractID, IGC.ContractNumber
 FROM Invoice.vwInvoiceGroupContracts AS IGC
 INNER JOIN its.AuditsAndAccountsPayments AS ITSPR ON RIGHT(ITSPR.InvoiceNumber,9) = RIGHT(IGC.InvoiceNumber, 9) AND RIGHT(IGC.ContractNumber, 11) = ITSPR.PurchaseOrder_Registration
 INNER JOIN Invoice.vwInvoices AS INV ON INV.InvoiceID = IGC.InvoiceID
 WHERE INV.StatusID = 4 AND INV.Active = 1;

 -- Update status to sent to A&A
 UPDATE INV
 SET INV.StatusID = 5, INV.ModifiedDate = GETDATE(), ModifiedBy = 0
 FROM Invoice.Invoice AS INV
 INNER JOIN @aaProcessing AS PR ON PR.InvoiceID = INV.InvoiceID;

 -- Create audit note for all invoices that are being processed by A&A
 INSERT INTO Common.Note(ParentType, ParentID, [Action], Note, CreatedBy)
 SELECT 'INVOICE', T.InvoiceID, 'SENT TO A&A', 'A&A processing invoice', 0
 FROM (SELECT DISTINCT InvoiceID FROM @aaProcessing) AS T;

 -- Find only new payment requests processed by A&A
 INSERT INTO @aaProcessed (InvoiceID, InvoiceGroupContractID, ContractNumber)
 SELECT DISTINCT IGC.InvoiceID, IGC.InvoiceGroupContractID, IGC.ContractNumber
 FROM Invoice.vwInvoiceGroupContracts AS IGC
 INNER JOIN its.AuditsAndAccountsPayments AS ITSPR ON RIGHT(ITSPR.Invoicenumber, 9) = RIGHT(IGC.InvoiceNumber, 9) AND RIGHT(IGC.ContractNumber, 11) = ITSPR.PurchaseOrder_Registration
 INNER JOIN Invoice.vwInvoices AS INV ON INV.InvoiceID = IGC.InvoiceID
 WHERE INV.StatusID = 5 AND INV.Active = 1 AND NOT ITSPR.VoucherNumber IS NULL;

 -- Update status to processed by A&A
 UPDATE INV
 SET INV.StatusID = 6, INV.ModifiedDate = GETDATE(), ModifiedBy = 0
 FROM Invoice.Invoice AS INV
 INNER JOIN @aaProcessed AS PR ON PR.InvoiceID = INV.InvoiceID;

 -- Create audit note for all invoices that have been processed by A&A
 INSERT INTO Common.Note(ParentType, ParentID, [Action], Note, CreatedBy)
 SELECT 'INVOICE', T.InvoiceID, 'PROCESSED BY A&A', 'A&A has sent invoice to FMS', 0
 FROM (SELECT DISTINCT InvoiceID FROM @aaProcessed) AS T;

 COMMIT TRAN @tranName;
	END TRY
	BEGIN CATCH
 IF (XACT_STATE() <> 0)
 BEGIN
 ROLLBACK TRAN @tranName;
 END;

 SELECT @exceptionDetails = ERROR_MESSAGE(), @severity = 'ERROR', @message = 'Failed to reconcile invoice payments using ITS payment(s)', @sendEmail	= 1;
	END CATCH;

	SET @elapsedTime = DATEDIFF(MILLISECOND, GETDATE(), @startTime);

	EXEC dbo.USP_AppLogCreate @correlationId = @correlationId,@machineName = @machineName,@processName = @processName,@action = @action,@severity = @severity,@message = @message,@messageDetails = @messageDetails,@exceptionDetails = @exceptionDetails,@domain = @domain,@username = @username,@userId = @userId,@sendEmail = @sendEmail,@elapsedTime = @elapsedTime;
END;

CREATE PROCEDURE [Invoice].[USP_AccountBillingAvailableByInvoiceBillingGroupRetrieve]
(
	@invoiceId AS INT,@groupId AS INT,@correlationId AS VARCHAR(128) = NULL,@machineName AS VARCHAR(128) = NULL,@processName AS VARCHAR(128) = NULL,@domain AS VARCHAR(25) = NULL,@username AS VARCHAR(25) = NULL,@userId AS INT = NULL,@exceptionDetails AS VARCHAR(MAX) = NULL OUTPUT
)
AS
BEGIN
	SET NOCOUNT ON;

	SELECT @invoiceId = NULLIF(@invoiceId, 0), @groupId = NULLIF(@groupId, 0), @correlationId	= ISNULL(@correlationId, CONVERT(VARCHAR(128), NEWID())),@machineName = COALESCE(@machineName, @@SERVERNAME, 'NA'), @userId = ISNULL(@userId, 0),@processName = COALESCE(@processName, QUOTENAME(OBJECT_SCHEMA_NAME(@@PROCID)) + '.' + QUOTENAME(OBJECT_NAME(@@PROCID)), 'NA');

	DECLARE @action AS VARCHAR(25) = 'RETRIEVE', @severity AS VARCHAR(25) = 'INFO', @message AS VARCHAR(255) = 'Retrieved available account billing(s) by invoice and group',@messageDetails AS VARCHAR(MAX), @startTime AS DATETIME = GETDATE(), @elapsedTime AS INT, @sendEmail AS BIT = 0, @BillingPeriod AS VARCHAR(6);
	
	SELECT @messageDetails = CONVERT(VARCHAR(MAX), (SELECT @invoiceId AS INVID, @groupId AS GRPID FOR XML PATH('INPUTS')));
	SELECT @BillingPeriod = InvoiceBillingPeriod FROM Invoice.Invoice WHERE InvoiceID = @invoiceId;

	BEGIN TRY
 SELECT BillingID,AmountBilled,AmountPaid,DateBilled,DateDue,BillingPeriod,BillingPeriodDays,FromDate,ToDate,IsEstimated,IsManualEntry,AccountID,AccountNumber,FormattedAccountNumber,EnergySource,EnergyUsage,DemandUsage,GroupID,GroupName,ProviderID,ProviderName,CreatedDate,CreatedBy,ModifiedDate,ModifiedBy,Active,ManualPaymentOverrideAmount
 FROM Billing.vwAccountBillings
 WHERE GroupID = ISNULL(@groupId, GroupID)
 AND BillingID NOT IN (SELECT DISTINCT BillingID FROM Invoice.vwInvoiceAccountBillings WHERE GroupID = ISNULL(@groupId, GroupID))
 AND BillingPeriod = @BillingPeriod
 ORDER BY ProviderName ASC, GroupName ASC, BillingPeriod DESC, AccountNumber ASC;
	END TRY
	BEGIN CATCH
 SELECT @exceptionDetails = ERROR_MESSAGE(), @severity = 'ERROR', @message = 'Failed to retrieve available account billing(s) by invoice and group', @sendEmail = 1;
	END CATCH;

	SET @elapsedTime = DATEDIFF(MILLISECOND, GETDATE(), @startTime);

	EXEC dbo.USP_AppLogCreate @correlationId = @correlationId,@machineName = @machineName,@processName = @processName,@action = @action,@severity = @severity,@message = @message,@messageDetails = @messageDetails,@exceptionDetails = @exceptionDetails,@domain = @domain,@username = @username,@userId = @userId,@sendEmail = @sendEmail,@elapsedTime = @elapsedTime;
END;

CREATE PROCEDURE [Invoice].[USP_BillingGroupAvailableByInvoiceRetrieve]
(
	@invoiceId AS INT,@billingType AS CHAR(1) = NULL,@correlationId AS VARCHAR(128) = NULL,@machineName AS VARCHAR(128) = NULL,@processName AS VARCHAR(128) = NULL,@domain AS VARCHAR(25) = NULL,@username AS VARCHAR(25) = NULL,@userId AS INT = NULL,@exceptionDetails AS VARCHAR(MAX) = NULL OUTPUT
)
AS
BEGIN
	SET NOCOUNT ON;

	SELECT @invoiceId = NULLIF(@invoiceId, 0), @billingType = NULLIF(@billingType, ''), @correlationId = ISNULL(@correlationId, CONVERT(VARCHAR(128), NEWID())),@machineName = COALESCE(@machineName, @@SERVERNAME, 'NA'), @userId = ISNULL(@userId, 0),@processName = COALESCE(@processName, QUOTENAME(OBJECT_SCHEMA_NAME(@@PROCID)) + '.' + QUOTENAME(OBJECT_NAME(@@PROCID)), 'NA');

	DECLARE @action AS VARCHAR(25) = 'RETRIEVE', @severity AS VARCHAR(25) = 'INFO', @message AS VARCHAR(255) = 'Retrieved utility provider billing groups for invoices',@messageDetails AS VARCHAR(MAX), @startTime AS DATETIME = GETDATE(), @elapsedTime AS INT, @sendEmail AS BIT = 0, @providerId AS INT;
	
	SELECT @messageDetails = CONVERT(VARCHAR(MAX), (SELECT @invoiceId AS INVID, @billingType AS BILLTYPE FOR XML PATH('INPUTS')));
	SELECT @providerId = ProviderID FROM Invoice.vwInvoices WHERE InvoiceID = @invoiceId;

	IF (ISNULL(@providerId, 0) = 0)
	BEGIN
 RAISERROR (N'Unable to determine provider for invoice', 16, 1);
	END;

	BEGIN TRY
 SELECT GroupID,GroupName,GroupDescription,SummaryAccountNumber,IsSummaryBilling,AccountCount,ProviderID,BillingType,CreatedDate,CreatedBy,ModifiedDate,ModifiedBy,Active
 FROM Billing.vwBillingGroups
 WHERE ProviderID = @providerId
 AND BillingType = ISNULL(@billingType, BillingType)
