 SELECT @exceptionDetails = ERROR_MESSAGE(), @severity = 'ERROR', @message = 'Failed to retrieve utility provider billing groups for invoices', @sendEmail = 1;
	END CATCH;

	SET @elapsedTime = DATEDIFF(MILLISECOND, GETDATE(), @startTime);

	EXEC dbo.USP_AppLogCreate @correlationId = @correlationId,@machineName = @machineName,@processName = @processName,@action = @action,@severity = @severity,@message = @message,@messageDetails = @messageDetails,@exceptionDetails = @exceptionDetails,@domain = @domain,@username = @username,@userId = @userId,@sendEmail = @sendEmail,@elapsedTime = @elapsedTime;
END;

CREATE PROCEDURE [Invoice].[USP_BillingGroupContractSave]
(
	@groupId AS INT,@fiscalYear AS VARCHAR(4),@contractIds AS VARCHAR(4000) = NULL,@correlationId AS VARCHAR(128) = NULL,@machineName AS VARCHAR(128) = NULL,@processName AS VARCHAR(128) = NULL,@domain AS VARCHAR(25) = NULL,@username AS VARCHAR(25) = NULL,@userId AS INT = NULL,@exceptionDetails AS VARCHAR(MAX) = NULL OUTPUT
)
AS
BEGIN
	SET NOCOUNT ON;

	DECLARE @action AS VARCHAR(25) = 'UPDATE', @severity AS VARCHAR(25) = 'INFO', @message AS VARCHAR(255) = 'Saved billing group contract(s)',@messageDetails AS VARCHAR(MAX), @startTime AS DATETIME = GETDATE(), @elapsedTime INT, @sendEmail AS BIT = 0,@tranName AS VARCHAR(128) = CONVERT(VARCHAR(128), NEWID());

	SELECT @groupId = NULLIF(@groupId, 0), @contractIds = NULLIF(@contractIds, ''), @userId = ISNULL(@userId, 0);
	SELECT @correlationId = ISNULL(@correlationId, CONVERT(VARCHAR(128), NEWID())), @machineName = COALESCE(@machineName, @@SERVERNAME, 'NA'),@processName = COALESCE(@processName, QUOTENAME(OBJECT_SCHEMA_NAME(@@PROCID)) + '.' + QUOTENAME(OBJECT_NAME(@@PROCID)), 'NA'),@messageDetails = CONVERT(VARCHAR(MAX), (SELECT @groupId AS INVID, @contractIds AS FY FOR XML PATH('INPUTS')));

	BEGIN TRY
 BEGIN TRAN @tranName;

 DECLARE @newBGC TABLE(GroupID INT,FiscalYear VARCHAR(4),ContractID VARCHAR(100),ToBeAdded BIT,BGCID INT);

 DECLARE @oldBGC TABLE(GroupID INT,FiscalYear VARCHAR(4),ContractID VARCHAR(100),ToBeDeleted BIT,BGCID INT);

 INSERT INTO @newBGC
 SELECT @groupId,@fiscalYear,ID,CONVERT(BIT, 1),CONVERT(INT, 0)
 FROM dbo.UDF_SplitToTable(@contractIds, ',');

 UPDATE T
 SET T.ToBeAdded = 0,T.BGCID = BGC.BillingGroupContractID
 FROM @newBGC AS T
 INNER JOIN Billing.BillingGroupContract AS BGC ON BGC.GroupID = T.GroupID
 AND BGC.FiscalYear = T.FiscalYear AND BGC.ContractID = T.ContractID;

 INSERT INTO @oldBGC
 SELECT GroupID,FiscalYear,ContractID,CONVERT(BIT, 0),BillingGroupContractID
 FROM Billing.BillingGroupContract
 WHERE GroupID = @groupId AND FiscalYear = @fiscalYear;

 UPDATE T
 SET T.ToBeDeleted = 1
 FROM @oldBGC AS T
 WHERE T.BGCID NOT IN (SELECT BGCID FROM @newBGC);

 IF EXISTS (SELECT 1 FROM @newBGC WHERE ToBeAdded = 1)
 BEGIN
 INSERT INTO Billing.BillingGroupContract (GroupID, FiscalYear, ContractID, CreatedBy)
 SELECT GroupID, FiscalYear, ContractID, @userId
 FROM @newBGC
 WHERE ToBeAdded = 1;
 END;

 IF EXISTS (SELECT 1 FROM @newBGC WHERE ToBeAdded = 0)
 BEGIN
 UPDATE BGC
 SET BGC.Active = 1,BGC.ModifiedBy = @userId,BGC.ModifiedDate = GETDATE()
 FROM Billing.BillingGroupContract AS BGC
 INNER JOIN @newBGC AS T ON T.BGCID = BGC.BillingGroupContractID;
 END;

 IF EXISTS (SELECT 1 FROM @oldBGC WHERE ToBeDeleted = 1)
 BEGIN
 UPDATE BGC
 SET BGC.Active = 0,BGC.ModifiedBy = @userId,BGC.ModifiedDate = GETDATE()
 FROM Billing.BillingGroupContract AS BGC
 INNER JOIN @oldBGC AS T ON T.BGCID = BGC.BillingGroupContractID
 WHERE ToBeDeleted = 1;
 END;

 COMMIT TRAN @tranName;
	END TRY
	BEGIN CATCH
 IF (XACT_STATE() <> 0)
 BEGIN
 ROLLBACK TRAN @tranName;
 END;

 SELECT @exceptionDetails = ERROR_MESSAGE(), @severity = 'ERROR', @message = 'Failed to save billing group contract(s)', @sendEmail	= 1;
	END CATCH;

	SET @elapsedTime = DATEDIFF(MILLISECOND, GETDATE(), @startTime);

	EXEC dbo.USP_AppLogCreate @correlationId = @correlationId,@machineName = @machineName,@processName = @processName,@action = @action,@severity = @severity,@message = @message,@messageDetails = @messageDetails,@exceptionDetails = @exceptionDetails,@domain = @domain,@username = @username,@userId = @userId,@sendEmail = @sendEmail,@elapsedTime = @elapsedTime;
END;

CREATE PROCEDURE [Invoice].[USP_ContractAvailableByInvoiceBillingGroupRetrieve]
(
	@invoiceId AS INT,@groupId AS INT,@fiscalYear AS VARCHAR(4),@correlationId AS VARCHAR(128) = NULL,@machineName AS VARCHAR(128) = NULL,@processName AS VARCHAR(128) = NULL,@domain AS VARCHAR(25) = NULL,@username AS VARCHAR(25) = NULL,@userId AS INT = NULL,@exceptionDetails AS VARCHAR(MAX) = NULL OUTPUT
)
AS
BEGIN
	SET NOCOUNT ON;

	SELECT @invoiceId = NULLIF(@invoiceId, 0), @groupId = NULLIF(@groupId, 0), @fiscalYear = NULLIF(@fiscalYear, ''),@correlationId = ISNULL(@correlationId, CONVERT(VARCHAR(128), NEWID())),@machineName = COALESCE(@machineName, @@SERVERNAME, 'NA'), @userId = ISNULL(@userId, 0),@processName = COALESCE(@processName, QUOTENAME(OBJECT_SCHEMA_NAME(@@PROCID)) + '.' + QUOTENAME(OBJECT_NAME(@@PROCID)), 'NA');

	DECLARE @action AS VARCHAR(25) = 'RETRIEVE', @severity AS VARCHAR(25) = 'INFO', @message AS VARCHAR(255) = 'Retrieved utility provider contracts by invoice and billing groups',@messageDetails AS VARCHAR(MAX), @startTime AS DATETIME = GETDATE(), @elapsedTime AS INT, @sendEmail AS BIT = 0;
	
	SELECT @messageDetails = CONVERT(VARCHAR(MAX), (SELECT @invoiceId AS INVID, @groupId AS GRPID, @fiscalYear AS FY FOR XML PATH('INPUTS')));

	BEGIN TRY
 DECLARE @fiscalYearStart AS VARCHAR(8), @fiscalYearEnd AS VARCHAR(8), @providerId AS INT;

 IF (ISNULL(@fiscalYear, '') <> '')
 BEGIN
 SELECT @fiscalYearStart = CONVERT(VARCHAR(4), (CONVERT(INT, @fiscalYear) - 1)) + '0701';
 SELECT @fiscalYearEnd = @fiscalYear + '0630';
 END;

 SELECT @providerId = ProviderID
 FROM Billing.vwBillingGroups
 WHERE GroupID = @groupId;

 DECLARE @tmpInvoiceContracts TABLE(ContractID INT NOT NULL);
 DECLARE @tmpGroupContracts TABLE(ContractID INT NOT NULL);

 INSERT INTO @tmpInvoiceContracts
 SELECT DISTINCT ContractID
 FROM Invoice.vwInvoiceGroupContracts
 WHERE InvoiceID = ISNULL(@invoiceId, InvoiceID) AND GroupID = ISNULL(@groupId, GroupID) AND FiscalYear = ISNULL(@fiscalYear, FiscalYear);

 INSERT INTO @tmpGroupContracts
 SELECT DISTINCT ContractID
 FROM Billing.vwBillingGroupContracts
 WHERE GroupID = ISNULL(@groupId, GroupID) AND FiscalYear = ISNULL(@fiscalYear, FiscalYear);

 SELECT C.ContractID,C.ContractNumber,C.[Name],C.[Description],C.ProviderID,C.ProviderName,C.FiscalYearStart,C.FiscalYearEnd,C.ActualAmount,C.CloseAmount,C.OpenAmount,C.AvailableAmount,C.CreatedDate,C.CreatedBy,C.ModifiedDate,C.ModifiedBy,C.Active,C.CapacityAmount
 FROM Billing.vwContracts AS C
 WHERE C.ProviderID = @providerId
 AND C.FiscalYearStart <= @fiscalYearStart AND C.FiscalYearEnd >= @fiscalYearEnd
 AND C.ContractID NOT IN (SELECT ContractID FROM @tmpInvoiceContracts)
 ORDER BY C.ContractNumber ASC;
	END TRY
	BEGIN CATCH
 SELECT @exceptionDetails = ERROR_MESSAGE(), @severity = 'ERROR', @message = 'Failed to retrieve utility provider contracts by invoice and billing groups', @sendEmail = 1;
	END CATCH;

	SET @elapsedTime = DATEDIFF(MILLISECOND, GETDATE(), @startTime);

	EXEC dbo.USP_AppLogCreate @correlationId = @correlationId,@machineName = @machineName,@processName = @processName,@action = @action,@severity = @severity,@message = @message,@messageDetails = @messageDetails,@exceptionDetails = @exceptionDetails,@domain = @domain,@username = @username,@userId = @userId,@sendEmail = @sendEmail,@elapsedTime = @elapsedTime;
END;

CREATE PROCEDURE [Invoice].[USP_FiscalYearAvailableByInvoiceBillingGroupRetrieve]
(
	@fiscalYear AS VARCHAR(4) = NULL,@groupId AS INT = NULL,@activeOnly AS BIT = NULL,@correlationId AS VARCHAR(128) = NULL,@machineName AS VARCHAR(128) = NULL,@processName AS VARCHAR(128) = NULL,@domain AS VARCHAR(25) = NULL,@username AS VARCHAR(25) = NULL,@userId AS INT = NULL,@exceptionDetails AS VARCHAR(MAX) = NULL OUTPUT
)
AS
BEGIN
	SET NOCOUNT ON;

	DECLARE @action AS VARCHAR(25) = 'RETRIEVE', @severity AS VARCHAR(25) = 'INFO', @message AS VARCHAR(255) = 'Retrieved fiscal years', @messageDetails AS VARCHAR(MAX),@startTime AS DATETIME = GETDATE(), @elapsedTime AS INT, @sendEmail AS BIT = 0;

	SELECT @fiscalYear = NULLIF(@fiscalYear, ''), @groupId = NULLIF(@groupId, 0), @activeOnly = ISNULL(@activeOnly, 0), @userId = ISNULL(@userId, 0),@correlationId = ISNULL(@correlationId, CONVERT(VARCHAR(128), NEWID())), @machineName = COALESCE(@machineName, @@SERVERNAME, 'NA'),@processName = COALESCE(@processName, QUOTENAME(OBJECT_SCHEMA_NAME(@@PROCID)) + '.' + QUOTENAME(OBJECT_NAME(@@PROCID)), 'NA');

	SELECT @messageDetails = CONVERT(VARCHAR(MAX), (SELECT @fiscalYear AS FY, @groupId AS GRPID, @activeOnly AS ACT FOR XML PATH('INPUTS')));

	BEGIN TRY

 DECLARE @tmpFiscalYears TABLE(FiscalYear INT);

 INSERT INTO @tmpFiscalYears
 SELECT DISTINCT FiscalYear
 FROM Billing.vwBillingGroupContracts
 WHERE GroupID = ISNULL(@groupId, GroupID);

 SELECT FY.FiscalYear, FY.FiscalYearDescription, FY.FiscalYearStart, FY.FiscalYearEnd, FY.Active
 FROM Billing.vwFiscalYears AS FY
 INNER JOIN @tmpFiscalYears AS T ON T.FiscalYear = FY.FiscalYear
 WHERE FY.FiscalYear = ISNULL(@fiscalYear, FY.FiscalYear)
 AND FY.Active = CASE WHEN @activeOnly = 1 THEN 1 ELSE FY.Active END
 ORDER BY FY.FiscalYear DESC;
	END TRY
	BEGIN CATCH
 SELECT @exceptionDetails = ERROR_MESSAGE(), @severity = 'ERROR', @message = 'Failed to retrieve fiscal years', @sendEmail = 1;
	END CATCH;

	SET @elapsedTime = DATEDIFF(MILLISECOND, GETDATE(), @startTime);

	EXEC dbo.USP_AppLogCreate @correlationId = @correlationId,@machineName = @machineName,@processName = @processName,@action = @action,@severity = @severity,@message = @message,@messageDetails = @messageDetails,@exceptionDetails = @exceptionDetails,@domain = @domain,@username = @username,@userId = @userId,@sendEmail = @sendEmail,@elapsedTime = @elapsedTime;
END;

CREATE PROCEDURE [Invoice].[USP_InvoiceAccountBillingAdd]
(
	@invoiceGroupId AS INT,@accountIds AS VARCHAR(4000),@correlationId AS VARCHAR(128) = NULL,@machineName AS VARCHAR(128) = NULL,@processName AS VARCHAR(128) = NULL,@domain AS VARCHAR(25) = NULL,@username AS VARCHAR(25) = NULL,@userId AS INT = NULL,@exceptionDetails AS VARCHAR(MAX) = NULL OUTPUT
)
AS
BEGIN
	SET NOCOUNT ON;

	DECLARE @action AS VARCHAR(25) = 'CREATE', @severity AS VARCHAR(25) = 'INFO', @message AS VARCHAR(255) = 'Added invoice billing account(s)', @messageDetails AS VARCHAR(MAX),@startTime AS DATETIME = GETDATE(), @elapsedTime INT, @sendEmail AS BIT = 0, @tranName AS VARCHAR(128) = CONVERT(VARCHAR(128), NEWID());
	
	SELECT @invoiceGroupId = NULLIF(@invoiceGroupId, 0), @accountIds = NULLIF(@accountIds, ''), @userId = ISNULL(@userId, 0),@correlationId = ISNULL(@correlationId, CONVERT(VARCHAR(128), NEWID())), @machineName = COALESCE(@machineName, @@SERVERNAME, 'NA'),@processName = COALESCE(@processName, QUOTENAME(OBJECT_SCHEMA_NAME(@@PROCID)) + '.' + QUOTENAME(OBJECT_NAME(@@PROCID)), 'NA');

	SELECT @messageDetails = CONVERT(VARCHAR(MAX), (SELECT @invoiceGroupId AS INVID, @accountIds AS FY FOR XML PATH('INPUTS')));

	DECLARE @invoiceId AS INT, @groupId AS INT, @contractId AS INT, @fiscalYear AS VARCHAR(4), @invoiceIsSplitFY AS BIT, @priorFY AS INT, @currentFY AS INT;

	SELECT @invoiceId = InvoiceID,@groupId = GroupID,@contractId = ContractID,@fiscalYear = FiscalYear
	FROM Invoice.vwInvoiceGroupContracts
	WHERE InvoiceGroupContractID = @invoiceGroupId;

	SELECT @invoiceIsSplitFY = Billing.UDF_IsSplitFYInvoice(InvoiceID), @currentFY = Billing.UDF_GetFiscalYear(CreatedDate)
	FROM Invoice.Invoice
	WHERE InvoiceID = @invoiceId;

	SET @priorFY = @currentFY-1;

	DECLARE @updatedBudgetCodeTable TABLE (BudgetCode VARCHAR(4));

	BEGIN TRY
 DECLARE @billingTable TABLE(InvoiceBillingID INT);

 BEGIN TRAN @tranName;

 -- Add invoice account billings
 IF @invoiceIsSplitFY = 0
 INSERT INTO Invoice.InvoiceAccountBilling(InvoiceGroupContractID, InvoiceID, GroupID, ContractID, BillingID,AccountID, AccountNumber, FormattedAccountNumber, BillingPeriod, BillingPeriodDays, FromDate, ToDate,SplitFY, BillingPeriodDaysPriorFY, BillingPeriodDaysCurrentFY,IsManualEntry, IsEstimated, DateBilled, DateDue,EnergyUnit,EnergyUsage, EnergyUsagePriorFY, EnergyUsageCurrentFY,DemandUsage, DemandUsagePriorFY, DemandUsageCurrentFY,AmountBilled, AmountPaid, AmountPaidPriorFY, AmountPaidCurrentFY,CreatedBy)
 OUTPUT INSERTED.InvoiceBillingID INTO @billingTable
 SELECT @invoiceGroupId, @invoiceId, @groupId, @contractId, T.ID,B.AccountID, B.AccountNumber, B.FormattedAccountNumber, B.BillingPeriod, B.BillingPeriodDays, B.FromDate, B.ToDate,[Billing].[UDF_IsSplitFYBilling](@invoiceId, B.FromDate, B.ToDate) --switched by Xin on 09/04/2019,0, Billing.UDF_GetBillingPeriodDaysInCurrentFY(@invoiceId, B.FromDate, B.ToDate, B.BillingPeriodDays),B.IsManualEntry, B.IsEstimated, B.DateBilled, B.DateDue,B.EnergyUnit,B.AccountEnergyUsage, 0, B.AccountEnergyUsage,B.AccountDemandUsage, 0, B.AccountDemandUsage,CONVERT(DECIMAL(18, 2), B.AmountBilled), CONVERT(DECIMAL(18, 2), B.AmountPaid), 0, CONVERT(DECIMAL(18, 2), B.AmountPaid),@userId
 FROM [dbo].[UDF_SplitToTable](@accountIds, ',') T
 INNER JOIN [Billing].[vwAccountBillings] B ON B.BillingID = T.ID
 WHERE B.GroupID = @groupId;
 ELSE
 INSERT INTO [Invoice].[InvoiceAccountBilling] (InvoiceGroupContractID, InvoiceID, GroupID, ContractID, BillingID, AccountID, AccountNumber, FormattedAccountNumber, BillingPeriod, BillingPeriodDays, FromDate, ToDate, SplitFY, BillingPeriodDaysPriorFY, BillingPeriodDaysCurrentFY, IsManualEntry, IsEstimated, DateBilled, DateDue, EnergyUnit, EnergyUsage, EnergyUsagePriorFY, EnergyUsageCurrentFY, DemandUsage, DemandUsagePriorFY, DemandUsageCurrentFY, AmountBilled, AmountPaid, AmountPaidPriorFY, AmountPaidCurrentFY, CreatedBy)
 OUTPUT INSERTED.InvoiceBillingID INTO @billingTable
 SELECT IG.InvoiceGroupContractID, @invoiceId, @groupId, IG.ContractID, T.ID, B.AccountID, B.AccountNumber, B.FormattedAccountNumber, B.BillingPeriod, B.BillingPeriodDays, B.FromDate, B.ToDate, [Billing].[UDF_IsSplitFYBilling](@invoiceId, B.FromDate, B.ToDate) --switched by Xin on 09/04/2019, 0, Billing.UDF_GetBillingPeriodDaysInCurrentFY(@invoiceId, B.FromDate, B.ToDate, B.BillingPeriodDays), B.IsManualEntry, B.IsEstimated, B.DateBilled, B.DateDue, B.EnergyUnit, B.AccountEnergyUsage, 0, B.AccountEnergyUsage, B.AccountDemandUsage, 0, B.AccountDemandUsage, CONVERT(DECIMAL(18, 2), B.AmountBilled), CONVERT(DECIMAL(18, 2), B.AmountPaid), 0, CONVERT(DECIMAL(18, 2), B.AmountPaid), @userId
 FROM [dbo].[UDF_SplitToTable](@accountIds, ',') T
 JOIN [Billing].[vwAccountBillings] B ON B.BillingID = T.ID
 JOIN Invoice.vwInvoiceGroupContracts IG ON B.GroupID=IG.GroupID AND 
 (	(Billing.UDF_GetBillingPeriodDaysInCurrentFY(@invoiceId, B.FromDate, B.ToDate, B.BillingPeriodDays)=0 AND IG.FiscalYear=@priorFY) 
 OR 
 (Billing.UDF_GetBillingPeriodDaysInCurrentFY(@invoiceId, B.FromDate, B.ToDate, B.BillingPeriodDays)>0 AND IG.FiscalYear=@currentFY)
 )
 WHERE IG.invoiceID=@invoiceId AND B.GroupID = @groupId; --07/26/2019 added by Xin for ConEd Steam change
 

 -- Update number of billing days in prior FY if there is a difference in FY between from and to dates
 UPDATE BILL
 SET BILL.BillingPeriodDaysPriorFY = BILL.BillingPeriodDays - BILL.BillingPeriodDaysCurrentFY
 FROM [Invoice].[InvoiceAccountBilling] BILL
 JOIN @billingTable T ON T.InvoiceBillingID = BILL.InvoiceBillingID;

 -- Update current FY data if there is a difference in FY between from and to dates
 UPDATE BILL
 SET BILL.AmountPaidCurrentFY = (BILL.BillingPeriodDaysCurrentFY * BILL.AmountPaid) / BILL.BillingPeriodDays, BILL.EnergyUsageCurrentFY = (BILL.BillingPeriodDaysCurrentFY * BILL.EnergyUsage) / BILL.BillingPeriodDays, BILL.DemandUsageCurrentFY = (BILL.BillingPeriodDaysCurrentFY * BILL.DemandUsage) / BILL.BillingPeriodDays
 FROM [Invoice].[InvoiceAccountBilling] BILL
 JOIN @billingTable T ON T.InvoiceBillingID = BILL.InvoiceBillingID;

 -- Update prior FY data if there is a difference in FY between from and to dates
 UPDATE BILL
 SET BILL.AmountPaidPriorFY = BILL.AmountPaid - BILL.AmountPaidCurrentFY, BILL.EnergyUsagePriorFY	= BILL.EnergyUsage - BILL.EnergyUsageCurrentFY, BILL.DemandUsagePriorFY	= BILL.DemandUsage - BILL.DemandUsageCurrentFY
 FROM [Invoice].[InvoiceAccountBilling] BILL
 JOIN @billingTable T ON T.InvoiceBillingID = BILL.InvoiceBillingID;

 -- Create audit note
 INSERT INTO [Common].[Note] (ParentType, ParentID, [Action], Note, CreatedBy)
 VALUES ('INVOICE', @invoiceId, 'ADD ACCOUNTS', 'Billing account(s) added', @userId);

 -- Update Payment Allocations if there is any. Added on 4/16/2018 
 INSERT INTO @updatedBudgetCodeTable (BudgetCode)
 SELECT DISTINCT d.BudgetCode
 FROM Invoice.InvoiceAccountBilling a
 LEFT JOIN Billing.EC3_Billing_Account AS b ON a.AccountID = b.AccountSeqid
 LEFT JOIN Billing.EC3_Billing_AgencyFundingAssignments AS c ON b.AgencyAccount = c.AgencyDivisionSeqid
 LEFT JOIN Billing.EC3_Billing_AgencyFunding AS d ON c.AgencyFundingSeqid = d.AgencyFundingSeqid
 WHERE a.InvoiceBillingID IN (SELECT InvoiceBillingID FROM @billingTable);

 IF @invoiceIsSplitFY = 0
 BEGIN
 DELETE FROM [Invoice].[InvoiceContractLineAllocation]
 WHERE InvoiceContractLineID IN (
 SELECT InvoiceContractLineID 
 FROM invoice.InvoiceContractLine 
 WHERE InvoiceID=@invoiceId AND FiscalYear = @fiscalYear AND ContractID=@contractId AND BudgetCode IN 
 (
 SELECT BudgetCode FROM @updatedBudgetCodeTable
 INTERSECT
 SELECT BudgetCode FROM invoice.InvoiceContractLine WHERE InvoiceID = @invoiceId AND FiscalYear = @fiscalYear AND ContractID = @contractId
 )
 );

 DELETE FROM invoice.InvoiceContractLine 
 WHERE InvoiceID=@invoiceId AND FiscalYear = @fiscalYear AND ContractID=@contractId AND BudgetCode IN 
 (
 SELECT BudgetCode FROM @updatedBudgetCodeTable
 INTERSECT
 SELECT BudgetCode FROM invoice.InvoiceContractLine WHERE InvoiceID = @invoiceId AND FiscalYear = @fiscalYear AND ContractID = @contractId
 );
 END;
 ELSE 
 BEGIN
 DELETE FROM [Invoice].[InvoiceContractLineAllocation]
 WHERE InvoiceContractLineID IN (
 SELECT InvoiceContractLineID 
 FROM invoice.InvoiceContractLine 
 WHERE InvoiceID=@invoiceId 
 AND FiscalYear IN (@priorFY, @currentFY)
 AND ContractID=@contractId 
 AND BudgetCode IN (SELECT BudgetCode FROM @updatedBudgetCodeTable)
 );

 DELETE FROM invoice.InvoiceContractLine 
 WHERE InvoiceID=@invoiceId 
 AND FiscalYear IN (@priorFY, @currentFY)
 AND ContractID=@contractId 
 AND BudgetCode IN (SELECT BudgetCode FROM @updatedBudgetCodeTable);
 END;

 COMMIT TRAN @tranName;
	END TRY
	BEGIN CATCH
 IF XACT_STATE() <> 0 ROLLBACK TRAN @tranName;

 SELECT @exceptionDetails = ERROR_MESSAGE();
 SET @severity	= 'ERROR';
 SET @message	= 'Failed to add invoice billing account(s)';
 SET @sendEmail	= 1;
	END CATCH;

	SET @elapsedTime = DATEDIFF(MILLISECOND, GETDATE(), @startTime);

	EXEC dbo.USP_AppLogCreate @correlationId = @correlationId,@machineName = @machineName,@processName = @processName,@action = @action,@severity = @severity,@message = @message,@messageDetails = @messageDetails,@exceptionDetails = @exceptionDetails,@domain = @domain,@username = @username,@userId = @userId,@sendEmail = @sendEmail,@elapsedTime = @elapsedTime;
END;

CREATE PROCEDURE [Invoice].[USP_InvoiceAccountBillingDelete]
(
	@invoiceGroupId AS INT = NULL,@invoiceBillingId AS INT = NULL,@comments AS VARCHAR(500) = NULL,@correlationId AS VARCHAR(128) = NULL,@machineName AS VARCHAR(128) = NULL,@processName AS VARCHAR(128) = NULL,@domain AS VARCHAR(25) = NULL,@username AS VARCHAR(25) = NULL,@userId AS INT = NULL,@exceptionDetails AS VARCHAR(MAX) = NULL OUTPUT
)
AS
BEGIN
	SET NOCOUNT ON;

	DECLARE @action AS VARCHAR(25) = 'DELETE', @severity AS VARCHAR(25) = 'INFO', @elapsedTime AS INT,@message AS VARCHAR(255) = 'Deleted invoice account(s) and billing(s)', @sendEmail AS BIT = 0,@messageDetails AS VARCHAR(MAX), @startTime AS DATETIME = GETDATE();
	
	SELECT @invoiceGroupId = NULLIF(@invoiceGroupId, 0), @invoiceBillingId = NULLIF(@invoiceBillingId, 0), @userId = ISNULL(@userId, 0),@comments = ISNULL(NULLIF(@comments, ''), 'Deleted invoice account and billing');

	SELECT @correlationId = ISNULL(@correlationId, CONVERT(VARCHAR(128), NEWID())), @machineName = COALESCE(@machineName, @@SERVERNAME, 'NA'),@processName = COALESCE(@processName, QUOTENAME(OBJECT_SCHEMA_NAME(@@PROCID)) + '.' + QUOTENAME(OBJECT_NAME(@@PROCID)), 'NA'),@messageDetails = CONVERT(VARCHAR(MAX), (SELECT @invoiceBillingId AS BILLID, @comments AS CMT FOR XML PATH('INPUTS')));

	IF (ISNULL(@invoiceGroupId, 0) = 0 AND ISNULL(@invoiceBillingId, 0) = 0)
	BEGIN
 RAISERROR (N'Please specify either the account or the group', 16, 1);
	END;

	DECLARE @invoiceId AS INT, @contractID AS INT, @accountID AS INT, @fiscalYear AS VARCHAR(4), @invoiceIsSplitFY AS BIT,@currentFY AS INT, @accountNumber AS VARCHAR(15), @groupId AS INT, @tranName VARCHAR(128) = CONVERT(VARCHAR(128), NEWID());

	SELECT @invoiceId = InvoiceID,@contractID = ContractID,@accountID = AccountID,@fiscalYear = FiscalYear,@accountNumber = ISNULL(AccountNumber, ''),@groupId = GroupID
	FROM Invoice.vwInvoiceAccountBillings
	WHERE InvoiceBillingID = ISNULL(@invoiceBillingId, InvoiceBillingID)
 AND InvoiceGroupContractID = ISNULL(@invoiceGroupId, InvoiceGroupContractID);

	SELECT @invoiceIsSplitFY = Billing.UDF_IsSplitFYInvoice(InvoiceID),@currentFY = Billing.UDF_GetFiscalYear(CreatedDate) 
	FROM Invoice.Invoice
	WHERE InvoiceID=@invoiceId;

	DECLARE @updatedBudgetCodeTable TABLE (BudgetCode VARCHAR(4));

	BEGIN TRY
 BEGIN TRAN @tranName;

 -- Delete account and billing
 DELETE FROM Invoice.InvoiceAccountBilling
 WHERE InvoiceBillingID = ISNULL(@invoiceBillingId, InvoiceBillingID)
 AND InvoiceGroupContractID = ISNULL(@invoiceGroupId, InvoiceGroupContractID);

 -- Create audit note
 INSERT INTO Common.Note(ParentType, ParentID, [Action], Note, CreatedBy)
 VALUES ('INVOICE', @invoiceId, 'DELETE ACCOUNT', @accountNumber + ' deleted: ' + @comments, @userId);

 -- Update Payment Allocations if there is any. Added on 4/16/2018
 INSERT INTO @updatedBudgetCodeTable(BudgetCode)
 SELECT DISTINCT d.BudgetCode
 FROM Billing.EC3_Billing_Account AS b
 LEFT JOIN Billing.EC3_Billing_AgencyFundingAssignments AS c ON b.AgencyAccount = c.AgencyDivisionSeqid
 LEFT JOIN Billing.EC3_Billing_AgencyFunding AS d ON c.AgencyFundingSeqid = d.AgencyFundingSeqid
 WHERE b.AccountSeqid = @accountID;

 IF @invoiceIsSplitFY = 0
 BEGIN
 DELETE FROM [Invoice].[InvoiceContractLineAllocation]
 WHERE InvoiceContractLineID IN (
 SELECT InvoiceContractLineID 
 FROM invoice.InvoiceContractLine 
 WHERE InvoiceID=@invoiceId AND FiscalYear = @fiscalYear AND ContractID=@contractID 
 AND BudgetCode IN (SELECT BudgetCode FROM @updatedBudgetCodeTable)
 );

 DELETE FROM invoice.InvoiceContractLine 
 WHERE InvoiceID=@invoiceId AND FiscalYear = @fiscalYear AND ContractID=@contractID 
 AND BudgetCode IN (SELECT BudgetCode FROM @updatedBudgetCodeTable);
 END;
 ELSE	
 BEGIN
 DELETE FROM [Invoice].[InvoiceContractLineAllocation]
 WHERE InvoiceContractLineID IN (
 SELECT InvoiceContractLineID 
 FROM invoice.InvoiceContractLine 
 WHERE InvoiceID=@invoiceId AND FiscalYear IN (@currentFY-1, @currentFY)
 AND ContractID IN (SELECT ContractID FROM Invoice.vwInvoiceGroupContracts WHERE invoiceid=@invoiceId AND groupid=@groupId) --=@contractID 
 AND BudgetCode IN (SELECT BudgetCode FROM @updatedBudgetCodeTable)
 );

 DELETE FROM invoice.InvoiceContractLine 
 WHERE InvoiceID=@invoiceId AND FiscalYear IN (@currentFY-1, @currentFY)
 AND ContractID IN (SELECT ContractID FROM Invoice.vwInvoiceGroupContracts WHERE invoiceid=@invoiceId AND groupid=@groupId) --=@contractID 
 AND BudgetCode IN (SELECT BudgetCode FROM @updatedBudgetCodeTable);
 END;

 COMMIT TRAN @tranName;
	END TRY
	BEGIN CATCH
 IF XACT_STATE() <> 0 ROLLBACK TRAN @tranName;

 SELECT @exceptionDetails = ERROR_MESSAGE();
 SET @severity	= 'ERROR';
 SET @message	= 'Failed to delete invoice account(s) and billing(s)';
 SET @sendEmail	= 1;
	END CATCH;

	SET @elapsedTime = DATEDIFF(MILLISECOND, GETDATE(), @startTime);

	EXEC dbo.USP_AppLogCreate @correlationId = @correlationId,@machineName = @machineName,@processName = @processName,@action = @action,@severity = @severity,@message = @message,@messageDetails = @messageDetails,@exceptionDetails = @exceptionDetails,@domain = @domain,@username = @username,@userId = @userId,@sendEmail = @sendEmail,@elapsedTime = @elapsedTime;
END;

CREATE PROCEDURE [Invoice].[USP_InvoiceAccountBillingRetrieve]
	@invoiceId INT = NULL,@groupId INT = NULL,@invoiceGroupId INT = NULL,@correlationId AS VARCHAR(128) = NULL,@machineName AS VARCHAR(128) = NULL,@processName AS VARCHAR(128) = NULL,@domain AS VARCHAR(25) = NULL,@username AS VARCHAR(25) = NULL,@userId AS INT = NULL,@exceptionDetails AS VARCHAR(MAX) = NULL OUTPUT
AS
BEGIN
	SET NOCOUNT ON;

	SET @invoiceId = NULLIF(@invoiceId, 0);
	SET @groupId = NULLIF(@groupId, 0);
	SET @invoiceGroupId = NULLIF(@invoiceGroupId, 0);

	IF ISNULL(@invoiceId, 0) <= 0 AND ISNULL(@groupId, 0) <= 0 AND ISNULL(@invoiceGroupId, 0) <= 0
 RAISERROR (N'The invoiceId or the groupId or invoiceGroupId must be specified', 16, 1);

	DECLARE @action VARCHAR(25), @severity VARCHAR(25), @message VARCHAR(255), @messageDetails VARCHAR(MAX), @startTime DATETIME, @elapsedTime INT, @sendEmail BIT;
	SET @startTime = GETDATE();
	SET @correlationId = ISNULL(@correlationId, CONVERT(VARCHAR(128), NEWID()));
	SET @machineName = COALESCE(@machineName, @@SERVERNAME, 'NA');
	SET @processName = COALESCE(@processName, QUOTENAME(OBJECT_SCHEMA_NAME(@@PROCID)) + '.' + QUOTENAME(OBJECT_NAME(@@PROCID)), 'NA');
	SET @action = 'RETRIEVE';
	SET @severity = 'INFO';
	SET @message = 'Invoice - Retrieved invoice accounts / billings';
	SET @messageDetails = CONVERT(VARCHAR(MAX), (SELECT ISNULL(@invoiceId, 0) AS INVID, ISNULL(@groupId, 0) AS GRPID, ISNULL(@invoiceGroupId, 0) AS INVGRPID
 FOR XML PATH('INPUTS')));
	SET @sendEmail = 0;
	SET @userId = ISNULL(@userId, 0);

	BEGIN TRY
 SELECT IAB.InvoiceBillingID, IAB.InvoiceGroupContractID, IAB.InvoiceID, IAB.InvoiceNumber, IAB.GroupID, IAB.GroupName, IAB.ContractID, IAB.ContractNumber, IAB.FiscalYear, IAB.AccountID, IAB.AccountNumber, IAB.FormattedAccountNumber, IAB.BillingID, IAB.BillingPeriod, IAB.BillingPeriodDays, IAB.BillingPeriodDaysPriorFY, IAB.BillingPeriodDaysCurrentFY, IAB.IsEstimated, IAB.IsManualEntry, IAB.AmountBilled, IAB.AmountPaid, IAB.AmountPaidPriorFY, IAB.AmountPaidCurrentFY, IAB.EnergyUsage, IAB.EnergyUsagePriorFY, IAB.EnergyUsageCurrentFY, IAB.DemandUsage, IAB.DemandUsagePriorFY, IAB.DemandUsageCurrentFY, IAB.DateBilled, IAB.DateDue, IAB.FromDate, IAB.ToDate, IAB.SplitFY, IAB.CreatedDate, IAB.CreatedBy, IAB.ModifiedDate, IAB.ModifiedBy, IAB.Active
 FROM [Invoice].[vwInvoiceAccountBillings] IAB
 WHERE IAB.InvoiceID = ISNULL(@invoiceId, IAB.InvoiceID)
 AND IAB.GroupID	= ISNULL(@groupId, IAB.GroupID)
 AND IAB.InvoiceGroupContractID = ISNULL(@invoiceGroupId, IAB.InvoiceGroupContractID)
 ORDER BY IAB.GroupName ASC, IAB.ContractNumber ASC, IAB.FiscalYear DESC, IAB.AccountNumber ASC;
	END TRY
	BEGIN CATCH
 SELECT @exceptionDetails = ERROR_MESSAGE();
 SET @severity	= 'ERROR';
 SET @message	= 'Invoice - Failed to retrieve invoice accounts / billings';
 SET @sendEmail	= 1;
	END CATCH;

	SET @elapsedTime = DATEDIFF(MILLISECOND, GETDATE(), @startTime);

	EXEC dbo.USP_AppLogCreate @correlationId = @correlationId,@machineName = @machineName,@processName = @processName,@action = @action,@severity = @severity,@message = @message,@messageDetails = @messageDetails,@exceptionDetails = @exceptionDetails,@domain = @domain,@username = @username,@userId = @userId,@sendEmail = @sendEmail,@elapsedTime = @elapsedTime;
END;

CREATE PROCEDURE [Invoice].[USP_InvoiceContractLineAdd]
	@invoiceId INT,@contractLineId INT,@amount DECIMAL(18, 2),@allocations [Invoice].[UDT_InvoiceContractLineAllocation] READONLY,@correlationId AS VARCHAR(128) = NULL,@machineName AS VARCHAR(128) = NULL,@processName AS VARCHAR(128) = NULL,@domain AS VARCHAR(25) = NULL,@username AS VARCHAR(25) = NULL,@userId AS INT = NULL,@exceptionDetails AS VARCHAR(MAX) = NULL OUTPUT
AS
BEGIN
	SET NOCOUNT ON;

	SET @invoiceId = NULLIF(@invoiceId, 0);
	SET @contractLineId = NULLIF(@contractLineId, 0);
	SET @amount = NULLIF(@amount, 0);

	DECLARE @action VARCHAR(25), @severity VARCHAR(25), @message VARCHAR(255), @messageDetails VARCHAR(MAX), @startTime DATETIME, @elapsedTime INT, @sendEmail BIT;
	SET @startTime = GETDATE();
	
	SET @correlationId = ISNULL(@correlationId, CONVERT(VARCHAR(128), NEWID()));
	SET @machineName = COALESCE(@machineName, @@SERVERNAME, 'NA');
	SET @processName = COALESCE(@processName, QUOTENAME(OBJECT_SCHEMA_NAME(@@PROCID)) + '.' + QUOTENAME(OBJECT_NAME(@@PROCID)), 'NA');
	SET @action = 'CREATE';
	SET @severity = 'INFO';
	SET @message = 'Added invoice contract line';
	SET @messageDetails = CONVERT(VARCHAR(MAX), (SELECT @invoiceId AS INVID, @contractLineId AS CLINEID, @amount AS AMT FOR XML PATH('INPUTS')));
	SET @sendEmail = 0;
	SET @userId = ISNULL(@userId, 0);

	DECLARE @tranName VARCHAR(128);
	SET @tranName = CONVERT(VARCHAR(128), NEWID());

	DECLARE @noteFiller VARCHAR(500);
	SELECT @noteFiller = (CL.ContractNumber + ',' + CONVERT(VARCHAR(10), CL.CommodityLine) + ',' + CONVERT(VARCHAR(10), CL.AccountingLine))
	FROM [Billing].[vwContractLines] CL
	WHERE CL.ContractLineID = @contractLineId;
	SET @noteFiller = ISNULL(@noteFiller, CONVERT(VARCHAR(10), @contractLineId) + ',NA,NA');

	DECLARE @invoiceIsSplitFY BIT;
	SELECT	@invoiceIsSplitFY = [Billing].[UDF_IsSplitFYInvoice](@invoiceId);

	DECLARE @invoiceContractLineTable TABLE (InvoiceContractLineID INT, FiscalYear VARCHAR(4), Amount DECIMAL(18, 2));

	BEGIN TRY
 BEGIN TRAN @tranName;

 IF EXISTS (SELECT 1 FROM Invoice.InvoiceContractLine WHERE InvoiceID = @invoiceId AND ContractLineID = @contractLineId)
 BEGIN
 -- Update contract line(s)
 UPDATE [Invoice].[InvoiceContractLine]
 SET	AmountPaid = AmountPaid + @amount, ModifiedBy = @userId, ModifiedDate = GETDATE()
 OUTPUT INSERTED.InvoiceContractLineID, INSERTED.FiscalYear, INSERTED.AmountPaid INTO @invoiceContractLineTable
 WHERE InvoiceID = @invoiceId AND ContractLineID = @contractLineId;
 

 -- Update default contract line allocation(s)
 IF @invoiceIsSplitFY =1
 UPDATE [Invoice].[InvoiceContractLineAllocation]
 SET	AllocationAmount = AllocationAmount + b.Amount, ModifiedBy = @userId, ModifiedDate = GETDATE()
 FROM [Invoice].[InvoiceContractLineAllocation] a JOIN @allocations b ON a.AllocationFiscalYear=b.FiscalYear
 WHERE a.InvoiceContractLineID = (SELECT InvoiceContractLineID FROM @invoiceContractLineTable);

 ELSE
 UPDATE [Invoice].[InvoiceContractLineAllocation]
 SET	AllocationAmount = AllocationAmount + @amount, ModifiedBy = @userId, ModifiedDate = GETDATE()
 WHERE InvoiceContractLineID = (SELECT InvoiceContractLineID FROM @invoiceContractLineTable) 
 AND AllocationFiscalYear = (SELECT FiscalYear FROM @invoiceContractLineTable);
 
 -- Create audit note
 INSERT INTO [Common].[Note] (ParentType, ParentID, [Action], Note, CreatedBy)
 VALUES ('INVOICE', @invoiceId, 'UPDATE CONTRACT LINE', 'Via Inserting New Payment, Contract line (' + @noteFiller + ') updated', @userId);
 END;
 ELSE
 BEGIN
 -- Add contract line(s)
 INSERT INTO [Invoice].[InvoiceContractLine] (InvoiceID, ContractLineID, ContractID, FiscalYear, CommodityLine, AccountingLine, BudgetCode, ObjectCode, UoA, AmountPaid, CreatedBy)
 OUTPUT INSERTED.InvoiceContractLineID, INSERTED.FiscalYear, INSERTED.AmountPaid INTO @invoiceContractLineTable
 SELECT @invoiceId, CL.ContractLineId, CL.ContractID, CL.FiscalYear, CL.CommodityLine, CL.AccountingLine, CL.BudgetCode, CL.ObjectCode, CL.UoA, @amount, @userId
 FROM [Billing].[ContractLine] CL
 WHERE CL.ContractLineID = @contractLineId;

 -- Add default contract line allocation(s)
 IF @invoiceIsSplitFY =1
 INSERT INTO [Invoice].[InvoiceContractLineAllocation] (InvoiceContractLineID, AllocationFiscalYear, AllocationAmount, Comments, CreatedBy)
 SELECT (SELECT ICL.InvoiceContractLineID FROM @invoiceContractLineTable ICL) AS InvoiceContractLineID, FiscalYear, Amount, Comments, @userId
 FROM @allocations;

 ELSE
 INSERT INTO [Invoice].[InvoiceContractLineAllocation] (InvoiceContractLineID, AllocationFiscalYear, AllocationAmount, CreatedBy)
 SELECT ICL.InvoiceContractLineID, ICL.FiscalYear, ICL.Amount, @userId 
 FROM @invoiceContractLineTable ICL;

 -- Create audit note
 INSERT INTO [Common].[Note] (ParentType, ParentID, [Action], Note, CreatedBy)
 VALUES ('INVOICE', @invoiceId, 'ADD CONTRACT LINE', 'Contract line (' + @noteFiller + ') added', @userId);

 END;

 COMMIT TRAN @tranName;
	END TRY
	BEGIN CATCH
 IF XACT_STATE() <> 0 ROLLBACK TRAN @tranName;

 SELECT @exceptionDetails = ERROR_MESSAGE();
 SET @severity	= 'ERROR';
 SET @message	= 'Failed to add invoice contract line';
 SET @sendEmail	= 1;
	END CATCH;

	SET @elapsedTime = DATEDIFF(MILLISECOND, GETDATE(), @startTime);

	EXEC dbo.USP_AppLogCreate @correlationId = @correlationId,@machineName = @machineName,@processName = @processName,@action = @action,@severity = @severity,@message = @message,@messageDetails = @messageDetails,@exceptionDetails = @exceptionDetails,@domain = @domain,@username = @username,@userId = @userId,@sendEmail = @sendEmail,@elapsedTime = @elapsedTime;
END;

CREATE PROCEDURE [Invoice].[USP_InvoiceContractLineAllocationRetrieve]
	@invoiceId INT = NULL,@invoiceContractLineId	INT = NULL,@correlationId VARCHAR(128) = NULL,@machineName VARCHAR(128) = NULL,@processName VARCHAR(128) = NULL,@domain VARCHAR(25) = NULL,@username VARCHAR(25) = NULL,@userId INT = NULL,@exceptionDetails VARCHAR(MAX) = NULL OUTPUT
AS
BEGIN
	SET NOCOUNT ON;

	SET @invoiceId = NULLIF(@invoiceId, 0);
	SET @invoiceContractLineId	= NULLIF(@invoiceContractLineId, 0);

	DECLARE @action VARCHAR(25), @severity VARCHAR(25), @message VARCHAR(255), @messageDetails VARCHAR(MAX), @startTime DATETIME, @elapsedTime INT, @sendEmail BIT;
	SET @startTime = GETDATE();
	
	SET @correlationId = ISNULL(@correlationId, CONVERT(VARCHAR(128), NEWID()));
	SET @machineName = COALESCE(@machineName, @@SERVERNAME, 'NA');
	SET @processName = COALESCE(@processName, QUOTENAME(OBJECT_SCHEMA_NAME(@@PROCID)) + '.' + QUOTENAME(OBJECT_NAME(@@PROCID)), 'NA');
	SET @action = 'RETRIEVE';
	SET @severity = 'INFO';
	SET @message = 'Retrieved invoice contract line allocation(s)';
	SET @messageDetails = CONVERT(VARCHAR(MAX), (SELECT @invoiceId AS INVID, @invoiceContractLineId AS ICLINEID
 FOR XML PATH('INPUTS')));
	SET @sendEmail = 0;
	SET @userId = ISNULL(@userId, 0);

	BEGIN TRY
 SELECT ICLA.InvoiceContractLineAllocationID, ICLA.AllocationFiscalYear, ICLA.AllocationAmount, ICLA.InvoiceContractLineID, ICLA.InvoiceID, ICLA.InvoiceNumber, ICLA.ContractID, ICLA.ContractNumber, ICLA.FiscalYear, ICLA.CommodityLine, ICLA.AccountingLine, ICLA.BudgetCode, ICLA.ObjectCode, ICLA.UoA, ICLA.AmountPaid, ICLA.Comments, ICLA.CreatedBy, ICLA.CreatedDate, ICLA.ModifiedBy, ICLA.ModifiedDate, ICLA.Active
 FROM [Invoice].[vwInvoiceContractLineAllocations] ICLA
 WHERE ICLA.InvoiceID = ISNULL(@invoiceId, ICLA.InvoiceID)
 AND ICLA.InvoiceContractLineID	= ISNULL(@invoiceContractLineId, ICLA.InvoiceContractLineID)
 ORDER BY ICLA.InvoiceNumber ASC, ICLA.CommodityLine ASC, ICLA.AccountingLine ASC;
	END TRY
	BEGIN CATCH
 SELECT @exceptionDetails = ERROR_MESSAGE();
 SET @severity	= 'ERROR';
 SET @message	= 'Failed to retrieve invoice contract line allocation(s)';
 SET @sendEmail	= 1;
	END CATCH;

	SET @elapsedTime = DATEDIFF(MILLISECOND, GETDATE(), @startTime);

	EXEC dbo.USP_AppLogCreate @correlationId = @correlationId,@machineName = @machineName,@processName = @processName,@action = @action,@severity = @severity,@message = @message,@messageDetails = @messageDetails,@exceptionDetails = @exceptionDetails,@domain = @domain,@username = @username,@userId = @userId,@sendEmail = @sendEmail,@elapsedTime = @elapsedTime;
END;

CREATE PROCEDURE [Invoice].[USP_InvoiceContractLineDelete]
	@invoiceId INT = NULL,@invoiceContractLineId	INT = NULL,@comments VARCHAR(500) = NULL,@correlationId VARCHAR(128) = NULL,@machineName VARCHAR(128) = NULL,@processName VARCHAR(128) = NULL,@domain VARCHAR(25) = NULL,@username VARCHAR(25) = NULL,@userId INT = NULL,@exceptionDetails VARCHAR(MAX) = NULL OUTPUT
AS
BEGIN
	SET NOCOUNT ON;

	SET @invoiceId = NULLIF(@invoiceId, 0);
	SET @invoiceContractLineId	= NULLIF(@invoiceContractLineId, 0);
	SET @comments = ISNULL(NULLIF(@comments, ''), 'Deleted invoice contract line');

	DECLARE @action VARCHAR(25), @severity VARCHAR(25), @message VARCHAR(255), @messageDetails VARCHAR(MAX), @startTime DATETIME, @elapsedTime INT, @sendEmail BIT;
	SET @startTime = GETDATE();
	
	SET @correlationId = ISNULL(@correlationId, CONVERT(VARCHAR(128), NEWID()));
	SET @machineName = COALESCE(@machineName, @@SERVERNAME, 'NA');
	SET @processName = COALESCE(@processName, QUOTENAME(OBJECT_SCHEMA_NAME(@@PROCID)) + '.' + QUOTENAME(OBJECT_NAME(@@PROCID)), 'NA');
	SET @action = 'DELETE';
	SET @severity = 'INFO';
	SET @message = 'Deleted invoice contract line';
	SET @messageDetails = CONVERT(VARCHAR(MAX), (SELECT @invoiceContractLineId AS BILLID, @comments AS CMT FOR XML PATH('INPUTS')));
	SET @sendEmail = 0;
	SET @userId = ISNULL(@userId, 0);

	IF ISNULL(@invoiceId, 0) = 0 AND ISNULL(@invoiceContractLineId, 0) <> 0
 SELECT @invoiceId = InvoiceID FROM [Invoice].[vwInvoiceContractLines] CL WHERE CL.InvoiceContractLineID = @invoiceContractLineId;

	IF ISNULL(@invoiceId, 0) = 0 AND ISNULL(@invoiceContractLineId, 0) = 0
 RAISERROR (N'Please specify either the invoice or the line', 16, 1);

	DECLARE @noteFiller VARCHAR(500);
	IF @invoiceContractLineId > 0
 SELECT @noteFiller = (CL.ContractNumber + ',' + CONVERT(VARCHAR(10), CL.CommodityLine) + ',' + CONVERT(VARCHAR(10), CL.AccountingLine))
 FROM [Invoice].[vwInvoiceContractLines] CL
 WHERE CL.InvoiceContractLineID = @invoiceContractLineId;
	ELSE IF @invoiceId > 0
 SET @noteFiller = 'All lines';

	DECLARE @tranName VARCHAR(128);
	SET @tranName = CONVERT(VARCHAR(128), NEWID());

	BEGIN TRY
 BEGIN TRAN @tranName;

 -- Delete invoice contract line allocations
 IF ISNULL(@invoiceContractLineId, 0) > 0
 BEGIN
 -- By specific invoice contract line
 DELETE FROM [Invoice].[InvoiceContractLineAllocation]
 WHERE InvoiceContractLineID	= @invoiceContractLineId;
 END
 
 ELSE IF ISNULL(@invoiceId, 0) > 0
 BEGIN
 -- By invoice
 DELETE ICLA
 FROM [Invoice].[InvoiceContractLineAllocation] ICLA
 JOIN [Invoice].[InvoiceContractLine] ICL ON ICLA.InvoiceContractLineID = ICL.InvoiceContractLineID
 WHERE ICL.InvoiceID = @invoiceId;
 END

 -- Delete invoice contract line
 DELETE FROM [Invoice].[InvoiceContractLine]
 WHERE InvoiceID = ISNULL(@invoiceId, InvoiceID)
 AND InvoiceContractLineID	= ISNULL(@invoiceContractLineId, InvoiceContractLineID);

 -- Create deletion record
 INSERT INTO [Common].[Note] (ParentType, ParentID, [Action], Note, CreatedBy)
 VALUES ('INVOICE', @invoiceId, 'REMOVE CONTRACT LINE', @noteFiller, @userId);

 -- Create audit note
 INSERT INTO [Common].[Note] (ParentType, ParentID, [Action], Note, CreatedBy)
 VALUES ('INVOICE', @invoiceId, 'REMOVE CONTRACT LINE', @comments, @userId);

 COMMIT TRAN @tranName;
	END TRY
	BEGIN CATCH
 IF XACT_STATE() <> 0 ROLLBACK TRAN @tranName;

 SELECT @exceptionDetails = ERROR_MESSAGE();
 SET @severity	= 'ERROR';
 SET @message	= 'Failed to delete invoice contract line';
 SET @sendEmail	= 1;
	END CATCH;

	SET @elapsedTime = DATEDIFF(MILLISECOND, GETDATE(), @startTime);

	EXEC dbo.USP_AppLogCreate @correlationId = @correlationId,@machineName = @machineName,@processName = @processName,@action = @action,@severity = @severity,@message = @message,@messageDetails = @messageDetails,@exceptionDetails = @exceptionDetails,@domain = @domain,@username = @username,@userId = @userId,@sendEmail = @sendEmail,@elapsedTime = @elapsedTime;
END;

CREATE PROCEDURE [Invoice].[USP_InvoiceContractLineRetrieve]
	@invoiceId INT = NULL,@invoiceContractLineId	INT = NULL,@correlationId VARCHAR(128) = NULL,@machineName VARCHAR(128) = NULL,@processName VARCHAR(128) = NULL,@domain VARCHAR(25) = NULL,@username VARCHAR(25) = NULL,@userId INT = NULL,@exceptionDetails VARCHAR(MAX) = NULL OUTPUT
AS
BEGIN
	SET NOCOUNT ON;

	SET @invoiceId = NULLIF(@invoiceId, 0);
	SET @invoiceContractLineId	= NULLIF(@invoiceContractLineId, 0);

	DECLARE @action VARCHAR(25), @severity VARCHAR(25), @message VARCHAR(255), @messageDetails VARCHAR(MAX), @startTime DATETIME, @elapsedTime INT, @sendEmail BIT;
	SET @startTime = GETDATE();
	
	SET @correlationId = ISNULL(@correlationId, CONVERT(VARCHAR(128), NEWID()));
	SET @machineName = COALESCE(@machineName, @@SERVERNAME, 'NA');
	SET @processName = COALESCE(@processName, QUOTENAME(OBJECT_SCHEMA_NAME(@@PROCID)) + '.' + QUOTENAME(OBJECT_NAME(@@PROCID)), 'NA');
	SET @action = 'RETRIEVE';
	SET @severity = 'INFO';
	SET @message = 'Retrieved invoice contract line(s)';
	SET @messageDetails = CONVERT(VARCHAR(MAX), (SELECT @invoiceId AS INVID, @invoiceContractLineId AS ICLINEID
 FOR XML PATH('INPUTS')));
	SET @sendEmail = 0;
	SET @userId = ISNULL(@userId, 0);

	BEGIN TRY
 DECLARE @invoiceIsSplitFY BIT, @currentFY INT;

 IF @invoiceId IS NULL
 SELECT @invoiceId = InvoiceID 
 FROM invoice.InvoiceContractLine 
 WHERE InvoiceContractLineID = @invoiceContractLineId;

 SET @invoiceIsSplitFY = Billing.UDF_IsSplitFYInvoice(@invoiceId);

 SELECT @currentFY = Billing.UDF_GetFiscalYear(CreatedDate) 
 FROM Invoice.Invoice
 WHERE InvoiceID=@invoiceId;

 SELECT DISTINCT ICL.InvoiceContractLineID, ICL.InvoiceID, ICL.InvoiceNumber, ICL.ContractID, ICL.ContractNumber, ICL.ContractLineID, ICL.FiscalYear, ICL.CommodityLine, ICL.AccountingLine, ICL.BudgetCode, ICL.ObjectCode, ICL.UoA, ICL.ActualAmount, ICL.CloseAmount, ICL.OpenAmount, ICL.AvailableAmount, ICL.AmountPaid, ICL.CreatedDate, ICL.CreatedBy, ICL.ModifiedDate, ICL.ModifiedBy, ICL.Active, SUM(IIF(ICLA.AllocationFiscalYear<Billing.UDF_GetFiscalYear(I.CreatedDate), ICLA.AllocationAmount, 0)) OVER (PARTITION BY ICL.InvoiceContractLineID) AS PriorFYAmountPaid, SUM(IIF(ICLA.AllocationFiscalYear=Billing.UDF_GetFiscalYear(I.CreatedDate), ICLA.AllocationAmount, 0)) OVER (PARTITION BY ICL.InvoiceContractLineID) AS CurrentFYAmountPaid, @invoiceIsSplitFY AS InvoiceIsSplitFY, IIF(@invoiceIsSplitFY = 1, @currentFY, ICL.FiscalYear) AS CurrentFY

 FROM [Invoice].[vwInvoiceContractLines] ICL
 JOIN Invoice.InvoiceContractLineAllocation ICLA ON ICL.InvoiceContractLineID=ICLA.InvoiceContractLineID
 JOIN Invoice.Invoice I ON icl.InvoiceID=I.InvoiceID
 WHERE ICL.InvoiceID = ISNULL(@invoiceId, ICL.InvoiceID)
 AND ICL.InvoiceContractLineID	= ISNULL(@invoiceContractLineId, ICL.InvoiceContractLineID)
 ORDER BY ICL.CommodityLine ASC, ICL.AccountingLine ASC;
	END TRY
	BEGIN CATCH
 SELECT @exceptionDetails = ERROR_MESSAGE();
 SET @severity	= 'ERROR';
 SET @message	= 'Failed to retrieve invoice contract line(s)';
 SET @sendEmail	= 1;
	END CATCH;

	SET @elapsedTime = DATEDIFF(MILLISECOND, GETDATE(), @startTime);
 
	EXEC dbo.USP_AppLogCreate @correlationId = @correlationId,@machineName = @machineName,@processName = @processName,@action = @action,@severity = @severity,@message = @message,@messageDetails = @messageDetails,@exceptionDetails = @exceptionDetails,@domain = @domain,@username = @username,@userId = @userId,@sendEmail = @sendEmail,@elapsedTime = @elapsedTime;
END;

CREATE PROCEDURE [Invoice].[USP_InvoiceContractLineUpdate]
	@invoiceContractLineId INT,@amount DECIMAL(18, 2),@allocations [Invoice].[UDT_InvoiceContractLineAllocation] READONLY,@correlationId VARCHAR(128) = NULL,@machineName VARCHAR(128) = NULL,@processName VARCHAR(128) = NULL,@domain VARCHAR(25) = NULL,@username VARCHAR(25) = NULL,@userId INT = NULL,@exceptionDetails VARCHAR(MAX) = NULL OUTPUT
AS
BEGIN
	SET NOCOUNT ON;

	SET @invoiceContractLineId	= NULLIF(@invoiceContractLineId, 0);
	SET @amount = NULLIF(@amount, 0);

	DECLARE @action VARCHAR(25), @severity VARCHAR(25), @message VARCHAR(255), @messageDetails VARCHAR(MAX), @startTime DATETIME, @elapsedTime INT, @sendEmail BIT;
	SET @startTime = GETDATE();
	
	SET @correlationId = ISNULL(@correlationId, CONVERT(VARCHAR(128), NEWID()));
	SET @machineName = COALESCE(@machineName, @@SERVERNAME, 'NA');
	SET @processName = COALESCE(@processName, QUOTENAME(OBJECT_SCHEMA_NAME(@@PROCID)) + '.' + QUOTENAME(OBJECT_NAME(@@PROCID)), 'NA');
	SET @action = 'UPDATE';
	SET @severity = 'INFO';
	SET @message = 'Updated invoice contract line';
	SET @messageDetails = CONVERT(VARCHAR(MAX), (SELECT @invoiceContractLineId AS ICLINEID, @amount AS AMT, (SELECT * FROM @allocations FOR XML PATH('ALLOC'), TYPE)
 FOR XML PATH('INPUTS')));
	SET @sendEmail = 0;
	SET @userId = ISNULL(@userId, 0);

	DECLARE @invoiceId INT;
	SELECT @invoiceId = InvoiceID FROM [Invoice].[vwInvoiceContractLines] CL WHERE CL.InvoiceContractLineID = @invoiceContractLineId;

	IF ISNULL(@invoiceId, 0) = 0 AND ISNULL(@invoiceContractLineId, 0) = 0
 RAISERROR (N'Please specify either the invoice or the line', 16, 1);

	DECLARE @noteFiller VARCHAR(500);
	SELECT @noteFiller = (CL.ContractNumber + ',' + CONVERT(VARCHAR(10), CL.CommodityLine) + ',' + CONVERT(VARCHAR(10), CL.AccountingLine))
	FROM [Invoice].[vwInvoiceContractLines] CL
	WHERE CL.InvoiceContractLineID = @invoiceContractLineId;

	DECLARE @tranName VARCHAR(128);
	SET @tranName = CONVERT(VARCHAR(128), NEWID());

	BEGIN TRY
 BEGIN TRAN @tranName;

 -- Update contract line
 UPDATE [Invoice].[InvoiceContractLine] SET AmountPaid = @amount
 WHERE InvoiceContractLineID = @invoiceContractLineId;

 -- Delete existing contract line allocations
 DELETE FROM [Invoice].[InvoiceContractLineAllocation]
 WHERE InvoiceContractLineID = @invoiceContractLineId;

 -- Insert new contract line allocations
 INSERT INTO [Invoice].[InvoiceContractLineAllocation] (InvoiceContractLineID, AllocationFiscalYear, AllocationAmount, Comments, CreatedBy)
 SELECT @invoiceContractLineId, FiscalYear, Amount, Comments, @userId
 FROM @allocations;

 -- Create audit note
 INSERT INTO [Common].[Note] (ParentType, ParentID, [Action], Note, CreatedBy)
 VALUES ('INVOICE', @invoiceId, 'UPDATE CONTRACT LINE', 'Updated contract line (' + @noteFiller + ')', @userId);

 COMMIT TRAN @tranName;
	END TRY
	BEGIN CATCH
 IF XACT_STATE() <> 0 ROLLBACK TRAN @tranName;

 SELECT @exceptionDetails = ERROR_MESSAGE();
 SET @severity	= 'ERROR';
 SET @message	= 'Failed to update invoice contract line';
 SET @sendEmail	= 1;
	END CATCH;

	SET @elapsedTime = DATEDIFF(MILLISECOND, GETDATE(), @startTime);

	EXEC dbo.USP_AppLogCreate @correlationId = @correlationId,@machineName = @machineName,@processName = @processName,@action = @action,@severity = @severity,@message = @message,@messageDetails = @messageDetails,@exceptionDetails = @exceptionDetails,@domain = @domain,@username = @username,@userId = @userId,@sendEmail = @sendEmail,@elapsedTime = @elapsedTime;
END;

CREATE PROCEDURE [Invoice].[USP_InvoiceCreate]
	@providerId INT,@billingPeriod VARCHAR(6),@invoiceId INT = NULL OUTPUT,@correlationId AS VARCHAR(128) = NULL,@machineName AS VARCHAR(128) = NULL,@processName AS VARCHAR(128) = NULL,@domain AS VARCHAR(25) = NULL,@username AS VARCHAR(25) = NULL,@userId AS INT = NULL,@exceptionDetails AS VARCHAR(MAX) = NULL OUTPUT
AS
BEGIN
	SET NOCOUNT ON;

	SET @providerId = NULLIF(@providerId, 0);
	SET @billingPeriod = NULLIF(@billingPeriod, '');

	DECLARE @action VARCHAR(25), @severity VARCHAR(25), @message VARCHAR(255), @messageDetails VARCHAR(MAX), @startTime DATETIME, @elapsedTime INT, @sendEmail BIT;
	SET @startTime = GETDATE();
	
	SET @correlationId = ISNULL(@correlationId, CONVERT(VARCHAR(128), NEWID()));
	SET @machineName = COALESCE(@machineName, @@SERVERNAME, 'NA');
	SET @processName = COALESCE(@processName, QUOTENAME(OBJECT_SCHEMA_NAME(@@PROCID)) + '.' + QUOTENAME(OBJECT_NAME(@@PROCID)), 'NA');
	SET @action = 'CREATE';
	SET @severity = 'INFO';
	SET @message = 'Created invoice';
	SET @messageDetails = CONVERT(VARCHAR(MAX), (SELECT @providerId AS PRVID, @billingPeriod AS PRD FOR XML PATH('INPUTS')));
	SET @sendEmail = 0;
	SET @userId = ISNULL(@userId, 0);

	DECLARE @tranName VARCHAR(128);
	SET @tranName = CONVERT(VARCHAR(128), NEWID());

	BEGIN TRY
 DECLARE @invoiceNumber VARCHAR(25), @ctr INT;
 SELECT @ctr = MAX(RIGHT(InvoiceNumber, 3)) FROM [Invoice].[vwInvoices] WHERE InvoiceBillingPeriod = @billingPeriod;
 SET @ctr = ISNULL(@ctr, 0) + 1;
 SET @invoiceNumber = 'DEM_' + @billingPeriod + RIGHT('0000' + CONVERT(VARCHAR(10), @ctr), 3);


 DECLARE @invoiceTable TABLE(InvoiceID INT);

 BEGIN TRAN @tranName;

 -- Create invoice header
 INSERT INTO [Invoice].[Invoice] (InvoiceNumber, ProviderID, InvoiceBillingPeriod, StatusID, CreatedBy)
 OUTPUT INSERTED.InvoiceID INTO @invoiceTable
 VALUES (@invoiceNumber, @providerId, @billingPeriod, 1, @userId);

 -- Create audit note
 INSERT INTO [Common].[Note] (ParentType, ParentID, [Action], Note, CreatedBy)
 SELECT 'INVOICE', T.InvoiceID, 'CREATE', 'Draft created', @userId FROM @invoiceTable T;

 COMMIT TRAN @tranName;

 SELECT @invoiceId = T.InvoiceID FROM @invoiceTable T;
	END TRY
	BEGIN CATCH
 IF XACT_STATE() <> 0 ROLLBACK TRAN @tranName;

 SELECT @exceptionDetails = ERROR_MESSAGE();
 SET @severity	= 'ERROR';
 SET @message	= 'Failed to create invoice';
 SET @sendEmail	= 1;
	END CATCH;

	SET @elapsedTime = DATEDIFF(MILLISECOND, GETDATE(), @startTime);

	EXEC dbo.USP_AppLogCreate @correlationId = @correlationId,@machineName = @machineName,@processName = @processName,@action = @action,@severity = @severity,@message = @message,@messageDetails = @messageDetails,@exceptionDetails = @exceptionDetails,@domain = @domain,@username = @username,@userId = @userId,@sendEmail = @sendEmail,@elapsedTime = @elapsedTime;
END;

CREATE PROCEDURE [Invoice].[USP_InvoiceDelete]
(
	@invoiceId AS INT,@comments AS VARCHAR(500) = NULL,@correlationId AS VARCHAR(128) = NULL,@machineName AS VARCHAR(128) = NULL,@processName AS VARCHAR(128) = NULL,@domain AS VARCHAR(25) = NULL,@username AS VARCHAR(25) = NULL,@userId AS INT = NULL,@exceptionDetails AS VARCHAR(MAX) = NULL OUTPUT
)
AS
BEGIN
	SET NOCOUNT ON;

	DECLARE @action AS VARCHAR(25), @severity AS VARCHAR(25), @message AS VARCHAR(255), @messageDetails AS VARCHAR(MAX),@startTime AS DATETIME, @elapsedTime AS INT, @sendEmail AS BIT, @tranName AS VARCHAR(128);

	SELECT @invoiceId = NULLIF(@invoiceId, 0), @comments = ISNULL(NULLIF(@comments, ''), 'Deleted invoice');
	SELECT @startTime = GETDATE(), @action = 'DELETE', @severity = 'INFO', @message = 'Deleted invoice', @sendEmail = 0,@userId = ISNULL(@userId, 0), @tranName = CONVERT(VARCHAR(128), NEWID()),@correlationId = ISNULL(@correlationId, CONVERT(VARCHAR(128), NEWID())),@machineName = COALESCE(@machineName, @@SERVERNAME, 'NA'),@processName = COALESCE(@processName, QUOTENAME(OBJECT_SCHEMA_NAME(@@PROCID)) + '.' + QUOTENAME(OBJECT_NAME(@@PROCID)), 'NA'),@messageDetails = CONVERT(VARCHAR(MAX), (SELECT @invoiceId AS PRVID, @comments AS CMT FOR XML PATH('INPUTS')));	

	BEGIN TRY
 BEGIN TRAN @tranName;

 -- Delete from invoice payments
 DELETE FROM [Invoice].[InvoicePayment] WHERE InvoiceID = @invoiceId;

 -- Delete from invoice contract line allocations
 DELETE CLA
 FROM Invoice.InvoiceContractLineAllocation AS CLA
 INNER JOIN Invoice.InvoiceContractLine AS CL ON CL.InvoiceContractLineID = CLA.InvoiceContractLineID
 WHERE CL.InvoiceID = @invoiceId;

 -- Delete from invoice contract lines
 DELETE FROM Invoice.InvoiceContractLine WHERE InvoiceID = @invoiceId;

 -- Unlock associated bills in EC3 (for electronic billing only)
 UPDATE EB
 SET EB.InvoiceTrackingSeqid = NULL,EB.VoucherBillingStatus = NULL,EB.VoucherBillingStatusPeriod = NULL,EB.IsLockedForEdit = 'N',EB.LastUpdate = GETDATE(),EB.AuthenticatedUserID = @userId
 FROM Billing.EC3_ManualBill_AccountManualBillingHeader_ElectronicBilling AS EB
 INNER JOIN Invoice.InvoiceAccountBilling AS ACCT ON ACCT.BillingID = EB.AccountManualBillingHeaderSeqid
 INNER JOIN Invoice.InvoiceGroupContract AS IGC ON IGC.InvoiceGroupContractID = ACCT.InvoiceGroupContractID
 INNER JOIN Billing.BillingGroupContract AS BGC ON BGC.BillingGroupContractID = IGC.BillingGroupContractID
 INNER JOIN Billing.vwBillingGroups AS BG ON BG.GroupID = BGC.GroupID
 WHERE IGC.InvoiceID = @invoiceId AND BG.BillingType = 'E'
 AND ACCT.BillingID NOT IN (SELECT DISTINCT BillingID FROM Invoice.InvoiceAccountBilling WHERE InvoiceID <> @invoiceId);

 -- Delete from invoice account billings
 DELETE FROM Invoice.InvoiceAccountBilling WHERE InvoiceID = @invoiceId;

 -- Delete from invoice group contracts
 DELETE FROM Invoice.InvoiceGroupContract WHERE InvoiceID = @invoiceId;

 -- Delete from invoice group contracts
 DELETE FROM Invoice.Invoice WHERE InvoiceID = @invoiceId;

 -- Update invoice header
 UPDATE Invoice.Invoice
 SET Active = 0,ModifiedDate = GETDATE(),ModifiedBy	= @userId
 WHERE InvoiceID = @invoiceId;

 -- Create audit note
 INSERT INTO Common.Note(ParentType, ParentID, [Action], Note, CreatedBy)
 VALUES ('INVOICE', @invoiceId, 'DELETED', @comments, @userId);

 COMMIT TRAN @tranName;
	END TRY
	BEGIN CATCH
 IF (XACT_STATE() <> 0)
 BEGIN
 ROLLBACK TRAN @tranName;
 END;

 SELECT @exceptionDetails = ERROR_MESSAGE(), @severity = 'ERROR', @message = 'Failed to update invoice', @sendEmail	= 1;
	END CATCH;

	SET @elapsedTime = DATEDIFF(MILLISECOND, GETDATE(), @startTime);

	EXEC dbo.USP_AppLogCreate @correlationId = @correlationId,@machineName = @machineName,@processName = @processName,@action = @action,@severity = @severity,@message = @message,@messageDetails = @messageDetails,@exceptionDetails = @exceptionDetails,@domain = @domain,@username = @username,@userId = @userId,@sendEmail = @sendEmail,@elapsedTime = @elapsedTime;
END;

CREATE PROCEDURE [Invoice].[USP_InvoiceEmailAddressRetrieve]
(
	@invoiceID AS INT = 0,@paymentNumber AS VARCHAR(255) = NULL,@applicationId AS VARCHAR(128) = NULL,@correlationId AS VARCHAR(128) = NULL,@machineName AS VARCHAR(128) = NULL,@processName AS VARCHAR(128) = NULL,@domain AS VARCHAR(25) = NULL,@username AS VARCHAR(25) = NULL,@userId AS INT = NULL,@exceptionDetails AS VARCHAR(MAX) = NULL OUTPUT
)
AS
BEGIN
	SET NOCOUNT ON;
	
	SELECT @invoiceID = NULLIF(@invoiceID, 0), @paymentNumber = NULLIF(@paymentNumber, '');

	IF (ISNULL(@invoiceId, 0) <= 0 AND @paymentNumber IS NULL)
	BEGIN
 RAISERROR (N'The invoiceId or paymentNumber must be specified', 16, 1);
	END;

	DECLARE @action AS VARCHAR(25), @severity AS VARCHAR(25), @message AS VARCHAR(255), @messageDetails AS VARCHAR(MAX),@startTime AS DATETIME, @elapsedTime AS INT, @sendEmail AS BIT, @ConEdSteamProfileId AS INT, @ConEdOtherProfileId AS INT,@LIPAProfileId AS INT, @NGridEastProfileId AS INT, @PAofNYNJProfileId AS INT, @MetroProfileId AS INT, @NavyYardProfileId AS INT,@GenerateHudson AS INT, @CentralHudson AS INT, @NYCEDC AS INT, @NYSOMH AS INT, @notificationProfileId AS INT = 0;
	DECLARE @providerList AS TABLE (providerID INT NOT NULL);
	SELECT @startTime = GETDATE(), @applicationId = ISNULL(@applicationId, 103), @correlationId = ISNULL(@correlationId, CONVERT(VARCHAR(128), NEWID())),@machineName = COALESCE(@machineName, @@SERVERNAME, 'NA'), @processName = COALESCE(@processName, QUOTENAME(OBJECT_SCHEMA_NAME(@@PROCID)) + '.' + QUOTENAME(OBJECT_NAME(@@PROCID)), 'NA'),@action = 'RETRIEVE', @severity = 'INFO', @message = 'Invoices - Retrieved Provider Emails by InvoiceID', @messageDetails = CONVERT(VARCHAR(MAX), (SELECT @invoiceID AS InvoiceID FOR XML PATH('INPUTS'))),@sendEmail = 0, @userId = ISNULL(@userId, 0);
 
	INSERT INTO @providerList (providerID) VALUES (1), (2), (3), (7), (9), (12), (16), (18), (19), (43), (47), (69);

	BEGIN TRY
 SELECT @ConEdSteamProfileId = [Con Edison Steam Payment Notification],
 @ConEdOtherProfileId = [Con Edison Payment Notification],
 @LIPAProfileId = [PSEG Payment Notification],
 @NGridEastProfileId = [National Grid East Payment Notification],
 @PAofNYNJProfileId = [PAofNYNJ Payment Notification],
 @MetroProfileId = [Metro Energy Payment Notification],
 @NavyYardProfileId = [Brooklyn Navy Yard Payment Notification],
 @GenerateHudson = [Generate Hudson Payment Notification],
 @CentralHudson = [Central Hudson Payment Notification],
 @NYCEDC = [NYCEDC Payment Notification],
 @NYSOMH = [NYSOMH Payment Notification]
 FROM (SELECT [MassDistributionProfileSeqid], shortDesc
 FROM Billing.EC3_Contact_MassDistributionProfile
 WHERE shortDesc IN ('PSEG Payment Notification', 'Con Edison Steam Payment Notification', 'Con Edison Payment Notification', 'National Grid East Payment Notification',
 'PAofNYNJ Payment Notification', 'Metro Energy Payment Notification', 'Brooklyn Navy Yard Payment Notification', 'Generate Hudson Payment Notification',
 'Central Hudson Payment Notification', 'NYCEDC Payment Notification', 'NYSOMH Payment Notification')
 ) AS SourceTable
 PIVOT (MAX([MassDistributionProfileSeqid]) FOR shortDesc IN ([PSEG Payment Notification], [Con Edison Steam Payment Notification], [Con Edison Payment Notification],
 [National Grid East Payment Notification], [PAofNYNJ Payment Notification], [Metro Energy Payment Notification], [Brooklyn Navy Yard Payment Notification],
 [Generate Hudson Payment Notification], [Central Hudson Payment Notification], [NYCEDC Payment Notification], [NYSOMH Payment Notification])
 ) AS pvt;

 SELECT @notificationProfileId =
 CASE WHEN ProviderID = 3 THEN @LIPAProfileId
 WHEN ProviderID = 18 THEN @NGridEastProfileId
 WHEN ProviderID = 43 THEN @PAofNYNJProfileId
 WHEN ProviderID = 1 THEN @MetroProfileId
 WHEN ProviderID = 19 THEN @NavyYardProfileId
 WHEN ProviderID = 69 THEN @GenerateHudson
 WHEN ProviderID = 12 THEN @CentralHudson
 WHEN ProviderID = 47 THEN @NYCEDC
 WHEN ProviderID = 16 THEN @NYSOMH
 WHEN (ProviderID = 9 AND GroupID = 10) THEN @ConEdSteamProfileId 
 ELSE @ConEdOtherProfileId
 END
 FROM (SELECT DISTINCT a.ProviderID, MIN(IIF(b.GroupID = 10, 10, 0)) AS groupid
 FROM Invoice.Invoice AS a
 INNER JOIN Invoice.InvoicePayment AS b ON a.InvoiceID = b.InvoiceID
 WHERE a.InvoiceID = ISNULL(@invoiceID, a.invoiceid) AND b.PaymentNumber = ISNULL(@paymentNumber, b.PaymentNumber)
 AND a.ProviderID IN (SELECT providerID FROM @providerList)
 GROUP BY a.ProviderID) AS tmp;

 SELECT a.MassDistributionProfileSeqid,c.ContactAddressSeqid,c.ContactName,c.ContactLastName,c.ContactFirstName,c.ContactMI,c.Curtsytitle,c.ContactTelephoneNumber,c.EmailAddress,CAST(IIF(c.UtilityCompanyContact IS NOT NULL, 1, 0) AS BIT) AS IsTo,CAST(IIF(c.AgencyContact IS NOT NULL, 1, 0) AS BIT) AS IsCc
 FROM Billing.EC3_Contact_MassDistributionProfile AS a
 INNER JOIN Billing.EC3_Contact_MassDistributionListReportContext AS b ON a.MassDistributionProfileSeqid = b.MassDistributionProfileSeqid
 INNER JOIN Billing.EC3_Contact_ContactAddress AS c ON b.ContactAddressSeqid = c.ContactAddressSeqid
 WHERE a.MassDistributionProfileSeqid = @notificationProfileId AND c.IsRemoved = 'N'
 ORDER BY a.MassDistributionProfileSeqid, IsTo DESC, c.ContactLastName, c.ContactFirstName;
	END TRY
	BEGIN CATCH
 SELECT @exceptionDetails = ERROR_MESSAGE(), @severity = 'ERROR', @message = 'Invoices - Failed to retrieve Provider Emails by InvoiceID', @sendEmail = 1;
	END CATCH;

	SET @elapsedTime = DATEDIFF(MILLISECOND, GETDATE(), @startTime);

	EXEC dbo.USP_AppLogCreate @correlationId = @correlationId,@machineName = @machineName,@processName = @processName,@action = @action,@severity = @severity,@message = @message,@messageDetails = @messageDetails,@exceptionDetails = @exceptionDetails,@domain = @domain,@username = @username,@userId = @userId,@sendEmail	= @sendEmail,@elapsedTime = @elapsedTime
END;

