CREATE PROCEDURE [Invoice].[USP_InvoiceEmailUnsentInvoiceRetrieve]
(
	@PaymentNumber AS VARCHAR(255) = NULL,@PreviousDayNum AS INT = 1000,@applicationId AS VARCHAR(128) = NULL,@correlationId AS VARCHAR(128) = NULL,@machineName AS VARCHAR(128) = NULL,@processName AS VARCHAR(128) = NULL,@domain AS VARCHAR(25) = NULL,@username	AS VARCHAR(25) = NULL,@userId AS INT = NULL,@exceptionDetails AS VARCHAR(MAX) = NULL OUTPUT
)
AS
BEGIN
	SET NOCOUNT ON;	
	SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;

	SELECT @PaymentNumber = NULLIF(@PaymentNumber, ''), @PreviousDayNum = ISNULL(@PreviousDayNum, 9999);

	DECLARE @action AS VARCHAR(25), @severity AS VARCHAR(25), @message AS VARCHAR(255), @messageDetails AS VARCHAR(MAX),@startTime AS DATETIME, @elapsedTime AS INT, @sendEmail AS BIT;
	DECLARE @providerList TABLE(providerID INT NOT NULL);

	SELECT @startTime = GETDATE(), @applicationId = ISNULL(@applicationId, 103), @correlationId = ISNULL(@correlationId, CONVERT(VARCHAR(128), NEWID())),
 @machineName = COALESCE(@machineName, @@SERVERNAME, 'NA'), @processName = COALESCE(@processName, QUOTENAME(OBJECT_SCHEMA_NAME(@@PROCID)) + '.' + QUOTENAME(OBJECT_NAME(@@PROCID)), 'NA'),
 @action = 'RETRIEVE', @severity = 'INFO', @message = 'Invoices - Retrieved Invoices by Payment# (notification is unsent)',
 @messageDetails = CONVERT(VARCHAR(MAX), (SELECT ISNULL(@PaymentNumber, @PreviousDayNum) AS PaymentNumber FOR XML PATH('INPUTS'))),
 @sendEmail = 0, @userId = ISNULL(@userId, 0);
	INSERT INTO @providerList (providerID) VALUES (1), (2), (3), (7), (9), (12), (16), (18), (19), (43), (47), (69);

	BEGIN TRY
 SELECT DISTINCT b.invoiceid, b.invoicenumber, b.providerid, c.ProviderName, a.PaymentType, a.PaymentNumber, a.PaymentAmount, a.DateIssued,CAST(IIF(a.groupid=10, 1, 0) AS BIT) AS IsConEdSteam
 FROM Invoice.InvoicePayment AS a
 INNER JOIN Invoice.Invoice AS b ON a.InvoiceID = b.InvoiceID
 INNER JOIN Billing.vwProviders AS c ON b.providerid = c.ProviderID
 WHERE a.PaymentNumber = ISNULL(@PaymentNumber, a.PaymentNumber) AND b.ProviderID IN (SELECT providerID FROM @providerList) AND b.StatusID >= 8 
 AND NOT EXISTS (SELECT 1 FROM Invoice.InvoiceProviderEmailLog AS b WHERE a.PaymentNumber = b.EFTNumber AND a.InvoiceID = b.InvoiceID AND b.Success = 1)
 AND a.DateIssued >= DATEADD(DAY, -120, GETDATE())
 ORDER BY a.PaymentNumber, b.invoiceid;
	END TRY
	BEGIN CATCH
 SELECT @exceptionDetails = ERROR_MESSAGE(), @severity = 'ERROR', @message = 'Invoices - Failed to retrieve Invoices by Payment# (notification is unsent)',@sendEmail = 1;
	END CATCH;

	SET @elapsedTime = DATEDIFF(MILLISECOND, GETDATE(), @startTime);

	EXEC dbo.USP_AppLogCreate @correlationId = @correlationId,@machineName = @machineName,@processName = @processName,@action = @action,@severity = @severity,@message = @message,@messageDetails = @messageDetails,@exceptionDetails = @exceptionDetails,@domain = @domain,@username = @username,@userId = @userId,@sendEmail = @sendEmail,@elapsedTime = @elapsedTime;
END;

CREATE PROCEDURE [Invoice].[USP_InvoiceGroupContractAdd]
(
	@invoiceId AS INT,@fiscalYear AS VARCHAR(4),@groupId AS INT,@contractId AS INT,@correlationId AS VARCHAR(128) = NULL,@machineName AS VARCHAR(128) = NULL,@processName AS VARCHAR(128) = NULL,@domain AS VARCHAR(25) = NULL,@username AS VARCHAR(25) = NULL,@userId AS INT = NULL,@exceptionDetails AS VARCHAR(MAX) = NULL OUTPUT
)
AS
BEGIN
	SET NOCOUNT ON;

	SELECT @invoiceId = NULLIF(@invoiceId, 0), @fiscalYear = NULLIF(@fiscalYear, ''), @groupId = NULLIF(@groupId, 0), @contractId = NULLIF(@contractId, 0),@correlationId = ISNULL(@correlationId, CONVERT(VARCHAR(128), NEWID())), @machineName = COALESCE(@machineName, @@SERVERNAME, 'NA'),@processName = COALESCE(@processName, QUOTENAME(OBJECT_SCHEMA_NAME(@@PROCID)) + '.' + QUOTENAME(OBJECT_NAME(@@PROCID)), 'NA'), @userId = ISNULL(@userId, 0);

	DECLARE @action AS VARCHAR(25) = 'CREATE', @severity AS VARCHAR(25) = 'INFO', @message AS VARCHAR(255) = 'Added invoice billing group and contract',@messageDetails AS VARCHAR(MAX), @startTime AS DATETIME = GETDATE(), @elapsedTime AS INT, @sendEmail AS BIT = 0, @tranName VARCHAR(128) = CONVERT(VARCHAR(128), NEWID())
	
	SELECT @messageDetails = CONVERT(VARCHAR(MAX), (SELECT @invoiceId AS INVID, @fiscalYear AS FY, @groupId AS GRPID, @contractId AS CTRCTID FOR XML PATH('INPUTS')));

	DECLARE @billingGroupContractId INT, @noteFiller VARCHAR(500), @isElectronicSummaryBilling BIT, @billingPeriod VARCHAR(6), @invoiceGroupId INT, @summaryAcctNumber VARCHAR(15), @invoiceNumAction VARCHAR(30);

	SELECT @billingGroupContractId = BillingGroupContractID
	FROM Billing.BillingGroupContract
	WHERE ContractID = @contractId AND GroupID = @groupId AND FiscalYear = @fiscalYear;

	SELECT @noteFiller = (GroupName + ',' + FiscalYear + ',' + ContractNumber)
	FROM Billing.vwBillingGroupContracts
	WHERE ContractID = @contractId AND GroupID = @groupId AND FiscalYear = @fiscalYear;
	SET @noteFiller = ISNULL(@noteFiller, CONVERT(VARCHAR(10), @groupId) + ',' + @fiscalYear + ',NA');

	SET @isElectronicSummaryBilling = 0;
	
	IF EXISTS (SELECT 1 FROM Billing.vwBillingGroups WHERE GroupID = @groupId AND BillingType = 'E')
	BEGIN
 SET @isElectronicSummaryBilling = 1;
	END;

	SET @invoiceNumAction = '';
	SELECT @summaryAcctNumber = SummaryAccountNumber FROM Billing.vwBillingGroups WHERE GroupID = @groupId;

	SELECT @billingPeriod = InvoiceBillingPeriod FROM [Invoice].[vwInvoices] WHERE InvoiceID = @invoiceId;

	BEGIN TRY
 DECLARE @invoiceGroupTable TABLE(InvoiceGroupID INT);
 DECLARE @billingTable TABLE(InvoiceBillingID INT);

 BEGIN TRAN @tranName;

 -- Add invoice group
 INSERT INTO Invoice.InvoiceGroupContract(InvoiceID, BillingGroupContractID, CreatedBy)
 OUTPUT INSERTED.InvoiceGroupContractID INTO @invoiceGroupTable
 VALUES (@invoiceId, @billingGroupContractId, @userId);

 -- Determine inserted invoice group identifier
 SELECT @invoiceGroupId = CONVERT(INT, InvoiceGroupID) FROM @invoiceGroupTable;

 -- Add associated accounts / billings if electronic billing and summary billing type
 IF (@isElectronicSummaryBilling = 1)
 BEGIN
 INSERT INTO Invoice.InvoiceAccountBilling(InvoiceGroupContractID, InvoiceID, GroupID, ContractID, BillingID,AccountID, AccountNumber, BillingPeriod,BillingPeriodDays, FromDate, ToDate,SplitFY,BillingPeriodDaysPriorFY,BillingPeriodDaysCurrentFY,IsManualEntry, IsEstimated, DateBilled, DateDue,EnergyUnit,EnergyUsage,EnergyUsagePriorFY,EnergyUsageCurrentFY,DemandUsage,DemandUsagePriorFY,DemandUsageCurrentFY,AmountBilled,AmountPaid,AmountPaidPriorFY,AmountPaidCurrentFY,CreatedBy)
 OUTPUT INSERTED.InvoiceBillingID INTO @billingTable
 SELECT @invoiceGroupId, @invoiceId, @groupId, @contractId, B.BillingID,B.AccountID, B.AccountNumber, B.BillingPeriod, B.BillingPeriodDays, B.FromDate, B.ToDate,Billing.UDF_IsSplitFYBilling(@invoiceId, B.FromDate, B.ToDate),CASE WHEN Billing.UDF_IsBillingInPreviousFY(@invoiceId, B.FromDate, B.ToDate) = 1 THEN B.BillingPeriodDays ELSE 0 END BillingPeriodDaysPriorFY,CASE WHEN Billing.UDF_IsBillingInPreviousFY(@invoiceId, B.FromDate, B.ToDate) = 1 THEN 0 ELSE B.BillingPeriodDays END BillingPeriodDaysCurrrentFY,B.IsManualEntry, B.IsEstimated, B.DateBilled, B.DateDue,B.EnergyUnit,B.AccountEnergyUsage,CASE WHEN Billing.UDF_IsBillingInPreviousFY(@invoiceId, B.FromDate, B.ToDate) = 1 THEN B.AccountEnergyUsage ELSE 0 END EnergyUsagePriorFY,CASE WHEN Billing.UDF_IsBillingInPreviousFY(@invoiceId, B.FromDate, B.ToDate) = 1 THEN 0 ELSE B.AccountEnergyUsage END EnergyUsageCurrentFY,B.AccountDemandUsage,CASE WHEN Billing.UDF_IsBillingInPreviousFY(@invoiceId, B.FromDate, B.ToDate) = 1 THEN B.AccountDemandUsage ELSE 0 END DemandUsagePriorFY,CASE WHEN Billing.UDF_IsBillingInPreviousFY(@invoiceId, B.FromDate, B.ToDate) = 1 THEN 0 ELSE B.AccountDemandUsage END DemandUsageCurrentFY,CONVERT(DECIMAL(18, 2), B.AmountBilled),CONVERT(DECIMAL(18, 2), B.AmountPaid),CASE WHEN Billing.UDF_IsBillingInPreviousFY(@invoiceId, B.FromDate, B.ToDate) = 1 THEN CONVERT(DECIMAL(18, 2), B.AmountPaid) ELSE 0 END AmountPaidPriorFY,CASE WHEN Billing.UDF_IsBillingInPreviousFY(@invoiceId, B.FromDate, B.ToDate) = 1 THEN 0 ELSE CONVERT(DECIMAL(18, 2), B.AmountPaid) END AmountPaidCurrentFY,@userId
 FROM Billing.vwAccountBillings AS B
 WHERE B.GroupID = @groupId AND B.BillingPeriod = @billingPeriod AND B.IsLockedForEdit='N'
 AND B.BillingID NOT IN (SELECT DISTINCT BillingID FROM Invoice.InvoiceAccountBilling WHERE GroupID = @groupId);

 -- Update number of billing days in prior FY if there is a difference in FY between from and to dates
 UPDATE BILL
 SET BILL.BillingPeriodDaysPriorFY = BILL.BillingPeriodDays - DATEDIFF(d, CONVERT(DATETIME, CONVERT(VARCHAR(4), YEAR(ToDate)) + '0630', 112), ToDate),BILL.BillingPeriodDaysCurrentFY = DATEDIFF(d, CONVERT(DATETIME, CONVERT(VARCHAR(4), YEAR(ToDate)) + '0630', 112), ToDate)
 FROM Invoice.InvoiceAccountBilling AS BILL
 INNER JOIN @billingTable T ON T.InvoiceBillingID = BILL.InvoiceBillingID
 WHERE BILL.SplitFY = 1;

 IF EXISTS (SELECT 1 FROM [Invoice].[InvoiceAccountBilling] BILL JOIN @billingTable T ON T.InvoiceBillingID = BILL.InvoiceBillingID WHERE BILL.SplitFY = 1)
 BEGIN
 -- Update current FY data if there is a difference in FY between from and to dates
 UPDATE BILL
 SET BILL.AmountPaidCurrentFY = (BILL.BillingPeriodDaysCurrentFY * BILL.AmountPaid) / BILL.BillingPeriodDays,BILL.EnergyUsageCurrentFY = (BILL.BillingPeriodDaysCurrentFY * BILL.EnergyUsage) / BILL.BillingPeriodDays,BILL.DemandUsageCurrentFY = (BILL.BillingPeriodDaysCurrentFY * BILL.DemandUsage) / BILL.BillingPeriodDays
 FROM Invoice.InvoiceAccountBilling AS BILL
 INNER JOIN @billingTable T ON T.InvoiceBillingID = BILL.InvoiceBillingID
 WHERE BILL.SplitFY = 1;

 -- Update current FY data if there is a difference in FY between from and to dates
 UPDATE BILL
 SET BILL.AmountPaidPriorFY = BILL.AmountPaid - BILL.AmountPaidCurrentFY,BILL.EnergyUsagePriorFY = BILL.EnergyUsage - BILL.EnergyUsageCurrentFY,BILL.DemandUsagePriorFY = BILL.DemandUsage - BILL.DemandUsageCurrentFY
 FROM Invoice.InvoiceAccountBilling AS BILL
 INNER JOIN @billingTable T ON T.InvoiceBillingID = BILL.InvoiceBillingID
 WHERE BILL.SplitFY = 1;
 END;

 -- Lock the parent records in EC3 (for electronic billing only), to prevent edits
 IF (@isElectronicSummaryBilling = 1) AND EXISTS (SELECT 1 FROM @billingTable)
 BEGIN
 UPDATE EB
 SET EB.InvoiceTrackingSeqid	= @invoiceId,EB.VoucherBillingStatus = 'VC',EB.VoucherBillingStatusPeriod = @billingPeriod,EB.IsLockedForEdit = 'Y',EB.LastUpdate = GETDATE(),EB.AuthenticatedUserID = @userId
 FROM Billing.EC3_ManualBill_AccountManualBillingHeader_ElectronicBilling AS EB
 INNER JOIN Invoice.InvoiceAccountBilling AS ACCT ON ACCT.BillingID = EB.AccountManualBillingHeaderSeqid
 INNER JOIN @billingTable AS BILL ON BILL.InvoiceBillingID = ACCT.InvoiceBillingID
 END;
 END;

 --added on 11/28/2018 by XL for updating invoiceNum
 IF ISNULL(@summaryAcctNumber, '')<>''
 BEGIN
 UPDATE Invoice.Invoice
 SET	InvoiceNumber = @summaryAcctNumber + '_' + RIGHT(InvoiceNumber, 9), ModifiedDate = GETDATE(), ModifiedBy = @userId
 WHERE InvoiceID = @invoiceId; 

 SET @invoiceNumAction = ' - Invoice Number Updated';
 END;

 -- Create audit note
 INSERT INTO [Common].[Note](ParentType, ParentID, [Action], Note, CreatedBy)
 VALUES ('INVOICE', @invoiceId, 'ADD GROUP', 'Billing group (' + @noteFiller + ') added' + @invoiceNumAction, @userId);

 COMMIT TRAN @tranName;
	END TRY
	BEGIN CATCH
 IF (XACT_STATE() <> 0)
 BEGIN
 ROLLBACK TRAN @tranName;
 END;

 SELECT @exceptionDetails = ERROR_MESSAGE(), @severity = 'ERROR', @message = 'Failed to add invoice billing group and contract', @sendEmail = 1;
	END CATCH;

	SET @elapsedTime = DATEDIFF(MILLISECOND, GETDATE(), @startTime);

	EXEC dbo.USP_AppLogCreate @correlationId = @correlationId,@machineName = @machineName,@processName = @processName,@action = @action,@severity = @severity,@message = @message,@messageDetails = @messageDetails,@exceptionDetails = @exceptionDetails,@domain = @domain,@username = @username,@userId = @userId,@sendEmail = @sendEmail,@elapsedTime = @elapsedTime;
END;

CREATE PROCEDURE [Invoice].[USP_InvoiceGroupContractDelete]
(
	@invoiceGroupId AS INT,@comments AS VARCHAR(500) = NULL,@correlationId AS VARCHAR(128) = NULL,@machineName AS VARCHAR(128) = NULL,@processName AS VARCHAR(128) = NULL,@domain AS VARCHAR(25) = NULL,@username AS VARCHAR(25) = NULL,@userId AS INT = NULL,@exceptionDetails AS VARCHAR(MAX) = NULL OUTPUT
)
AS
BEGIN
	SET NOCOUNT ON;

	DECLARE @action AS VARCHAR(25), @severity AS VARCHAR(25), @message AS VARCHAR(255), @messageDetails AS VARCHAR(MAX),@startTime AS DATETIME, @elapsedTime AS INT, @sendEmail AS BIT, @invoiceId AS INT, @noteFiller AS VARCHAR(500),@groupId AS INT, @isElectronicSummaryBilling AS BIT = 0, @summaryAcctNumber AS VARCHAR(15), @invoiceNumAction AS VARCHAR(30) = '',@invoiceIsSplitFY AS BIT, @fiscalYear AS INT, @currentFY AS INT, @anotherFYInvoiceGroupId AS INT, @billingGroupContractID AS INT,@tranName AS VARCHAR(128) = CONVERT(VARCHAR(128), NEWID());

	SELECT @invoiceGroupId = NULLIF(@invoiceGroupId, 0), @comments = ISNULL(NULLIF(@comments, ''), 'Deleted invoice group');

	SELECT @startTime = GETDATE(), @correlationId = ISNULL(@correlationId, CONVERT(VARCHAR(128), NEWID())),@machineName = COALESCE(@machineName, @@SERVERNAME, 'NA'),@processName = COALESCE(@processName, QUOTENAME(OBJECT_SCHEMA_NAME(@@PROCID)) + '.' + QUOTENAME(OBJECT_NAME(@@PROCID)), 'NA'),@action = 'DELETE', @severity = 'INFO', @message = 'Deleted invoice group and contract',@messageDetails = CONVERT(VARCHAR(MAX), (SELECT @invoiceGroupId AS GRPID, @comments AS CMT FOR XML PATH('INPUTS'))),@sendEmail = 0, @userId = ISNULL(@userId, 0);

	SELECT @invoiceId = InvoiceID,@groupId = GroupID,@fiscalYear = CAST(FiscalYear AS INT),@billingGroupContractID = BillingGroupContractID
	FROM Invoice.vwInvoiceGroupContracts
	WHERE InvoiceGroupContractID = @invoiceGroupId;

	SELECT @invoiceIsSplitFY = Billing.UDF_IsSplitFYInvoice(InvoiceID),@currentFY = Billing.UDF_GetFiscalYear(CreatedDate)
	FROM Invoice.Invoice
	WHERE InvoiceID = @invoiceId;

	IF EXISTS (SELECT 1 FROM Billing.vwBillingGroups WHERE GroupID = @groupId AND BillingType = 'E')
	BEGIN
 SET @isElectronicSummaryBilling = 1;
	END;

	SELECT @summaryAcctNumber = SummaryAccountNumber
	FROM Billing.vwBillingGroups
	WHERE GroupID = @groupId;

	SELECT @noteFiller = (GroupName + ',' + FiscalYear + ',' + ContractNumber)
	FROM Invoice.vwInvoiceGroupContracts
	WHERE InvoiceGroupContractID = @invoiceGroupId;

	SET @noteFiller = ISNULL(@noteFiller, CONVERT(VARCHAR(10), @invoiceGroupId));

	BEGIN TRY
 BEGIN TRAN @tranName;

 IF (@invoiceIsSplitFY = 0)
 BEGIN
 -- Delete contract line allocations associated with the group
 DELETE ICLA
 FROM Invoice.InvoiceContractLineAllocation AS ICLA
 INNER JOIN Invoice.InvoiceContractLine AS ICL ON ICL.InvoiceContractLineID = ICLA.InvoiceContractLineID
 INNER JOIN Billing.ContractLine AS CL ON CL.ContractLineID = ICL.ContractLineID
 INNER JOIN Billing.vwBillingGroupContracts AS BGC ON BGC.ContractID = CL.ContractID AND BGC.FiscalYear = CL.FiscalYear
 INNER JOIN Invoice.InvoiceGroupContract AS IGC ON IGC.BillingGroupContractID = BGC.BillingGroupContractID
 WHERE IGC.InvoiceGroupContractID = @invoiceGroupId
 AND ICL.InvoiceID = IGC.InvoiceID;

 -- Delete contract lines associated with the group
 DELETE ICL
 FROM Invoice.InvoiceContractLine AS ICL
 INNER JOIN Billing.ContractLine AS CL ON CL.ContractLineID = ICL.ContractLineID
 INNER JOIN Billing.vwBillingGroupContracts AS BGC ON BGC.ContractID = CL.ContractID AND BGC.FiscalYear = CL.FiscalYear
 INNER JOIN Invoice.InvoiceGroupContract AS IGC ON IGC.BillingGroupContractID = BGC.BillingGroupContractID
 WHERE IGC.InvoiceGroupContractID = @invoiceGroupId
 AND ICL.InvoiceID = IGC.InvoiceID;

 -- Unlock associated bills in EC3 (for electronic billing only)
 IF (@isElectronicSummaryBilling = 1)
 BEGIN
 UPDATE EB
 SET EB.InvoiceTrackingSeqid = NULL,EB.VoucherBillingStatus = NULL,EB.VoucherBillingStatusPeriod = NULL,EB.IsLockedForEdit = 'N',EB.LastUpdate = GETDATE(),EB.AuthenticatedUserID = @userId
 FROM Billing.EC3_ManualBill_AccountManualBillingHeader_ElectronicBilling AS EB
 INNER JOIN Invoice.InvoiceAccountBilling AS ACCT ON ACCT.BillingID = EB.AccountManualBillingHeaderSeqid
 WHERE ACCT.InvoiceGroupContractID = @invoiceGroupId
 AND ACCT.BillingID NOT IN (SELECT DISTINCT BillingID FROM Invoice.InvoiceAccountBilling WHERE InvoiceID <> @invoiceId);
 END;

 -- Delete accounts associated with the group
 DELETE FROM Invoice.InvoiceAccountBilling WHERE InvoiceGroupContractID = @invoiceGroupId;
 END;
 ELSE --@fiscalYear = @priorFY
 BEGIN
 SELECT @anotherFYInvoiceGroupId = InvoiceGroupContractID
 FROM Invoice.vwInvoiceGroupContracts
 WHERE InvoiceID = @invoiceId AND groupid = @groupId AND InvoiceGroupContractID <> @invoiceGroupId;

 -- Delete contract line allocations associated with the group
 DELETE ICLA
 FROM Invoice.InvoiceContractLineAllocation AS ICLA
 INNER JOIN Invoice.InvoiceContractLine AS ICL ON ICL.InvoiceContractLineID = ICLA.InvoiceContractLineID
 WHERE ICL.InvoiceID = @invoiceId
 AND ICL.ContractID IN (SELECT ContractID
 FROM Billing.vwBillingGroupContracts
 WHERE GroupID = @groupId AND FiscalYear IN (CAST(@currentFY AS VARCHAR(4)), CAST(@currentFY-1 AS VARCHAR(4))));

 -- Delete contract lines associated with the group
 DELETE ICL
 FROM Invoice.InvoiceContractLine AS ICL
 WHERE ICL.InvoiceID = @invoiceId
 AND ICL.ContractID IN (SELECT ContractID
 FROM Billing.vwBillingGroupContracts
 WHERE GroupID = @groupId AND FiscalYear IN (CAST(@currentFY AS VARCHAR(4)), CAST(@currentFY-1 AS VARCHAR(4))));

 -- Unlock associated bills in EC3 (for electronic billing only)
 IF (@isElectronicSummaryBilling = 1)
 BEGIN
 UPDATE EB
 SET EB.InvoiceTrackingSeqid = NULL,EB.VoucherBillingStatus = NULL,EB.VoucherBillingStatusPeriod = NULL,EB.IsLockedForEdit = 'N',EB.LastUpdate = GETDATE(),EB.AuthenticatedUserID = @userId
 FROM Billing.EC3_ManualBill_AccountManualBillingHeader_ElectronicBilling AS EB
 INNER JOIN Invoice.InvoiceAccountBilling AS ACCT ON ACCT.BillingID = EB.AccountManualBillingHeaderSeqid
 WHERE ACCT.InvoiceGroupContractID IN (@invoiceGroupId, @anotherFYInvoiceGroupId)
 AND ACCT.BillingID NOT IN (SELECT DISTINCT BillingID FROM Invoice.InvoiceAccountBilling WHERE InvoiceID <> @invoiceId);
 END;

 -- Delete accounts associated with the groups
 IF (@fiscalYear = @currentFY)
 BEGIN
 DELETE FROM Invoice.InvoiceAccountBilling
 WHERE InvoiceID = @invoiceId AND GroupID = @groupId
 AND InvoiceGroupContractID IN (@invoiceGroupId, @anotherFYInvoiceGroupId)
 AND (FromDate < CAST(CAST(@fiscalYear-1 AS VARCHAR(4)) + '-06-30' AS DATETIME)
 AND ToDate > CAST(CAST(@fiscalYear-1 AS VARCHAR(4)) + '-06-30' AS DATETIME)
 OR FromDate> = CAST(CAST(@fiscalYear-1 AS VARCHAR(4)) + '-06-30' AS DATETIME));
 END;
 ELSE IF (@fiscalYear = @currentFY-1)
 BEGIN
 DELETE FROM Invoice.InvoiceAccountBilling
 WHERE InvoiceID = @invoiceId AND GroupID = @groupId
 AND InvoiceGroupContractID IN (@invoiceGroupId, @anotherFYInvoiceGroupId)
 AND (FromDate<CAST(CAST(@fiscalYear AS VARCHAR(4)) + '-06-30' AS DATETIME)
 AND ToDate>CAST(CAST(@fiscalYear AS VARCHAR(4)) + '-06-30' AS DATETIME)
 OR FromDate<CAST(CAST(@fiscalYear AS VARCHAR(4)) + '-06-30' AS DATETIME));
 END;
 END;

 -- Delete the group
 DELETE FROM Invoice.InvoiceGroupContract WHERE InvoiceGroupContractID = @invoiceGroupId;

 IF (ISNULL(@summaryAcctNumber, '') <> ''
 AND (NOT EXISTS(SELECT 1 
 FROM Invoice.InvoiceGroupContract AS a
 INNER JOIN Billing.BillingGroupContract AS b ON a.BillingGroupContractID = b.BillingGroupContractID
 INNER JOIN Billing.vwBillingGroups AS c ON b.GroupID = c.GroupID
 WHERE a.InvoiceID = @invoiceId AND ISNULL(c.SummaryAccountNumber, '') <> '')))
 BEGIN
 UPDATE Invoice.Invoice
 SET	InvoiceNumber = 'DEM_' + RIGHT(InvoiceNumber, 9),ModifiedDate = GETDATE(),ModifiedBy = @userId
 WHERE InvoiceID = @invoiceId; 

 SET @invoiceNumAction = ' - Invoice Number Updated';
 END;

 -- Create filler note
 INSERT INTO Common.Note(ParentType, ParentID, [Action], Note, CreatedBy)
 VALUES ('INVOICE', @invoiceId, 'REMOVE GROUP', 'Billing group (' + @noteFiller + ') removed' + @invoiceNumAction, @userId);

 -- Create audit note
 INSERT INTO Common.Note(ParentType, ParentID, [Action], Note, CreatedBy)
 VALUES ('INVOICE', @invoiceId, 'REMOVE GROUP', @comments, @userId);

 COMMIT TRAN @tranName;
	END TRY
	BEGIN CATCH
 IF (XACT_STATE() <> 0)
 BEGIN
 ROLLBACK TRAN @tranName;
 END;

 SELECT @exceptionDetails = ERROR_MESSAGE(), @severity = 'ERROR', @message = 'Failed to delete invoice group and contract', @sendEmail = 1;
	END CATCH;

	SET @elapsedTime = DATEDIFF(MILLISECOND, GETDATE(), @startTime);

	EXEC dbo.USP_AppLogCreate @correlationId = @correlationId,@machineName = @machineName,@processName = @processName,@action = @action,@severity = @severity,@message = @message,@messageDetails = @messageDetails,@exceptionDetails = @exceptionDetails,@domain = @domain,@username = @username,@userId = @userId,@sendEmail = @sendEmail,@elapsedTime = @elapsedTime;
END;

CREATE PROCEDURE [Invoice].[USP_InvoiceGroupContractRetrieve]
(
	@invoiceId AS INT = NULL,@correlationId AS VARCHAR(128) = NULL,@machineName AS VARCHAR(128) = NULL,@processName AS VARCHAR(128) = NULL,@domain AS VARCHAR(25) = NULL,@username AS VARCHAR(25) = NULL,@userId AS INT = NULL,@exceptionDetails AS VARCHAR(MAX) = NULL OUTPUT
)
AS
BEGIN
	SET NOCOUNT ON;

	SELECT @invoiceId = NULLIF(@invoiceId, 0), @userId = ISNULL(@userId, 0), @correlationId	= ISNULL(@correlationId, CONVERT(VARCHAR(128), NEWID())),@machineName = COALESCE(@machineName, @@SERVERNAME, 'NA'),@processName = COALESCE(@processName, QUOTENAME(OBJECT_SCHEMA_NAME(@@PROCID)) + '.' + QUOTENAME(OBJECT_NAME(@@PROCID)), 'NA');
	
	DECLARE @action AS VARCHAR(25) = 'RETRIEVE', @severity AS VARCHAR(25) = 'INFO', @message AS VARCHAR(255) = 'Invoice - Retrieved invoice groups / contracts',@messageDetails AS VARCHAR(MAX), @startTime AS DATETIME = GETDATE(), @elapsedTime AS INT, @sendEmail AS BIT = 0;
	
	SELECT @messageDetails = CONVERT(VARCHAR(MAX), (SELECT @invoiceId AS INVID FOR XML PATH('INPUTS')));
	
	BEGIN TRY
 SELECT IGC.InvoiceGroupContractID,IGC.InvoiceID,IGC.GroupID,IGC.GroupName,IGC.IsSummaryBilling,IGC.BillingType,IGC.AmountBilled,IGC.AmountPaid,IGC.FiscalYear,IGC.ContractNumber,IGC.ActualAmount,IGC.CloseAmount,IGC.OpenAmount,IGC.AvailableAmount,IGC.CreatedDate,IGC.CreatedBy,IGC.ModifiedDate,IGC.ModifiedBy,B.SummaryAccountNumber,B.GroupDescription,P.ProviderName
 FROM Invoice.vwInvoiceGroupContracts AS IGC
 INNER JOIN Billing.vwBillingGroups AS B ON IGC.GroupID = B.GroupID
 INNER JOIN Billing.vwProviders AS P ON B.ProviderID = P.ProviderID
 WHERE IGC.InvoiceID	= ISNULL(@invoiceId, IGC.InvoiceID);
	END TRY
	BEGIN CATCH
 SELECT @exceptionDetails = ERROR_MESSAGE(), @severity = 'ERROR', @message	= 'Invoice - Failed to retrieve invoice groups / contracts', @sendEmail	= 1;
	END CATCH;

	SET @elapsedTime = DATEDIFF(MILLISECOND, GETDATE(), @startTime);

	EXEC dbo.USP_AppLogCreate @correlationId = @correlationId,@machineName = @machineName,@processName = @processName,@action = @action,@severity = @severity,@message = @message,@messageDetails = @messageDetails,@exceptionDetails = @exceptionDetails,@domain = @domain,@username = @username,@userId = @userId,@sendEmail = @sendEmail,@elapsedTime = @elapsedTime;
END;

CREATE PROCEDURE [Invoice].[USP_InvoiceRetrieve]
	@invoiceId INT = NULL,@providerId INT = NULL,@billingPeriod VARCHAR(6) = NULL,@correlationId AS VARCHAR(128) = NULL,@machineName AS VARCHAR(128) = NULL,@processName AS VARCHAR(128) = NULL,@domain AS VARCHAR(25) = NULL,@username AS VARCHAR(25) = NULL,@userId AS INT = NULL,@exceptionDetails AS VARCHAR(MAX) = NULL OUTPUT
AS
BEGIN
	SET NOCOUNT ON;

	SET @invoiceId = NULLIF(@invoiceId, 0);
	SET @providerId = NULLIF(@providerId, 0);
	SET @billingPeriod = NULLIF(@billingPeriod, '');

	DECLARE @action VARCHAR(25), @severity VARCHAR(25), @message VARCHAR(255), @messageDetails VARCHAR(MAX), @startTime DATETIME, @elapsedTime INT, @sendEmail BIT;
	SET @startTime = GETDATE();
	SET @correlationId = ISNULL(@correlationId, CONVERT(VARCHAR(128), NEWID()));
	SET @machineName = COALESCE(@machineName, @@SERVERNAME, 'NA');
	SET @processName = COALESCE(@processName, QUOTENAME(OBJECT_SCHEMA_NAME(@@PROCID)) + '.' + QUOTENAME(OBJECT_NAME(@@PROCID)), 'NA');
	SET @action = 'RETRIEVE';
	SET @severity = 'INFO';
	SET @message = 'Retrieved invoices';
	SET @messageDetails = CONVERT(VARCHAR(MAX), (SELECT @invoiceId AS INVID, @providerId AS PRVID, @billingPeriod AS BILLPRD FOR XML PATH('INPUTS')));
	SET @sendEmail = 0;
	SET @userId = ISNULL(@userId, 0);

	BEGIN TRY
 SELECT INV.InvoiceID, INV.InvoiceNumber, INV.AmountBilled, INV.AmountPaid, INV.InvoiceBillingPeriod, INV.StatusID, INV.ProviderID, INV.ProviderName, INV.InvoiceGroupCount, INV.InvoiceDate, INV.CreatedDate, INV.CreatedBy, INV.ModifiedDate, INV.ModifiedBy, INV.Active
 FROM [Invoice].[vwInvoices] INV
 WHERE InvoiceID = ISNULL(@invoiceId, InvoiceID)
 AND ProviderID = ISNULL(@providerId, ProviderID)
 AND InvoiceBillingPeriod	= ISNULL(@billingPeriod, InvoiceBillingPeriod)
 ORDER BY CreatedDate DESC;
	END TRY
	BEGIN CATCH
 SELECT @exceptionDetails = ERROR_MESSAGE();
 SET @severity	= 'ERROR';
 SET @message	= 'Failed to retrieve invoices';
 SET @sendEmail	= 1;
	END CATCH;

	SET @elapsedTime = DATEDIFF(MILLISECOND, GETDATE(), @startTime);

	EXEC dbo.USP_AppLogCreate @correlationId = @correlationId,@machineName = @machineName,@processName = @processName,@action = @action,@severity = @severity,@message = @message,@messageDetails = @messageDetails,@exceptionDetails = @exceptionDetails,@domain = @domain,@username = @username,@userId = @userId,@sendEmail = @sendEmail,@elapsedTime = @elapsedTime;
END;

