CREATE PROCEDURE [Invoice].[USP_InvoiceUpdate]
	@invoiceId INT,@statusId INT,@comments VARCHAR(500) = NULL,@correlationId AS VARCHAR(128) = NULL,@machineName AS VARCHAR(128) = NULL,@processName AS VARCHAR(128) = NULL,@domain AS VARCHAR(25) = NULL,@username AS VARCHAR(25) = NULL,@userId AS INT = NULL,@exceptionDetails AS VARCHAR(MAX) = NULL OUTPUT
AS
BEGIN
	SET NOCOUNT ON;

	SET @invoiceId = NULLIF(@invoiceId, 0);
	SET @statusId = NULLIF(@statusId, 0);
	SET @comments = NULLIF(@comments, '');

	DECLARE @action VARCHAR(25), @severity VARCHAR(25), @message VARCHAR(255), @messageDetails VARCHAR(MAX), @startTime DATETIME, @elapsedTime INT, @sendEmail BIT, @oldStatusId INT;
	SET @startTime = GETDATE();	
	SET @correlationId = ISNULL(@correlationId, CONVERT(VARCHAR(128), NEWID()));
	SET @machineName = COALESCE(@machineName, @@SERVERNAME, 'NA');
	SET @processName = COALESCE(@processName, QUOTENAME(OBJECT_SCHEMA_NAME(@@PROCID)) + '.' + QUOTENAME(OBJECT_NAME(@@PROCID)), 'NA');
	SET @action = 'UPDATE';
	SET @severity = 'INFO';
	SET @message = 'Updated invoice';
	SET @messageDetails = CONVERT(VARCHAR(MAX), (SELECT @invoiceId AS PRVID, @statusId AS PRD, @comments AS CMT FOR XML PATH('INPUTS')));
	SET @sendEmail = 0;
	SET @userId = ISNULL(@userId, 0);

	DECLARE @tranName VARCHAR(128);
	SET @tranName = CONVERT(VARCHAR(128), NEWID());

	DECLARE @actionType VARCHAR(25);
	SET @actionType = CASE
 WHEN @statusId = 2 THEN 'SUBMITTED'
 WHEN @statusId = 3 THEN 'RETURNED'
 WHEN @statusId = 4 THEN 'APPROVED'
 WHEN @statusId = 5 THEN 'SENT to A&A'
 ELSE 'UPDATED'
 END;

	IF ISNULL(@comments, '') = ''
 SET @comments = CASE
 WHEN @actionType = 'SUBMITTED' THEN 'Invoice submitted for approval'
 WHEN @actionType = 'RETURNED' THEN 'Invoice returned for corrections'
 WHEN @actionType = 'APPROVED' THEN 'Invoice approved, sent for A&A review'
 ELSE 'Invoice updated'
 END;
	
	SELECT @oldStatusId = StatusID
	FROM invoice.Invoice
	WHERE InvoiceID=@invoiceId;

	BEGIN TRY 
 BEGIN TRAN @tranName;

 -- Update invoice header
 UPDATE [Invoice].[Invoice]
 SET StatusID = @statusId, InvoiceDate	= CASE WHEN @actionType = 'APPROVED' THEN GETDATE() ELSE InvoiceDate END, ModifiedDate	= GETDATE(), ModifiedBy	= @userId
 WHERE InvoiceID = @invoiceId;

 -- Create audit note
 INSERT INTO [Common].[Note] (ParentType, ParentID, [Action], Note, CreatedBy)
 VALUES ('INVOICE', @invoiceId, @actionType, @comments, @userId);

 COMMIT TRAN @tranName;

 -- Update close amount of contract and contract lines, added on 4/11/18
 IF (@statusId=3 AND @oldStatusId=4) OR @statusId=4
 BEGIN
 UPDATE [Billing].ContractLine
 SET	CloseAmount = [Billing].[UDF_GetContractLineAmountPaid](a.ContractLineID, GETDATE()), OpenAmount = a.ActualAmount - [Billing].[UDF_GetContractLineAmountPaid](a.ContractLineID, GETDATE()), ModifiedDate = GETDATE(), ModifiedBy = @userId
 FROM Billing.ContractLine a
 WHERE a.ContractLineID IN 
 (
 SELECT DISTINCT ContractLineID
 FROM invoice.InvoiceContractLine
 WHERE invoiceid = @invoiceId
 );

 UPDATE [Billing].Contract
 SET	CloseAmount = ISNULL(b.CloseAmount, 0),OpenAmount = ISNULL(b.OpenAmount, 0),ModifiedDate = GETDATE(),ModifiedBy = @userId
 FROM [Billing].Contract a JOIN
 (
 SELECT ContractID, SUM(ISNULL(CloseAmount, 0)) AS CloseAmount, SUM(ISNULL(OpenAmount, 0)) AS OpenAmount
 FROM [Billing].ContractLine 
 WHERE ContractID IN 
 (
 SELECT DISTINCT contractid
 FROM invoice.InvoiceContractLine
 WHERE invoiceid = @invoiceId
 )
 GROUP BY ContractID
 ) b ON a.ContractID=b.contractid;
 
 END;

 -- insert payments into history table
 DECLARE @dte DATETIME
 
 IF @actionType = 'APPROVED'
 BEGIN
 SET @dte = getdate();
 INSERT INTO Billing.HistoricalContractLine
 (
 TransactionType,
 ContractLineID,
 ActualAmount,
 StartDate,
 EndDate,
 ModifiedBy,
 ChangeAmount,
 InvoiceId
 )
 --values(2,0,0,@dte,null,@username,0,@invoiceId)
 SELECT 2,
 c.ContractLineID,
 0,
 @dte,
 NULL,
 @username,
 -SUM(c.AmountPaid),
 @invoiceId
 FROM Invoice.InvoiceGroupContract a
 JOIN Billing.BillingGroupContract b ON a.BillingGroupContractID = b.BillingGroupContractID
 JOIN Invoice.InvoiceContractLine c ON a.InvoiceID=c.InvoiceID AND b.ContractID = c.ContractID
 WHERE a.InvoiceID = @invoiceId
 GROUP BY c.ContractLineID;


 UPDATE Billing.HistoricalContractLine
 SET EndDate = GETDATE()
 WHERE StartDate < @dte AND invoiceId = @invoiceId AND EndDate IS NULL AND TransactionType = 2
 END

 IF (@statusId=3 AND @oldStatusId=4) --@actionType = 'RETURNED' [changed by Xin on 08/16/2019]
 BEGIN
 INSERT INTO Billing.HistoricalContractLine
 (
 TransactionType,
 ContractLineID,
 ActualAmount,
 StartDate,
 EndDate,
 ModifiedBy,
 ChangeAmount,
 InvoiceId
 )
 SELECT 3,
 c.ContractLineID,
 0,
 GETDATE(),
 NULL,
 @username,
 SUM(c.AmountPaid),
 @invoiceId
 FROM Invoice.InvoiceGroupContract a
 JOIN Billing.BillingGroupContract b ON a.BillingGroupContractID = b.BillingGroupContractID
 JOIN Invoice.InvoiceContractLine c ON a.InvoiceID=c.InvoiceID AND b.ContractID = c.ContractID AND b.FiscalYear=c.FiscalYear
 WHERE a.InvoiceID = @invoiceId
 GROUP BY c.ContractLineID;
 END;
	END TRY
	BEGIN CATCH
 IF XACT_STATE() <> 0 ROLLBACK TRAN @tranName;

 SELECT @exceptionDetails = ERROR_MESSAGE();
 SET @severity	= 'ERROR';
 SET @message	= 'Failed to update invoice';
 SET @sendEmail	= 1;
	END CATCH;

	SET @elapsedTime = DATEDIFF(MILLISECOND, GETDATE(), @startTime);

	EXEC dbo.USP_AppLogCreate @correlationId = @correlationId,@machineName = @machineName,@processName = @processName,@action = @action,@severity = @severity,@message = @message,@messageDetails = @messageDetails,@exceptionDetails = @exceptionDetails,@domain = @domain,@username = @username,@userId = @userId,@sendEmail = @sendEmail,@elapsedTime = @elapsedTime;
END;

CREATE PROCEDURE [Invoice].[USP_InvoiceWorkQueueRetrieve]
	@providerId INT = NULL,@billingPeriod VARCHAR(6) = NULL,@pageNumber INT = NULL,@pageSize INT = NULL,@sortBy VARCHAR(25) = NULL,@sortDirection VARCHAR(5) = NULL,@searchValue VARCHAR(25) = NULL,@correlationId AS VARCHAR(128) = NULL,@machineName AS VARCHAR(128) = NULL,@processName AS VARCHAR(128) = NULL,@domain AS VARCHAR(25) = NULL,@username AS VARCHAR(25) = NULL,@userId AS INT = NULL,@exceptionDetails AS VARCHAR(MAX) = NULL OUTPUT
AS
BEGIN
	SET NOCOUNT ON;

	SET @providerId = NULLIF(@providerId, 0);
	SET @billingPeriod = NULLIF(@billingPeriod, '');
	SET @pageNumber = ISNULL(NULLIF(@pageNumber, 0), 1);
	SET @pageSize = ISNULL(NULLIF(@pageSize, 0), 1000);
	SET @sortBy = ISNULL(NULLIF(@sortBy, ''), 'INVOICENO');
	SET @sortDirection = ISNULL(NULLIF(@sortDirection, ''), 'DESC');
	SET @searchValue = NULLIF(@searchValue, '');

	DECLARE @action VARCHAR(25), @severity VARCHAR(25), @message VARCHAR(255), @messageDetails VARCHAR(MAX), @startTime DATETIME, @elapsedTime INT, @sendEmail BIT;
	SET @startTime = GETDATE();
	SET @correlationId = ISNULL(@correlationId, CONVERT(VARCHAR(128), NEWID()));
	SET @machineName = COALESCE(@machineName, @@SERVERNAME, 'NA');
	SET @processName = COALESCE(@processName, QUOTENAME(OBJECT_SCHEMA_NAME(@@PROCID)) + '.' + QUOTENAME(OBJECT_NAME(@@PROCID)), 'NA');
	SET @action = 'RETRIEVE';
	SET @severity = 'INFO';
	SET @message = 'Retrieved invoices';
	SET @messageDetails = CONVERT(VARCHAR(MAX), (SELECT @providerId AS PRVID, @billingPeriod AS BILLPRD, @pageNumber AS PAGENUM, @pageSize AS PAGESIZE
 FOR XML PATH('INPUTS')));
	SET @sendEmail = 0;
	SET @userId = ISNULL(@userId, 0);

	DECLARE @startRowIndex INT, @endRowIndex INT;
	SET @startRowIndex	= ((@pageNumber - 1) * @pageSize) + 1;
	SET @endRowIndex	= @pageNumber * @pageSize;
	IF @endRowIndex <= 0 SET @endRowIndex = 10000; -- Hard-coding for 10,000 rows of data

	BEGIN TRY
 DECLARE @tmpInvoices TABLE (
 InvoiceID INT NOT NULL, BillingGroups VARCHAR(1000) NULL, Contracts VARCHAR(1000) NULL, Payments VARCHAR(1000) NULL
 );

 INSERT INTO @tmpInvoices(InvoiceID, BillingGroups, Contracts, Payments)
 SELECT INV.InvoiceID, SUBSTRING((SELECT DISTINCT ', ' + IGC.GroupName FROM [Invoice].[vwInvoiceGroupContracts] IGC WHERE IGC.InvoiceID = INV.InvoiceID FOR XML PATH('')), 2, 1000) AS BillingGroups, SUBSTRING((SELECT DISTINCT ', ' + IGC.ContractNumber FROM [Invoice].[vwInvoiceGroupContracts] IGC WHERE IGC.InvoiceID = INV.InvoiceID FOR XML PATH('')), 2, 1000) AS Contracts, SUBSTRING((SELECT DISTINCT ', ' + IP.PaymentNumber FROM [Invoice].[vwInvoicePayments] IP WHERE IP.InvoiceID = INV.InvoiceID FOR XML PATH('')), 2, 1000) AS Payments
 FROM [Invoice].[vwInvoices] INV;

 WITH invoicesCTE AS (
 SELECT CASE
 WHEN @sortBy = 'INVOICENO' AND @sortDirection = 'ASC' THEN ROW_NUMBER() OVER (ORDER BY INV.InvoiceBillingPeriod ASC, RIGHT(INV.InvoiceNumber, 3) ASC)
 WHEN @sortBy = 'INVOICENO' AND @sortDirection = 'DESC' THEN ROW_NUMBER() OVER (ORDER BY INV.InvoiceBillingPeriod DESC, RIGHT(INV.InvoiceNumber, 3) DESC)
 WHEN @sortBy = 'INVOICEDATE' AND @sortDirection = 'ASC' THEN ROW_NUMBER() OVER (ORDER BY INV.InvoiceDate ASC)
 WHEN @sortBy = 'INVOICEDATE' AND @sortDirection = 'DESC' THEN ROW_NUMBER() OVER (ORDER BY INV.InvoiceDate DESC)
 WHEN @sortBy = 'PROVIDER' AND @sortDirection = 'ASC' THEN ROW_NUMBER() OVER (ORDER BY INV.ProviderName ASC)
 WHEN @sortBy = 'PROVIDER' AND @sortDirection = 'DESC' THEN ROW_NUMBER() OVER (ORDER BY INV.ProviderName DESC)
 WHEN @sortBy = 'BILLINGGROUP' AND @sortDirection = 'ASC' THEN ROW_NUMBER() OVER (ORDER BY T.BillingGroups ASC)
 WHEN @sortBy = 'BILLINGGROUP' AND @sortDirection = 'DESC' THEN ROW_NUMBER() OVER (ORDER BY T.BillingGroups DESC)
 WHEN @sortBy = 'CONTRACT' AND @sortDirection = 'ASC' THEN ROW_NUMBER() OVER (ORDER BY T.Contracts ASC)
 WHEN @sortBy = 'CONTRACT' AND @sortDirection = 'DESC' THEN ROW_NUMBER() OVER (ORDER BY T.Contracts DESC)
 WHEN @sortBy = 'PAYMENT' AND @sortDirection = 'ASC' THEN ROW_NUMBER() OVER (ORDER BY T.Payments ASC)
 WHEN @sortBy = 'PAYMENT' AND @sortDirection = 'DESC' THEN ROW_NUMBER() OVER (ORDER BY T.Payments DESC)
 WHEN @sortBy = 'INVOICEAMOUNT' AND @sortDirection = 'ASC' THEN ROW_NUMBER() OVER (ORDER BY INV.AmountPaid ASC)
 WHEN @sortBy = 'INVOICEAMOUNT' AND @sortDirection = 'DESC' THEN ROW_NUMBER() OVER (ORDER BY INV.AmountPaid DESC)
 WHEN @sortBy = 'BILLINGPERIOD' AND @sortDirection = 'ASC' THEN ROW_NUMBER() OVER (ORDER BY INV.InvoiceBillingPeriod ASC)
 WHEN @sortBy = 'BILLINGPERIOD' AND @sortDirection = 'DESC' THEN ROW_NUMBER() OVER (ORDER BY INV.InvoiceBillingPeriod DESC)
 WHEN @sortBy = 'STATUS' AND @sortDirection = 'ASC' THEN ROW_NUMBER() OVER (ORDER BY INV.StatusDescription ASC)
 WHEN @sortBy = 'STATUS' AND @sortDirection = 'DESC' THEN ROW_NUMBER() OVER (ORDER BY INV.StatusDescription DESC)
 ELSE ROW_NUMBER() OVER (ORDER BY INV.InvoiceBillingPeriod DESC, RIGHT(INV.InvoiceNumber, 3) DESC)
 END AS RowID, INV.InvoiceID, INV.InvoiceNumber, INV.AmountBilled, INV.AmountPaid, INV.InvoiceBillingPeriod, INV.StatusID, INV.ProviderID, INV.ProviderName, T.BillingGroups, T.Contracts, T.Payments, INV.InvoiceDate, INV.CreatedDate, INV.CreatedBy, INV.ModifiedDate, INV.ModifiedBy, INV.Active
 FROM [Invoice].[vwInvoices] INV
 JOIN @tmpInvoices T ON T.InvoiceID = INV.InvoiceID
 WHERE Active = 1
 AND INV.ProviderID = ISNULL(@providerId, ProviderID)
 AND INV.InvoiceBillingPeriod	= ISNULL(@billingPeriod, InvoiceBillingPeriod)
 
 AND 1 = CASE
 WHEN ISNULL(@searchValue, '') <> '' THEN
 CASE
 WHEN INV.InvoiceNumber LIKE '%' + @searchValue + '%'
 OR T.BillingGroups LIKE '%' + @searchValue + '%'
 OR T.Contracts LIKE '%' + @searchValue + '%'
 OR T.Payments LIKE '%' + @searchValue + '%'
 THEN 1
 ELSE 0
 END
 ELSE 1
 END
 )
 SELECT INV.InvoiceID, INV.InvoiceNumber, INV.AmountBilled, INV.AmountPaid, INV.InvoiceBillingPeriod, INV.StatusID, INV.ProviderID, INV.ProviderName, INV.BillingGroups, INV.Contracts, INV.Payments, INV.InvoiceDate, INV.CreatedDate, INV.CreatedBy, INV.ModifiedDate, INV.ModifiedBy, INV.Active, CONVERT(INT, (SELECT COUNT(1) FROM Invoice.vwInvoices WHERE Active = 1)) AS TotalRecords, CONVERT(INT, (SELECT COUNT(1) FROM invoicesCTE)) AS FilteredRecords
 FROM invoicesCTE INV
 JOIN @tmpInvoices T ON T.InvoiceID = INV.InvoiceID
 WHERE (RowID BETWEEN @startRowIndex AND @endRowIndex)
 AND ((@billingPeriod IS NOT NULL AND INV.InvoiceBillingPeriod = @billingPeriod) OR (@billingPeriod IS NULL))
 ORDER BY RowID;
	END TRY
	BEGIN CATCH
 SELECT @exceptionDetails = ERROR_MESSAGE();
 SET @severity	= 'ERROR';
 SET @message	= 'Failed to retrieve invoices';
 SET @sendEmail	= 1;
	END CATCH;

	SET @elapsedTime = DATEDIFF(MILLISECOND, GETDATE(), @startTime);

	EXEC dbo.USP_AppLogCreate @correlationId = @correlationId,@machineName = @machineName,@processName = @processName,@action = @action,@severity = @severity,@message = @message,@messageDetails = @messageDetails,@exceptionDetails = @exceptionDetails,@domain = @domain,@username = @username,@userId = @userId,@sendEmail = @sendEmail,@elapsedTime = @elapsedTime;
END;

CREATE PROCEDURE [Report].[USP_InvoiceAccountBillingRetrieve]
(
	@invoiceId AS INT = NULL,@invoiceNumber AS VARCHAR(25) = NULL,@correlationId AS VARCHAR(128) = NULL,@machineName AS VARCHAR(128) = NULL,@processName AS VARCHAR(128) = NULL,@domain AS VARCHAR(25) = NULL,@username AS VARCHAR(25) = NULL,@userId AS INT = NULL,@exceptionDetails AS VARCHAR(MAX) = NULL OUTPUT
)
AS
BEGIN
	SET NOCOUNT ON;
	SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;

	SELECT @invoiceId = NULLIF(@invoiceId, 0), @invoiceNumber = NULLIF(@invoiceNumber, ''), @userId = ISNULL(@userId, 0),@correlationId = ISNULL(@correlationId, CONVERT(VARCHAR(128), NEWID())), @machineName = COALESCE(@machineName, @@SERVERNAME, 'NA'),@processName = COALESCE(@processName, QUOTENAME(OBJECT_SCHEMA_NAME(@@PROCID)) + '.' + QUOTENAME(OBJECT_NAME(@@PROCID)), 'NA');

	IF (ISNULL(@invoiceId, 0) <= 0 AND ISNULL(@invoiceNumber, '') = '')
	BEGIN
 RAISERROR (N'Either the invoice identifier or the invoice number must be specified', 16, 1);
	END;

	DECLARE @action AS VARCHAR(25) = 'RETRIEVE', @severity AS VARCHAR(25) = 'INFO',@message AS VARCHAR(255) = 'Reports - Retrieved invoice accounts / billings', @messageDetails AS VARCHAR(MAX),@startTime AS DATETIME = GETDATE(), @elapsedTime AS INT, @sendEmail AS BIT = 0;
	
	SELECT @messageDetails = CONVERT(VARCHAR(MAX), (SELECT @invoiceId AS INVID, @invoiceNumber AS INVNUM FOR XML PATH('INPUTS')));
	
	BEGIN TRY
 DECLARE @invoiceIsSplitFY AS BIT = Billing.UDF_IsSplitFYInvoice(@invoiceId);

 IF (@invoiceIsSplitFY = 0)
 BEGIN
 SELECT InvoiceBillingID,InvoiceGroupContractID,InvoiceID,InvoiceNumber,GroupID,GroupName,ContractID,ContractNumber,FiscalYear,AccountID,Billing.MaskAccountNumber(CASE WHEN ProviderID = 2 AND LEN(AccountNumber) = 15 AND AccountNumber LIKE '%0000' THEN SUBSTRING(AccountNumber, 2, 10)
 WHEN ProviderID = 7 AND LEN(AccountNumber) = 15 AND AccountNumber LIKE '%0000' THEN SUBSTRING(AccountNumber, 0, 12)
 WHEN ProviderID = 9 AND LEN(AccountNumber) = 15 AND AccountNumber LIKE '%0000' THEN SUBSTRING(AccountNumber, 0, 12)
 ELSE AccountNumber END, ProviderID) AS AccountNumber,FormattedAccountNumber,BillingID,BillingPeriod,BillingPeriodDays,BillingPeriodDaysPriorFY,BillingPeriodDaysCurrentFY,IsEstimated,IsManualEntry,AmountBilled,AmountPaid,AmountPaidPriorFY,AmountPaidCurrentFY,EnergyUsage,EnergyUsagePriorFY,EnergyUsageCurrentFY,DemandUsage,DemandUsagePriorFY,DemandUsageCurrentFY,DateBilled,DateDue,FromDate,ToDate,SplitFY,CreatedDate,CreatedBy,ModifiedDate,ModifiedBy,Active
 FROM Invoice.vwInvoiceAccountBillings
 WHERE InvoiceID	= ISNULL(@invoiceId, InvoiceID)
 AND InvoiceNumber = ISNULL(@invoiceNumber, InvoiceNumber)
 ORDER BY GroupName ASC, ContractNumber ASC, FiscalYear DESC, AccountNumber ASC;
 END;
 ELSE
 BEGIN
 WITH fyTbl AS (SELECT GroupID, ContractNumber = STUFF((SELECT DISTINCT ' & ' + ContractNumber
 FROM Invoice.vwInvoiceGroupContracts AS b
 WHERE a.InvoiceID = b.InvoiceID AND b.GroupID = a.GroupID
 FOR XML PATH('')), 1, 7, '')
 FROM Invoice.vwInvoiceGroupContracts AS a
 WHERE InvoiceID	= ISNULL(@invoiceId, a.InvoiceID)
 AND InvoiceNumber = ISNULL(@invoiceNumber, a.InvoiceNumber)
 GROUP BY InvoiceID, GroupID)
 SELECT IAB.InvoiceBillingID,IAB.InvoiceGroupContractID,IAB.InvoiceID,IAB.InvoiceNumber,IAB.GroupID,IAB.GroupName,IAB.ContractID, REPLACE(fyTbl.ContractNumber, '&amp;', '&') AS ContractNumber,IAB.FiscalYear,IAB.AccountID,Billing.MaskAccountNumber(CASE WHEN IAB.ProviderID = 2 AND LEN(IAB.AccountNumber) = 15 AND IAB.AccountNumber LIKE '%0000' THEN SUBSTRING(IAB.AccountNumber, 2, 10)
 WHEN IAB.ProviderID = 7 AND LEN(IAB.AccountNumber) = 15 AND IAB.AccountNumber LIKE '%0000' THEN SUBSTRING(IAB.AccountNumber, 0, 12)
 WHEN IAB.ProviderID = 9 AND LEN(IAB.AccountNumber) = 15 AND IAB.AccountNumber LIKE '%0000' THEN SUBSTRING(IAB.AccountNumber, 0, 12)
 ELSE IAB.AccountNumber END, IAB.ProviderID) AS AccountNumber,IAB.FormattedAccountNumber,IAB.BillingID,IAB.BillingPeriod,IAB.BillingPeriodDays,IAB.BillingPeriodDaysPriorFY,IAB.BillingPeriodDaysCurrentFY,IAB.IsEstimated,IAB.IsManualEntry,IAB.AmountBilled,IAB.AmountPaid,IAB.AmountPaidPriorFY,IAB.AmountPaidCurrentFY,IAB.EnergyUsage,IAB.EnergyUsagePriorFY,IAB.EnergyUsageCurrentFY,IAB.DemandUsage,IAB.DemandUsagePriorFY,IAB.DemandUsageCurrentFY,IAB.DateBilled,IAB.DateDue,IAB.FromDate,IAB.ToDate,IAB.SplitFY,IAB.CreatedDate,IAB.CreatedBy,IAB.ModifiedDate,IAB.ModifiedBy,IAB.Active
 FROM Invoice.vwInvoiceAccountBillings AS IAB
 INNER JOIN fyTbl ON IAB.GroupID = fyTbl.GroupID
 WHERE IAB.InvoiceID = ISNULL(@invoiceId, IAB.InvoiceID)
 AND IAB.InvoiceNumber = ISNULL(@invoiceNumber, IAB.InvoiceNumber)
 ORDER BY IAB.GroupName ASC, IAB.ContractNumber ASC, IAB.FiscalYear DESC, IAB.AccountNumber ASC;
 END;
	END TRY
	BEGIN CATCH
 SELECT @exceptionDetails = ERROR_MESSAGE(), @severity = 'ERROR', @message = 'Reports - Failed to retrieve invoice accounts / billings',@sendEmail = 1;
	END CATCH;

	SET @elapsedTime = DATEDIFF(MILLISECOND, GETDATE(), @startTime);

	EXEC dbo.USP_AppLogCreate @correlationId = @correlationId,@machineName = @machineName,@processName = @processName,@action = @action,@severity = @severity,@message = @message,@messageDetails = @messageDetails,@exceptionDetails = @exceptionDetails,@domain = @domain,@username = @username,@userId = @userId,@sendEmail = @sendEmail,@elapsedTime = @elapsedTime;
END;

CREATE PROCEDURE [Report].[USP_InvoiceAccountBillingRetrieve_AandA]
(
	@invoiceId AS INT = NULL,@invoiceNumber AS VARCHAR(25) = NULL,@correlationId AS VARCHAR(128) = NULL,@machineName AS VARCHAR(128) = NULL,@processName AS VARCHAR(128) = NULL,@domain AS VARCHAR(25) = NULL,@username AS VARCHAR(25) = NULL,@userId AS INT = NULL,@exceptionDetails AS VARCHAR(MAX) = NULL OUTPUT
)
AS
BEGIN
	SET NOCOUNT ON;
	SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;

	SELECT @invoiceId = NULLIF(@invoiceId, 0), @invoiceNumber = NULLIF(@invoiceNumber, ''), @userId = ISNULL(@userId, 0),@correlationId = ISNULL(@correlationId, CONVERT(VARCHAR(128), NEWID())), @machineName = COALESCE(@machineName, @@SERVERNAME, 'NA'),@processName = COALESCE(@processName, QUOTENAME(OBJECT_SCHEMA_NAME(@@PROCID)) + '.' + QUOTENAME(OBJECT_NAME(@@PROCID)), 'NA');

	IF (ISNULL(@invoiceId, 0) <= 0 AND ISNULL(@invoiceNumber, '') = '')
	BEGIN
 RAISERROR (N'Either the invoice identifier or the invoice number must be specified', 16, 1);
	END;

	DECLARE @action AS VARCHAR(25) = 'RETRIEVE', @severity AS VARCHAR(25) = 'INFO',@message AS VARCHAR(255) = 'Reports - Retrieved invoice accounts / billings', @messageDetails AS VARCHAR(MAX),@startTime AS DATETIME = GETDATE(), @elapsedTime AS INT, @sendEmail AS BIT = 0;
	
	SELECT @messageDetails = CONVERT(VARCHAR(MAX), (SELECT @invoiceId AS INVID, @invoiceNumber AS INVNUM FOR XML PATH('INPUTS')));
	
	BEGIN TRY
 DECLARE @invoiceIsSplitFY AS BIT = Billing.UDF_IsSplitFYInvoice(@invoiceId);

 IF (@invoiceIsSplitFY = 0)
 BEGIN
 SELECT FormattedAccountNumberForEFT AS [Account Number],BillingPeriod AS [Billing Period],FORMAT(FromDate, 'M/d/yyyy') + '-' + FORMAT(ToDate, 'M/d/yyyy') AS [From-To Date],EnergyUsage AS [Energy Usage],FORMAT(AmountBilled, 'C') AS [Billed Amount],FORMAT(AmountPaid, 'C') AS [Payment Amount],FORMAT(AmountPaidPriorFY, 'C') AS [Prior FY],FORMAT(AmountPaidCurrentFY, 'C') AS [Current FY],GroupName,ContractNumber
 FROM Invoice.vwInvoiceAccountBillings
 WHERE InvoiceID	= ISNULL(@invoiceId, InvoiceID)
 AND InvoiceNumber = ISNULL(@invoiceNumber, InvoiceNumber)
 ORDER BY GroupName ASC, ContractNumber ASC, FiscalYear DESC, AccountNumber ASC;
 END;
 ELSE
 BEGIN
 WITH fyTbl AS (SELECT GroupID, ContractNumber = STUFF((SELECT DISTINCT ' & ' + ContractNumber
 FROM Invoice.vwInvoiceGroupContracts AS b
 WHERE a.InvoiceID = b.InvoiceID AND b.GroupID = a.GroupID
 FOR XML PATH('')), 1, 7, '')
 FROM Invoice.vwInvoiceGroupContracts AS a
 WHERE InvoiceID	= ISNULL(@invoiceId, a.InvoiceID)
 AND InvoiceNumber = ISNULL(@invoiceNumber, a.InvoiceNumber)
 GROUP BY InvoiceID, GroupID)
 SELECT IAB.FormattedAccountNumberForEFT AS [Account Number],IAB.BillingPeriod AS [Billing Period],FORMAT(IAB.FromDate, 'M/d/yyyy') + '-' + FORMAT(IAB.ToDate, 'M/d/yyyy') AS [From-To Date],IAB.EnergyUsage AS [Energy Usage],FORMAT(IAB.AmountBilled, 'C') AS [Billed Amount],FORMAT(IAB.AmountPaid, 'C') AS [Payment Amount],FORMAT(IAB.AmountPaidPriorFY, 'C') AS [Prior FY],FORMAT(IAB.AmountPaidCurrentFY, 'C') AS [Current FY],IAB.GroupName,IAB.ContractNumber
 FROM Invoice.vwInvoiceAccountBillings AS IAB
 INNER JOIN fyTbl ON IAB.GroupID = fyTbl.GroupID
 WHERE IAB.InvoiceID = ISNULL(@invoiceId, IAB.InvoiceID)
 AND IAB.InvoiceNumber = ISNULL(@invoiceNumber, IAB.InvoiceNumber)
 ORDER BY IAB.GroupName ASC, IAB.ContractNumber ASC, IAB.FiscalYear DESC, IAB.AccountNumber ASC;
 END;
	END TRY
	BEGIN CATCH
 SELECT @exceptionDetails = ERROR_MESSAGE(), @severity = 'ERROR', @message = 'Reports - Failed to retrieve invoice accounts / billings',@sendEmail = 1;
