CREATE PROCEDURE [Report].[USP_InvoiceContractLineRetrieve_AandA]
(
	@invoiceId AS INT = NULL,@invoiceNumber AS VARCHAR(25) = NULL,@correlationId AS VARCHAR(128) = NULL,@machineName AS VARCHAR(128) = NULL,@processName AS VARCHAR(128) = NULL,@domain AS VARCHAR(25) = NULL,@username AS VARCHAR(25) = NULL,@userId AS INT = NULL,@exceptionDetails AS VARCHAR(MAX) = NULL OUTPUT
)
AS
BEGIN
	SET NOCOUNT ON;
	SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;

	SELECT @invoiceId = NULLIF(@invoiceId, 0), @invoiceNumber = NULLIF(@invoiceNumber, ''), @userId	= ISNULL(@userId, 0),@correlationId = ISNULL(@correlationId, CONVERT(VARCHAR(128), NEWID())), @machineName = COALESCE(@machineName, @@SERVERNAME, 'NA'),@processName = COALESCE(@processName, QUOTENAME(OBJECT_SCHEMA_NAME(@@PROCID)) + '.' + QUOTENAME(OBJECT_NAME(@@PROCID)), 'NA');

	IF (ISNULL(@invoiceId, 0) <= 0 AND ISNULL(@invoiceNumber, '') = '')
	BEGIN
 RAISERROR (N'Either the invoice identifier or the invoice number must be specified', 16, 1);
	END;

	DECLARE @action AS VARCHAR(25) = 'RETRIEVE', @severity AS VARCHAR(25) = 'INFO', @message AS VARCHAR(255) = 'Reports - Retrieved invoice contract lines',@messageDetails AS VARCHAR(MAX), @startTime AS DATETIME = GETDATE(), @elapsedTime AS INT, @sendEmail AS BIT = 0;
	
	SELECT @messageDetails = CONVERT(VARCHAR(MAX), (SELECT @invoiceId AS INVID, @invoiceNumber AS INVNUM FOR XML PATH('INPUTS')));

	BEGIN TRY
 DECLARE @invoiceIsSplitFY AS BIT = Billing.UDF_IsSplitFYInvoice(@invoiceId);

 IF (@invoiceIsSplitFY = 0)
 BEGIN
 SELECT CommodityLine AS [Commodity Line],AccountingLine AS [Accounting Line],UoA AS [U/A],BudgetCode AS [Budget Code],ObjectCode AS [Object Code],FORMAT(AmountPaid, 'C') AS [Payment Amount]
 FROM Invoice.vwInvoiceContractLines
 WHERE InvoiceID	= ISNULL(@invoiceId, InvoiceID)
 AND InvoiceNumber = ISNULL(@invoiceNumber, InvoiceNumber)
 ORDER BY ContractNumber ASC,FiscalYear DESC,CommodityLine ASC,AccountingLine ASC;
 END;
 ELSE
 BEGIN
 WITH tmpTbl AS(SELECT InvoiceContractLineID,InvoiceID,InvoiceNumber,ContractID,ContractNumber,ContractLineID,FiscalYear,CommodityLine,AccountingLine,BudgetCode,ObjectCode,UoA,AmountPaid,CreatedDate,CreatedBy,ModifiedDate,ModifiedBy,Active
 FROM Invoice.vwInvoiceContractLines
 WHERE InvoiceID = ISNULL(@invoiceId, InvoiceID)
 AND InvoiceNumber	= ISNULL(@invoiceNumber, InvoiceNumber)),fyTbl AS(SELECT a.ContractID,FiscalYear = STUFF((SELECT DISTINCT ', ' + b.FiscalYear
 FROM tmpTbl AS b
 WHERE b.ContractID = a.ContractID
 FOR XML PATH('')), 1, 2, '')
 FROM tmpTbl AS a
 GROUP BY a.ContractID)
 SELECT T.CommodityLine AS [Commodity Line],T.AccountingLine AS [Accounting Line],T.UoA AS [U/A],T.BudgetCode AS [Budget Code],T.ObjectCode AS [Object Code],FORMAT(T.AmountPaid, 'C') AS [Payment Amount]
 FROM tmpTbl AS T
 INNER JOIN fyTbl AS F ON F.ContractID = T.ContractID
 ORDER BY T.ContractNumber ASC,T.FiscalYear DESC,T.CommodityLine ASC,T.AccountingLine ASC;
 END;
	END TRY
	BEGIN CATCH
 SELECT @exceptionDetails = ERROR_MESSAGE(), @severity = 'ERROR', @message = 'Reports - Failed to retrieve invoice contract lines', @sendEmail = 1;
	END CATCH;

	SET @elapsedTime = DATEDIFF(MILLISECOND, GETDATE(), @startTime);

	EXEC dbo.USP_AppLogCreate @correlationId = @correlationId,@machineName = @machineName,@processName = @processName,@action = @action,@severity = @severity,@message = @message,@messageDetails = @messageDetails,@exceptionDetails = @exceptionDetails,@domain = @domain,@username = @username,@userId = @userId,@sendEmail = @sendEmail,@elapsedTime = @elapsedTime;
END;

CREATE PROCEDURE [Report].[USP_InvoiceGroupContractRetrieve]
(
	@invoiceId AS INT = NULL,@invoiceNumber AS VARCHAR(25) = NULL,@correlationId AS VARCHAR(128) = NULL,@machineName AS VARCHAR(128) = NULL,@processName AS VARCHAR(128) = NULL,@domain AS VARCHAR(25) = NULL,@username AS VARCHAR(25) = NULL,@userId AS INT = NULL,@exceptionDetails AS VARCHAR(MAX) = NULL OUTPUT
)
AS
BEGIN
	SET NOCOUNT ON;
	SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;

	SELECT @invoiceId = NULLIF(@invoiceId, 0), @invoiceNumber = NULLIF(@invoiceNumber, ''), @userId	= ISNULL(@userId, 0),@correlationId = ISNULL(@correlationId, CONVERT(VARCHAR(128), NEWID())), @machineName = COALESCE(@machineName, @@SERVERNAME, 'NA'),@processName = COALESCE(@processName, QUOTENAME(OBJECT_SCHEMA_NAME(@@PROCID)) + '.' + QUOTENAME(OBJECT_NAME(@@PROCID)), 'NA');

	IF (ISNULL(@invoiceId, 0) <= 0 AND ISNULL(@invoiceNumber, '') = '')
	BEGIN
 RAISERROR (N'Either the invoice identifier or the invoice number must be specified', 16, 1);
	END;

	DECLARE @action AS VARCHAR(25) = 'RETRIEVE', @severity AS VARCHAR(25) = 'INFO', @message AS VARCHAR(255) = 'Reports - Retrieved invoice groups / contracts',@messageDetails AS VARCHAR(MAX), @startTime AS DATETIME = GETDATE(), @elapsedTime AS INT, @sendEmail AS BIT = 0;
	
	SELECT @messageDetails = CONVERT(VARCHAR(MAX), (SELECT @invoiceId AS INVID, @invoiceNumber AS INVNUM FOR XML PATH('INPUTS')));

	BEGIN TRY
 SELECT IGC.InvoiceGroupContractID,IGC.InvoiceID,IGC.GroupID,IGC.GroupName,IGC.IsSummaryBilling,IGC.BillingType,IGC.AmountBilled,IGC.AmountPaid,IGC.FiscalYear,IGC.ContractNumber,IGC.ActualAmount,IGC.CloseAmount,IGC.OpenAmount,IGC.AvailableAmount,IGC.CreatedDate,IGC.CreatedBy,IGC.ModifiedDate,IGC.ModifiedBy,b.SummaryAccountNumber
 FROM Invoice.vwInvoiceGroupContracts AS IGC
 INNER JOIN Billing.vwBillingGroups AS b ON IGC.groupid = b.groupid
 WHERE IGC.InvoiceID	= ISNULL(@invoiceId, IGC.InvoiceID)
 AND IGC.InvoiceNumber = ISNULL(@invoiceNumber, IGC.InvoiceNumber)
 ORDER BY IGC.GroupName ASC, IGC.ContractNumber ASC, IGC.FiscalYear DESC;
	END TRY
	BEGIN CATCH
 SELECT @exceptionDetails = ERROR_MESSAGE(), @severity = 'ERROR', @message = 'Reports - Failed to retrieve invoice groups / contracts', @sendEmail = 1;
	END CATCH;

	SET @elapsedTime = DATEDIFF(MILLISECOND, GETDATE(), @startTime);

	EXEC dbo.USP_AppLogCreate @correlationId = @correlationId,@machineName = @machineName,@processName = @processName,@action = @action,@severity = @severity,@message = @message,@messageDetails = @messageDetails,@exceptionDetails = @exceptionDetails,@domain = @domain,@username = @username,@userId = @userId,@sendEmail = @sendEmail,@elapsedTime = @elapsedTime;
END;

