CREATE PROCEDURE [Report].[USP_InvoiceHeaderRetrieve]
(
	@invoiceId AS INT = NULL,@invoiceNumber AS VARCHAR(25) = NULL,@correlationId AS VARCHAR(128) = NULL,@machineName AS VARCHAR(128) = NULL,@processName AS VARCHAR(128) = NULL,@domain AS VARCHAR(25) = NULL,@username AS VARCHAR(25) = NULL,@userId AS INT = NULL,@exceptionDetails AS VARCHAR(MAX) = NULL OUTPUT
)
AS
BEGIN
	SET NOCOUNT ON;
	SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;

	SELECT @invoiceId = NULLIF(@invoiceId, 0), @invoiceNumber = NULLIF(@invoiceNumber, ''), @userId = ISNULL(@userId, 0),@correlationId = ISNULL(@correlationId, CONVERT(VARCHAR(128), NEWID())), @machineName = COALESCE(@machineName, @@SERVERNAME, 'NA'),@processName = COALESCE(@processName, QUOTENAME(OBJECT_SCHEMA_NAME(@@PROCID)) + '.' + QUOTENAME(OBJECT_NAME(@@PROCID)), 'NA');

	IF (ISNULL(@invoiceId, 0) <= 0 AND ISNULL(@invoiceNumber, '') = '')
	BEGIN
 RAISERROR (N'Either the invoice identifier or the invoice number must be specified', 16, 1);
	END;

	DECLARE @action AS VARCHAR(25) = 'RETRIEVE', @severity AS VARCHAR(25) = 'INFO', @message AS VARCHAR(255) = 'Reports - Retrieved invoice header',@messageDetails AS VARCHAR(MAX), @startTime AS DATETIME = GETDATE(), @elapsedTime AS INT, @sendEmail AS BIT = 0;
	
	SELECT @messageDetails = CONVERT(VARCHAR(MAX), (SELECT @invoiceId AS INVID, @invoiceNumber AS INVNUM FOR XML PATH('INPUTS')));

	BEGIN TRY
 SELECT I.InvoiceID,I.InvoiceBillingPeriod,I.InvoiceNumber,I.InvoiceDate,I.InvoiceGroupCount,I.AmountBilled,I.AmountPaid,I.ProviderID,P.ProviderName,SUBSTRING((SELECT DISTINCT ', ' + IGC.GroupName FROM Invoice.vwInvoiceGroupContracts IGC WHERE IGC.InvoiceID = I.InvoiceID FOR XML PATH('')), 2, 1000) AS BillingGroups,I.StatusID,S.StatusDescription,I.CreatedDate,I.CreatedBy,I.ModifiedDate,I.ModifiedBy,I.Active
 FROM Invoice.vwInvoices AS I
 INNER JOIN Billing.vwProviders AS P ON P.ProviderID = I.ProviderID
 INNER JOIN Invoice.vwInvoiceStatuses AS S ON S.StatusID = I.StatusID
 WHERE I.InvoiceID = ISNULL(@invoiceId, I.InvoiceID)
 AND I.InvoiceNumber	= ISNULL(@invoiceNumber, I.InvoiceNumber);
	END TRY
	BEGIN CATCH
 SELECT @exceptionDetails = ERROR_MESSAGE(), @severity = 'ERROR', @message = 'Reports - Failed to retrieve invoice header', @sendEmail = 1;
	END CATCH;

