CREATE PROCEDURE [Report].[USP_InvoiceRetrieve]
(
	@invoiceId AS INT = NULL,@providerId AS INT = NULL,@groupId AS INT = NULL,@fiscalYear AS VARCHAR(4) = NULL,@contractId AS INT = NULL,@billingPeriod AS VARCHAR(6) = NULL,@fromDate AS DATETIME = NULL,@toDate AS DATETIME = NULL,@statusId AS INT = NULL
)
AS
BEGIN
	SET NOCOUNT ON;
	SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;

	SELECT @invoiceId = NULLIF(@invoiceId, 0), @providerId = NULLIF(@providerId, 0), @groupId = NULLIF(@groupId, 0), @fiscalYear = NULLIF(@fiscalYear, ''),@contractId = NULLIF(@contractId, 0), @billingPeriod = NULLIF(@billingPeriod, '0'), @statusId = NULLIF(@statusId, 0);

	SELECT INV.InvoiceNumber AS InvoiceNumber,INV.InvoiceBillingPeriod AS BillingPeriod,SUBSTRING((SELECT DISTINCT ', ' + IGC.GroupName FROM Invoice.vwInvoiceGroupContracts AS IGC WHERE IGC.InvoiceID = INV.InvoiceID FOR XML PATH('')), 2, 1000) AS BillingGroups,SUBSTRING((SELECT DISTINCT ', ' + IGC.ContractNumber FROM Invoice.vwInvoiceGroupContracts AS IGC WHERE IGC.InvoiceID = INV.InvoiceID FOR XML PATH('')), 2, 1000) AS Contracts,SUBSTRING((SELECT DISTINCT ', ' + P.PaymentNumber FROM Invoice.vwInvoicePayments AS P WHERE P.InvoiceID = INV.InvoiceID FOR XML PATH('')), 2, 1000) AS Payments,FORMAT(INV.InvoiceDate, 'M/d/yyyy') AS InvoiceDate,FORMAT(INV.AmountPaid, 'C') AS AmountPaid,INV.StatusDescription AS [Status]
	FROM Invoice.vwInvoices AS INV
	WHERE INV.InvoiceID	= ISNULL(@invoiceId, INV.InvoiceID)
 AND INV.ProviderID = ISNULL(@providerId, INV.ProviderID)
 AND CASE WHEN NOT @billingPeriod IS NULL THEN CASE WHEN INV.InvoiceBillingPeriod = ISNULL(@billingPeriod, INV.InvoiceBillingPeriod) THEN 1 ELSE 0 END ELSE 1 END = 1
 AND CASE WHEN NOT @fromDate IS NULL THEN CASE WHEN CONVERT(VARCHAR(12), INV.InvoiceDate, 112) >= CONVERT(VARCHAR(12), @fromDate, 112) THEN 1 ELSE 0 END ELSE 1 END = 1
 AND CASE WHEN NOT @toDate IS NULL THEN CASE WHEN CONVERT(VARCHAR(12), INV.InvoiceDate, 112) <= CONVERT(VARCHAR(12), @toDate, 112) THEN 1 ELSE 0 END ELSE 1 END = 1
 AND INV.StatusID = ISNULL(@statusId, INV.StatusID)
 AND INV.InvoiceID IN (SELECT InvoiceID FROM Invoice.vwInvoiceGroupContracts
 WHERE CASE WHEN NOT @fiscalYear IS NULL THEN CASE WHEN FiscalYear = @fiscalYear THEN 1 ELSE 0 END ELSE 1 END = 1
 AND GroupID = ISNULL(@groupId, GroupID)
 AND ContractID = ISNULL(@contractId, ContractID))
	ORDER BY INV.CreatedDate DESC;
END;

CREATE PROCEDURE [Report].[USP_InvoiceSummary_Export]
(
	@invoiceId AS INT = NULL,@providerId AS INT = NULL,@groupId AS INT = NULL,@fiscalYear AS VARCHAR(4) = NULL,@contractId AS INT = NULL,@billingPeriod AS VARCHAR(6) = NULL,@fromDate AS DATETIME = NULL,@toDate AS DATETIME = NULL,@statusId AS INT = NULL
)
AS
BEGIN
	SET NOCOUNT ON;
	SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;

	SELECT @invoiceId = NULLIF(@invoiceId, 0), @providerId = NULLIF(@providerId, 0), @groupId = NULLIF(@groupId, 0), @fiscalYear = NULLIF(@fiscalYear, ''),@contractId = NULLIF(@contractId, 0), @billingPeriod = NULLIF(@billingPeriod, '0'), @statusId = NULLIF(@statusId, 0);

	DECLARE @action AS VARCHAR(25) = 'RETRIEVE', @severity AS VARCHAR(25) = 'INFO', @message AS VARCHAR(255) = 'Retrieved report for invoices', @messageDetails AS VARCHAR(MAX),@startTime AS DATETIME = GETDATE(), @elapsedTime AS INT, @sendEmail AS BIT = 0;

	SET @messageDetails = CONVERT(VARCHAR(MAX), (SELECT @invoiceId AS INVID, @providerId AS PRVID, @groupId AS GRPID, @fiscalYear AS FY, @contractId AS CNTRCTID,@billingPeriod AS BILLPRD, @fromDate AS FROMDATE, @toDate AS TODATE, @statusId AS STSID FOR XML PATH('INPUTS')));

	SELECT INV.InvoiceNumber AS [Invoice #],INV.InvoiceBillingPeriod AS [Billing Period],SUBSTRING((SELECT DISTINCT ', ' + IGC.GroupName FROM Invoice.vwInvoiceGroupContracts AS IGC WHERE IGC.InvoiceID = INV.InvoiceID FOR XML PATH('')), 2, 1000) AS [Billing Group(s)],SUBSTRING((SELECT DISTINCT ', ' + IGC.ContractNumber FROM Invoice.vwInvoiceGroupContracts AS IGC WHERE IGC.InvoiceID = INV.InvoiceID FOR XML PATH('')), 2, 1000) AS [Contract(s)],SUBSTRING((SELECT DISTINCT ', ' + P.PaymentNumber FROM Invoice.vwInvoicePayments AS P WHERE P.InvoiceID = INV.InvoiceID FOR XML PATH('')), 2, 1000) AS [Payment(s)],FORMAT(INV.InvoiceDate, 'M/d/yyyy') AS [Invoice Date],FORMAT(INV.AmountPaid, 'C') AS [Amount Paid],INV.StatusDescription AS [Status]
	FROM Invoice.vwInvoices AS INV
	WHERE INV.InvoiceID	= ISNULL(@invoiceId, INV.InvoiceID)
 AND INV.ProviderID = ISNULL(@providerId, INV.ProviderID)
 AND CASE WHEN NOT @billingPeriod IS NULL THEN CASE WHEN INV.InvoiceBillingPeriod = ISNULL(@billingPeriod, INV.InvoiceBillingPeriod) THEN 1 ELSE 0 END ELSE 1 END = 1
 AND CASE WHEN NOT @fromDate IS NULL THEN CASE WHEN CONVERT(VARCHAR(12), INV.InvoiceDate, 112) >= CONVERT(VARCHAR(12), @fromDate, 112) THEN 1 ELSE 0 END ELSE 1 END = 1
 AND CASE WHEN NOT @toDate IS NULL THEN CASE WHEN CONVERT(VARCHAR(12), INV.InvoiceDate, 112) <= CONVERT(VARCHAR(12), @toDate, 112) THEN 1 ELSE 0 END ELSE 1 END = 1
 AND INV.StatusID = ISNULL(@statusId, INV.StatusID)
 AND INV.InvoiceID IN (SELECT InvoiceID FROM Invoice.vwInvoiceGroupContracts
 WHERE CASE WHEN NOT @fiscalYear IS NULL THEN CASE WHEN FiscalYear = @fiscalYear THEN 1 ELSE 0 END ELSE 1 END = 1
 AND GroupID = ISNULL(@groupId, GroupID)
 AND ContractID = ISNULL(@contractId, ContractID))
	ORDER BY INV.CreatedDate DESC;
END;
