CREATE  FUNCTION [Budget].[CalcBudgetRateSurplus] 
(
	@BudgetRate FLOAT,
	@ActualRate FLOAT,
	@BudgetUsage EnergyUnit
)
RETURNS FLOAT AS  
BEGIN 

declare @RateSurplus FLOAT
set @RateSurplus = ( ISNULL(@BudgetRate, 0) - ISNULL(@ActualRate, 0) ) * CAST(ISNULL(@BudgetUsage, 0) AS FLOAT)

return 	@RateSurplus					 
END

CREATE  FUNCTION [Budget].[CalcBudgetUsageSurplus] 
(
	@BudgetUsage EnergyUnit,
	@ActualUsage EnergyUnit,
	@BudgetRate FLOAT
)

RETURNS FLOAT AS  
BEGIN 


declare @UsageSurplus FLOAT

set @UsageSurplus = CAST((ISNULL(@BudgetUsage, 0) - ISNULL(@ActualUsage, 0)) AS FLOAT) * ISNULL(@BudgetRate, 0)


return 	@UsageSurplus					 
END

CREATE FUNCTION [Common].[DetermineFindBillingPeriodElseProject]
(
	@AccountNumber AS VARCHAR(15)
	,@MeterNumber AS VARCHAR(10)
	,@ToDate AS VARCHAR(8)
	,@FromOrTo AS VARCHAR(1)
	,@BillingCycle AS VARCHAR(1)
	,@Tariff AS VARCHAR(3)
	,@Utility AS INT
)
RETURNS VARCHAR(6)
AS  
BEGIN
	DECLARE @BillingPeriod AS VARCHAR(6), @WDNumber AS VARCHAR(2), @FoundRowCount AS INT
		,@NationalGridWest AS INT, @ConEdison AS INT;
	
	SELECT @NationalGridWest = 2, @ConEdison = 9;
	
	IF (@MeterNumber IS NULL)
	BEGIN
		SELECT @BillingPeriod = AB.BillingPeriodRevision
		FROM Billing.Account AS A
			INNER JOIN Billing.AccountBilling AS AB ON A.AccountSeqid = AB.AccountSeqid
		WHERE A.UtilityAccountProvider = @Utility
			AND A.CurrentAccountNumber = @AccountNumber
			AND AB.FromDate < @ToDate AND  @ToDate <= AB.ToDate;

		SELECT @FoundRowCount = @@rowcount;
	END;
	ELSE
	BEGIN
		SELECT @BillingPeriod = MB.BillingPeriodRevision
		FROM Billing.Account AS A
			INNER JOIN Billing.AccountExchangeMeterTrack AS AE ON A.AccountSeqid = AE.OriginalAccountSeqid
			INNER JOIN Billing.Meter AS M ON AE.OriginalMeterSeqid = M.MeterSeqid
			INNER JOIN Billing.MeterBilling AS MB ON AE.AccountExchangeMeterTrackSeqid = MB.OriginalAccountNumber
		WHERE A.UtilityAccountProvider = @Utility
			AND A.CurrentAccountNumber = @AccountNumber
			AND M.CurrentMeterNumber = @MeterNumber
			AND MB.FromDate < @ToDate AND @ToDate <= MB.ToDate;

		SELECT @FoundRowCount = @@rowcount;
	END;

	IF (@FoundRowCount =  0)
	BEGIN
		IF(@Utility = @ConEdison)
		BEGIN 
			SELECT @BillingPeriod = dbo.DetermineConEdisonBillingPeriodProjected (@ToDate, @Tariff,@Utility);
		END;
		ELSE IF (@Utility = @NationalGridWest)
		BEGIN
			SELECT @WDNumber = SUBSTRING(@AccountNumber, 12, 2);
			SELECT @BillingPeriod = NationalGridWest.DetermineBillingPeriod(@BillingCycle, @ToDate, @FromOrTo, @WDNumber);
		END;
		ELSE
		BEGIN
			SELECT @BillingPeriod = dbo.DetermineBillingPeriodProjected(@ToDate, @Utility);
		END;
	END;

	RETURN @BillingPeriod;
END;

CREATE FUNCTION [Common].[GetDefaultEnergySubType]
(@EnergyTypeID AS INT)
