CREATE  FUNCTION [Common].[udf_GetAccountSeqId]
(
	@AccountNumber AS VARCHAR(15)
)
RETURNS INT
AS
BEGIN
	DECLARE @LastAccountSeqid AS INT;

	IF EXISTS(SELECT * FROM Billing.Account WHERE OriginalAccountNumber = @AccountNumber AND IsActive = 1)
	BEGIN
		SELECT @LastAccountSeqid = AccountSeqid FROM Billing.Account
		WHERE DateAdded = (SELECT MAX(DateAdded) FROM Billing.Account WHERE OriginalAccountNumber = @AccountNumber AND IsActive = 1 GROUP BY OriginalAccountNumber)
		AND OriginalAccountNumber = @AccountNumber;
	END;
	ELSE
	BEGIN
		SELECT @LastAccountSeqid = AccountSeqid FROM Billing.Account
		WHERE DateAdded = (SELECT MAX(DateAdded) FROM Billing.Account WHERE OriginalAccountNumber = @AccountNumber GROUP BY OriginalAccountNumber)
		AND OriginalAccountNumber = @AccountNumber;
	END;

	RETURN @LastAccountSeqid;
END;

