CREATE FUNCTION [dbo].[CalculateDeltaBillingPeriods]
(
	@FromBillingPeriod AS VARCHAR(6),
	@ToBillingPeriod AS VARCHAR(6),
	@BillingCycle AS VARCHAR(1)
)
RETURNS INT AS
BEGIN
	DECLARE @monthInCycle AS INT, @yearDifference AS INT, @monthDifference AS INT, @numberOfPeriod AS INT;

	SET @monthInCycle = dbo.MonthsInBillingCycle(@BillingCycle);
	SET @yearDifference = CAST(LEFT(@ToBillingPeriod, 4) AS INT) - CAST(LEFT(@FromBillingPeriod, 4) AS INT);
	SET @monthDifference = CAST(RIGHT(@ToBillingPeriod, 2) AS INT) - CAST(RIGHT(@FromBillingPeriod, 2) AS INT) + 1;
	SET @monthDifference = @monthDifference + 12 * @yearDifference;
	SET @numberOfPeriod = CEILING(CAST(@monthDifference AS FLOAT) / @monthInCycle);
	RETURN @numberOfPeriod;
END;

CREATE FUNCTION [dbo].[CalculateGasThermFactor]
(
	@CCF AS dbo.EnergyUnit,
	@Therms AS dbo.EnergyUnit
)
RETURNS dbo.ThermsFactor AS
BEGIN
	IF (@CCF = 0 OR ISNULL(@CCF, 0) = 0 OR ISNULL(@Therms, 0) = 0)
		RETURN 1.000;
	
	RETURN (@Therms*1.0)/@CCF;
END;

