CREATE FUNCTION [dbo].[CreateSummerPeriod]
(
	@CurrentBillingPeriod AS VARCHAR(6),
	@StartEnd AS VARCHAR(1)
)
RETURNS VARCHAR(6) AS
BEGIN
	DECLARE @SummerYear AS VARCHAR(4), @mm AS INTEGER, @yyyy AS INTEGER;

	SELECT @mm = CAST(SUBSTRING(@CurrentBillingPeriod,5,2) AS INTEGER),
		@yyyy = CAST(SUBSTRING(@CurrentBillingPeriod,1,4) AS INTEGER);
	
	SET @SummerYear = CAST((@yyyy+1) AS VARCHAR(4));

	IF (6 <= @mm AND @mm <= 12)
		SET @SummerYear = CAST((@yyyy) AS VARCHAR(4));

	IF @StartEnd ='S'
		RETURN @SummerYear + '07';
	ELSE
		RETURN @SummerYear + '09';

	RETURN '1876';
END;

CREATE FUNCTION [dbo].[DefineEnergySourceUnit]
(
	@EnergyDescription AS INT
)
RETURNS VARCHAR(20)
AS
BEGIN
	IF (@EnergyDescription = 1)
		RETURN 'kWh';
	ELSE IF (@EnergyDescription = 2)
		RETURN 'Therms';
	ELSE IF (@EnergyDescription = 3)
		RETURN 'MLbs';
	ELSE IF (@EnergyDescription = 4)
		RETURN 'Chilled Water';
	ELSE IF (@EnergyDescription = 5)
		RETURN 'Oil';
	ELSE RETURN 0;

	RETURN 0;
END;

