CREATE   FUNCTION [dbo].[PadMeterZeros]  (@meterNumber varchar(7))
--
RETURNS varchar(7) AS  
BEGIN 
declare @lenOfMeterNumber integer
declare @trimmedMeterNumber varchar(7)
set @trimmedMeterNumber =  ltrim(rtrim(@MeterNumber))
set @lenOfMeterNumber = len( @trimmedMeterNumber )
if @lenOfMeterNumber  = 0  return '0000000'
else if @lenOfMeterNumber  = 1  return '000000' + cast (@trimmedMeterNumber as varchar(1))
else if @lenOfMeterNumber  = 2  return '00000' + cast (@trimmedMeterNumber as varchar(2))
else if @lenOfMeterNumber  = 3  return '0000' + cast (@trimmedMeterNumber as varchar(3))
else if @lenOfMeterNumber  = 4  return '000' + cast (@trimmedMeterNumber as varchar(4))
else if @lenOfMeterNumber  = 5  return '00' + cast (@trimmedMeterNumber as varchar(5))
else if @lenOfMeterNumber  = 6  return '0' + cast (@trimmedMeterNumber as varchar(6))
return @MeterNumber
END

CREATE FUNCTION [dbo].[ProcessServiceTariff]
(
	@ServiceTariff AS VARCHAR(14),
	@CommodityOrDelivery AS VARCHAR(1)
)
RETURNS VARCHAR(3) AS
BEGIN
	IF (@ServiceTariff = '16-1 /C-1 /062')
	BEGIN
      IF (@CommodityOrDelivery = 'D')
          RETURN '062';
      ELSE
          RETURN '001';
	END;
	ELSE IF (@ServiceTariff = '16-2 /H-1 /069')
	BEGIN
		IF (@CommodityOrDelivery = 'D')
			RETURN '069';
		ELSE
			RETURN '002';
	END;
	ELSE IF (@ServiceTariff = '16-4 /A-1 /091')
	BEGIN
		IF (@CommodityOrDelivery = 'D')
			RETURN '911';
		ELSE
			RETURN '041';
	END;
	ELSE IF (@ServiceTariff = '16-4 /A-1 /091')
	BEGIN
		IF (@CommodityOrDelivery = 'D')
			RETURN '912';
		ELSE
			RETURN '042';
	END;
	ELSE IF (@ServiceTariff = '16-4 /A-2 /093')
	BEGIN
		IF (@CommodityOrDelivery = 'D')
			RETURN '931';
		ELSE
			RETURN '041';
	END;
	ELSE IF (@ServiceTariff = '16-4 /A-3 /098')
	BEGIN
		IF (@CommodityOrDelivery = 'D')
			RETURN '981';
		ELSE
			RETURN '041';
	END;

	RETURN 'ERR';
END;

CREATE FUNCTION [dbo].[StripNumbersOffConstraintName]
(
