END;

CREATE FUNCTION [dbo].[UDF_SplitToTable]
(
	@Input AS VARCHAR(MAX),
	@Character AS CHAR(1)
)
RETURNS @Output TABLE (ID VARCHAR(100))
AS
BEGIN
    DECLARE @StartIndex AS INT = 1, @EndIndex AS INT;
 
    IF SUBSTRING(@Input, LEN(@Input) - 1, LEN(@Input)) <> @Character
    BEGIN
		SET @Input = @Input + @Character;
    END;
 
    WHILE (CHARINDEX(@Character, @Input) > 0)
    BEGIN
        SET @EndIndex = CHARINDEX(@Character, @Input);
           
        INSERT INTO @Output(ID)
        SELECT SUBSTRING(@Input, @StartIndex, @EndIndex - 1);
           
        SET @Input = SUBSTRING(@Input, @EndIndex + 1, LEN(@Input));
    END;
 
    RETURN;
END;

CREATE FUNCTION [dbo].[ZeroTrailingAddress]
(
	@Address AS VARCHAR(25)
)
RETURNS VARCHAR(25) AS
BEGIN
	DECLARE @CovertToAddressLength AS INT, @AddressWithLeadingZeroes AS VARCHAR(3),
	@AsciiSpace AS CHAR(1); 

	SELECT @AsciiSpace =  CAST(' ' AS CHAR(1)), @CovertToAddressLength = LEN(RTRIM(@Address));

	IF (@CovertToAddressLength < 25) 
		SET @AddressWithLeadingZeroes = RTRIM(@Address) + REPLICATE(@AsciiSpace, 25 - @CovertToAddressLength);

	RETURN @AddressWithLeadingZeroes;
END;

CREATE FUNCTION [Factors].[uftn_GetDegreeDayInformationForReporting]
(
	@BillingPeriod AS VARCHAR(6)
)
RETURNS @DegreeDayInformation TABLE
	(BillingPeriod VARCHAR(6),
	CurrentMonthDescription VARCHAR(20),
	CurrentFiscalYear VARCHAR(4),
	PriorFiscalYear VARCHAR(4),
	CurrentFiscalYearBillingPeriod VARCHAR(30),
	CurrentMonthlyBaseCoolingDegreeDays INT,
	CurrentMonthlyBaseHeatingDegreeDays INT,
	PriorMonthlyBaseCoolingDegreeDays INT,
	PriorMonthlyBaseHeatingDegreeDays INT,
	CurrentFiscalYearBaseCoolingDegreeDays INT,
	CurrentFiscalYearBaseHeatingDegreeDays INT,
	CurrentFiscalYearNormalCoolingDegreeDays INT,
	CurrentFiscalYearNormalHeatingDegreeDays INT,
	PriorFiscalYearBillingPeriod VARCHAR(30),
	PriorFiscalYearBaseCoolingDegreeDays INT,
	PriorFiscalYearBaseHeatingDegreeDays INT,
	PriorFiscalYearNormalCoolingDegreeDays INT,
	PriorFiscalYearNormalHeatingDegreeDays INT ,
	DeltaChangeInYearCoolingDegreeDays NUMERIC(6,4),
	DeltaChangeInYearHeatingDegreeDays NUMERIC(6,4))
AS
BEGIN
	DECLARE @Month AS VARCHAR(2), @MonthName AS VARCHAR(20), @Year AS VARCHAR(4), @PriorYear AS VARCHAR(4), @BillingPeriodOneYearPrior AS VARCHAR(6)
		,@StartCurrentFiscalYear AS VARCHAR(6), @StartPriorFiscalYear AS VARCHAR(6), @CurrentMonthlyBaseCoolingDegreeDays AS INT, @CurrentMonthlyBaseHeatingDegreeDays AS INT
		,@PriorMonthlyBaseCoolingDegreeDays AS INT, @PriorMonthlyBaseHeatingDegreeDays AS INT, @CurrentFiscalYearBillingPeriod AS VARCHAR(30)
		,@CurrentFiscalYearBaseCoolingDegreeDays AS INT, @CurrentFiscalYearBaseHeatingDegreeDays AS INT, @CurrentFiscalYearNormalCoolingDegreeDays AS INT
		,@CurrentFiscalYearNormalHeatingDegreeDays AS INT, @PriorFiscalYearBaseCoolingDegreeDays AS INT, @PriorFiscalYearBaseHeatingDegreeDays AS INT
		,@PriorFiscalYearNormalCoolingDegreeDays AS INT, @PriorFiscalYearNormalHeatingDegreeDays AS INT, @YearMinusOne AS INT
		,@DeltaChangeInYearCoolingDegreeDays AS NUMERIC(6, 4), @DeltaChangeInYearHeatingDegreeDays AS NUMERIC(6, 4);

	SELECT @Month = SUBSTRING(@BillingPeriod, 5, 2), @Year = SUBSTRING(@BillingPeriod, 1, 4), @MonthName = DATENAME(mm, @BillingPeriod +'01')
		,@PriorYear = SUBSTRING(dbo.CreatePreviousPeriod(@BillingPeriod, 'Y'), 1, 4), @BillingPeriodOneYearPrior = dbo.CreatePreviousPeriod(@BillingPeriod, 'Y');
	SELECT @CurrentFiscalYearBillingPeriod = @MonthName + ', ' + SUBSTRING(@BillingPeriod, 1, 4);
	SELECT @CurrentMonthlyBaseCoolingDegreeDays = BaseCoolingDegreeDays, @CurrentMonthlyBaseHeatingDegreeDays = BaseHeatingDegreeDays FROM Factors.DegreeDayBase WHERE BillingPeriod = @BillingPeriod;
	SELECT @PriorMonthlyBaseCoolingDegreeDays = BaseCoolingDegreeDays, @PriorMonthlyBaseHeatingDegreeDays = BaseHeatingDegreeDays FROM Factors.DegreeDayBase WHERE BillingPeriod = @BillingPeriodOneYearPrior;

	IF (6 < @month AND @month < 13)  
	BEGIN	
		SET @StartCurrentFiscalYear = @Year + '07';
	END;
	ELSE 
	BEGIN
		SET @YearMinusOne = CAST(@Year AS INT) - 1;
		SET @StartCurrentFiscalYear = CAST(@YearMinusOne AS VARCHAR(4)) + '07';
	END;

	SELECT @CurrentFiscalYearBaseCoolingDegreeDays = SUM(DDB.BaseCoolingDegreeDays), @CurrentFiscalYearBaseHeatingDegreeDays = SUM(DDB.BaseHeatingDegreeDays), 
		@CurrentFiscalYearNormalCoolingDegreeDays = SUM(DDA.NormalCoolingDegreeDays), @CurrentFiscalYearNormalHeatingDegreeDays = SUM(DDA.NormalHeatingDegreeDays)
	FROM Factors.DegreeDayBase AS DDB INNER JOIN Factors.DegreeDayNormalized30YearAverage AS DDA ON DDB.MM = DDA.MM
	WHERE @StartCurrentFiscalYear <= DDB.BillingPeriod AND DDB.BillingPeriod <= @BillingPeriod;
	
	IF (6 < @month AND @month < 13)  
	BEGIN	
		SET @StartPriorFiscalYear = @PriorYear + '07'; 
	END	
	ELSE 
	BEGIN
		SET @YearMinusOne = CAST(@PriorYear AS INT)	- 1;
		SET @StartPriorFiscalYear = CAST(@YearMinusOne AS VARCHAR(4)) + '07';
	END;

	SELECT @PriorFiscalYearBaseCoolingDegreeDays= SUM(DDB.BaseCoolingDegreeDays), @PriorFiscalYearBaseHeatingDegreeDays = SUM(DDB.BaseHeatingDegreeDays), 
		@PriorFiscalYearNormalCoolingDegreeDays = SUM(DDA.NormalCoolingDegreeDays), @PriorFiscalYearNormalHeatingDegreeDays = SUM(DDA.NormalHeatingDegreeDays)
	FROM Factors.DegreeDayBase AS DDB INNER JOIN Factors.DegreeDayNormalized30YearAverage AS DDA ON DDB.MM = DDA.MM
	WHERE @StartPriorFiscalYear <= DDB.BillingPeriod AND DDB.BillingPeriod  <= @BillingPeriodOneYearPrior;
	
	SELECT @DeltaChangeInYearCoolingDegreeDays = CASE WHEN @PriorFiscalYearBaseCoolingDegreeDays = 0 THEN 0
		ELSE CAST(@CurrentFiscalYearBaseCoolingDegreeDays AS NUMERIC(12, 4)) / CAST(@PriorFiscalYearBaseCoolingDegreeDays AS NUMERIC(12, 4)) END;
	SELECT @DeltaChangeInYearHeatingDegreeDays = CASE WHEN @PriorFiscalYearBaseHeatingDegreeDays = 0 THEN 0
		ELSE CAST(@CurrentFiscalYearBaseHeatingDegreeDays AS NUMERIC(12, 4)) / CAST(@PriorFiscalYearBaseHeatingDegreeDays AS NUMERIC(12, 4)) END;
	
	INSERT INTO @DegreeDayInformation
		(BillingPeriod,
		CurrentMonthDescription,
		CurrentFiscalYear,
		PriorFiscalYear,
		CurrentFiscalYearBillingPeriod,
		CurrentMonthlyBaseCoolingDegreeDays,
		CurrentMonthlyBaseHeatingDegreeDays,
		PriorMonthlyBaseCoolingDegreeDays,
		PriorMonthlyBaseHeatingDegreeDays,
		CurrentFiscalYearBaseCoolingDegreeDays,
		CurrentFiscalYearBaseHeatingDegreeDays,
		CurrentFiscalYearNormalCoolingDegreeDays,
		CurrentFiscalYearNormalHeatingDegreeDays,
		PriorFiscalYearBillingPeriod,
		PriorFiscalYearBaseCoolingDegreeDays,
		PriorFiscalYearBaseHeatingDegreeDays,
		PriorFiscalYearNormalCoolingDegreeDays,
		PriorFiscalYearNormalHeatingDegreeDays,
		DeltaChangeInYearCoolingDegreeDays,
		DeltaChangeInYearHeatingDegreeDays)
	VALUES (@BillingPeriod,
		DATENAME(mm, @BillingPeriod + '01'),
		[dbo].[CreateFiscalYear] (@BillingPeriod,'C'), -- 'C'urrent Fiscal Year
		[dbo].[CreateFiscalYear] (@BillingPeriod,'P'), -- 'P'rior Fiscal Year
		@CurrentFiscalYearBillingPeriod,
		@CurrentMonthlyBaseCoolingDegreeDays,
		@CurrentMonthlyBaseHeatingDegreeDays,
		@PriorMonthlyBaseCoolingDegreeDays,
		@PriorMonthlyBaseHeatingDegreeDays,
		@CurrentFiscalYearBaseCoolingDegreeDays,
		@CurrentFiscalYearBaseHeatingDegreeDays,
		@CurrentFiscalYearNormalCoolingDegreeDays,
		@CurrentFiscalYearNormalHeatingDegreeDays,
		@monthName + ', ' + SUBSTRING(dbo.CreatePreviousPeriod(@BillingPeriod,'Y'), 1, 4),
		@PriorFiscalYearBaseCoolingDegreeDays,
		@PriorFiscalYearBaseHeatingDegreeDays,
		@PriorFiscalYearNormalCoolingDegreeDays,
		@PriorFiscalYearNormalHeatingDegreeDays,
		@DeltaChangeInYearCoolingDegreeDays - 1.0,
		@DeltaChangeInYearHeatingDegreeDays - 1.0);
	RETURN;
END;

CREATE FUNCTION [ManualBill].[DetermineBillingPeriodRevisionByToDate]
(
	@UtilityCompany AS dbo.seqid,	
	@InvoiceBillingGroup AS dbo.seqid,	
	@ToDate AS dbo.ToDate,
	@TripNumber AS VARCHAR(2),
	@BillingCycle AS VARCHAR(1)
)
RETURNS varchar(6)
AS
BEGIN
	--******************************************************************************************************
	--* Mohammed Belarrem
	--*	10/05/2009 Creation:
	--*	10/09/2009 Update  : I added the logic to compare against the Todate - 6
	--******************************************************************************************************
	DECLARE @BillingPeriod AS dbo.BillingPeriod, @NumberOfDays AS INT, @BillingPeriodDays AS INT
		,@DeltaPeriods AS INT, @CurrentBillingPeriod AS dbo.BillingPeriod, @TopMeterReadingBillingPeriod AS dbo.BillingPeriod;
		
	SELECT @CurrentBillingPeriod = BillingPeriod FROM ManualBill.ManualBillingApplicationTimeFrame WHERE CurrentProcessingPeriod = 'Y';
		
	SELECT TOP 1 @TopMeterReadingBillingPeriod = BillingPeriod
	FROM ManualBill.ReadingScheduleManualBilling
	WHERE InvoiceAccountGroupSeqid = @InvoiceBillingGroup AND TripNumber = @TripNumber AND BillingCycle = @BillingCycle
	ORDER BY BillingPeriod DESC;
		
	-- check if there are records in the ReadingScheduleManualBilling then evaluate BillingPeriod otherwise skip it
	IF (@TopMeterReadingBillingPeriod >= @CurrentBillingPeriod)
	BEGIN
		SELECT TOP 1  @BillingPeriod = BillingPeriod
		FROM ManualBill.ReadingScheduleManualBilling
		WHERE InvoiceAccountGroupSeqid = @InvoiceBillingGroup AND MeterReadingFromDate <= @ToDate AND TripNumber = @TripNumber AND BillingCycle = @BillingCycle
		ORDER BY BillingPeriod DESC;
	END;

	-- overshoot prevention
	IF (@CurrentBillingPeriod < @BillingPeriod)
	BEGIN
		SET @BillingPeriod = @CurrentBillingPeriod;
	END;

	RETURN @BillingPeriod;
END;

--******************************************************************************************************
--* estimates the Billing Period Revision based on the to , from the First Period Canceled
--* this function became a necessity since the data entry opperator only inputs the First period canceled  
--* and since the assumption that all rebills span from period P until the current billing period falls apart
--* in case of back bills we needed a way to get the billingperiod revision automatically.
--* and since the MeterReadingSchedule is not reliable he function estimated the period when there is no record 
--*
--* Mohammed Belarrem
--*	created 07/31/2009
--******************************************************************************************************
CREATE FUNCTION [ManualBill].[DetermineBillingPeriodRevisionUsingToDateAndFirstPeriodCanceled]
(
	@UtilityCompany AS dbo.seqid,	
	@InvoiceBillingGroup AS dbo.seqid,	
	@FromDate AS dbo.FromDate,
	@ToDate AS dbo.ToDate,
	@FirstPeriodCanceled AS dbo.BillingPeriod,
	@TripNumber AS VARCHAR(2),
	@BillingCycle AS VARCHAR(1)
)
RETURNS VARCHAR(6)
AS
BEGIN	
	DECLARE @BillingPeriodRevision AS dbo.BillingPeriod, @NumberOfDays AS INT, @BillingPeriodDays AS INT
		,@DeltaPeriods AS INT, @CurrentBillingPeriod AS dbo.BillingPeriod, @TopMeterReadingBillingPeriod AS dbo.BillingPeriod;
		
	SELECT @CurrentBillingPeriod = BillingPeriod FROM ManualBill.ManualBillingApplicationTimeFrame WHERE CurrentProcessingPeriod = 'Y';
		
	SELECT TOP 1 @TopMeterReadingBillingPeriod = BillingPeriod
	FROM ManualBill.ReadingScheduleManualBilling
	WHERE InvoiceAccountGroupSeqid = @InvoiceBillingGroup AND TripNumber = @TripNumber AND BillingCycle = @BillingCycle
	ORDER BY BillingPeriod DESC;
		
	-- check if there are records in the ReadingScheduleManualBilling then evaluate BillingPeriodRevision otherwise skip it
	IF (@TopMeterReadingBillingPeriod >= @CurrentBillingPeriod)
	BEGIN
		SELECT TOP 1 @BillingPeriodRevision = BillingPeriod
		FROM ManualBill.ReadingScheduleManualBilling
		WHERE InvoiceAccountGroupSeqid = @InvoiceBillingGroup AND MeterReadingToDate <= @ToDate AND BillingPeriod >= @FirstPeriodCanceled AND TripNumber = @TripNumber AND BillingCycle = @BillingCycle
		ORDER BY BillingPeriod DESC;
	END;
		
	-- if there are no records in the ReadingScheduleManualBilling estimate it
	IF (@BillingPeriodRevision IS NULL)
	BEGIN
		SET @NumberOfDays = CASE @BillingCycle
							WHEN 'M' THEN 28
							WHEN 'B' THEN 56
							WHEN 'Q' THEN 84
							WHEN 'Y' THEN 350
							ELSE 28
							END;
             
		SELECT @BillingPeriodDays = dbo.CalculateNumberOfBillingDays(@FromDate, @ToDate);
		SET @DeltaPeriods = ROUND( (CAST(@BillingPeriodDays AS FLOAT))/ @NumberOfDays, 0);
		
		-- set default
		SET @BillingPeriodRevision = @FirstPeriodCanceled;
		
		WHILE (@DeltaPeriods > 1)
		BEGIN
			SET @BillingPeriodRevision = dbo.CalculateNextBillingPeriod(@BillingPeriodRevision, @BillingCycle);
			SET @DeltaPeriods = @DeltaPeriods - 1;
		END;
	END;

	-- overshoot prevention
	IF (@CurrentBillingPeriod < @BillingPeriodRevision)
	BEGIN
		SET @BillingPeriodRevision = @CurrentBillingPeriod;
	END;

	RETURN @BillingPeriodRevision;
END;

CREATE FUNCTION [ManualBill].[DetermineFirstPeriodCanceledByFromDate]
(
	@UtilityCompany dbo.seqid,	
	@InvoiceBillingGroup dbo.seqid,	
	@FromDate dbo.FromDate,
	@TripNumber varchar(2),
	@BillingCycle varchar(1)
)
RETURNS varchar(6)
AS
BEGIN
	--******************************************************************************************************
	--* Mohammed Belarrem
	--*	10/05/2009 created:	
	--* 10/09/2009 updated:	
	--******************************************************************************************************
		
	DECLARE @BillingPeriod AS dbo.BillingPeriod, @NumberOfDays AS INT,  @BillingPeriodDays AS INT, @DeltaPeriods AS INT
		,@CurrentBillingPeriod AS dbo.BillingPeriod, @TopMeterReadingBillingPeriod AS dbo.BillingPeriod;
		
	SELECT @CurrentBillingPeriod = BillingPeriod FROM ManualBill.ManualBillingApplicationTimeFrame WHERE CurrentProcessingPeriod = 'Y';
		
	SELECT  top 1   @TopMeterReadingBillingPeriod = BillingPeriod
	FROM ManualBill.ReadingScheduleManualBilling
	WHERE InvoiceAccountGroupSeqid = @InvoiceBillingGroup AND TripNumber = @TripNumber AND BillingCycle = @BillingCycle
	ORDER BY BillingPeriod DESC;
		
	-- check if there are records in the ReadingScheduleManualBilling then evaluate BillingPeriod otherwise skip it
	IF ( @TopMeterReadingBillingPeriod >= @CurrentBillingPeriod )
	BEGIN
		SELECT TOP 1 @BillingPeriod = BillingPeriod
		FROM ManualBill.ReadingScheduleManualBilling
		WHERE dbo.ConvertDatetimeToYYYYMMDD(DATEADD(DAY, -6, dbo.ConvertYYYYMMDDToDatetime(MeterReadingToDate))) > @FromDate
			AND TripNumber = @TripNumber AND BillingCycle = @BillingCycle AND MeterReadingFromDate <= @FromDate AND InvoiceAccountGroupSeqid = @InvoiceBillingGroup
		ORDER BY BillingPeriod DESC;
	END;
		
	-- overshoot prevention
	IF (@CurrentBillingPeriod < @BillingPeriod)
	BEGIN
		SET @BillingPeriod = @CurrentBillingPeriod;
	END;

	RETURN @BillingPeriod;
END;

CREATE FUNCTION [ManualBill].[GetPreviousPeriodEnergyUsage]
(
	@CurrentAccountNumver AS VARCHAR(15),
	@BillingPeriod AS VARCHAR(6)
)
RETURNS INT
AS
BEGIN
	DECLARE @EnergyUsage AS INT;
	
	SELECT TOP(1)  @EnergyUsage = AB.AccountEnergyUsage
	FROM ManualBill.AccountManualBillingHeader AS AB
		INNER JOIN Billing.Account AS A ON AB.UniqueAccountSeqID = A.UniqueAccountSeqID AND A.IsCurrenTRecord = 'Y'
	WHERE A.AccountStatus IN ('AC', '46')
		AND A.CurrentAccountNumber = @CurrentAccountNumver
		AND AB.BillingPeriod < @BillingPeriod
	ORDER BY AB.BillingPeriod DESC;
	
	RETURN ISNULL(@EnergyUsage,0);
END;

CREATE FUNCTION [ManualBill].[GetPreviousPeriodEnergyUsage2]
(
	@UniqueAccountSeqId AS INT,
