CREATE FUNCTION [Nypa].[DetermineAccountBillingGroupByCoversheetDescription]
(
	@ServiceClassification AS VARCHAR(14),
	@IsTimeOfDay AS VARCHAR(14),
	@BillingPeriod AS VARCHAR(6),
	@RevisedBillingPeriod AS VARCHAR(6)
)
RETURNS VARCHAR(64) AS
BEGIN 
	DECLARE @CoverSheetCategory AS VARCHAR(64);
	SELECT
		@CoverSheetCategory = CASE
		WHEN @ServiceClassification = 'GOV/NYC/080' AND @BillingPeriod = @RevisedBillingPeriod THEN 'STREETLIGHTING'
		WHEN @ServiceClassification = 'GOV/NYC/080' AND @BillingPeriod <> @RevisedBillingPeriod THEN 'STREETLIGHTING ADJUSTMENT'
		WHEN @IsTimeOfDay = 'N' AND @BillingPeriod = @RevisedBillingPeriod THEN 'METERED: CONVENTIONAL'
		WHEN @IsTimeOfDay = 'N' AND @BillingPeriod <> @RevisedBillingPeriod THEN 'METERED ADJUSTMENTS: CONVENTIONAL'
		WHEN @IsTimeOfDay = 'Y' AND @BillingPeriod = @RevisedBillingPeriod THEN 'METERED: TOD'
		WHEN @IsTimeOfDay = 'Y' AND @BillingPeriod <> @RevisedBillingPeriod THEN 'METERED ADJUSTMENTS: TOD'
		ELSE '-1'
	END;
	RETURN @CoverSheetCategory;
END;

CREATE FUNCTION [Nypa].[DetermineAccountBillingGroupByCoversheetSeqid]
(
	@ServiceClassification AS VARCHAR(14),
	@IsTimeOfDay AS VARCHAR(14),
	@BillingPeriod AS VARCHAR(6),
	@RevisedBillingPeriod AS VARCHAR(6)
)
RETURNS INT AS
BEGIN
	DECLARE @CoverSheetCategory AS INT;

	SELECT
		@CoverSheetCategory = CASE
		WHEN @ServiceClassification = 'GOV/NYC/080' AND @BillingPeriod = @RevisedBillingPeriod THEN 5
		WHEN @ServiceClassification = 'GOV/NYC/080' AND @BillingPeriod <> @RevisedBillingPeriod THEN 6
		WHEN @IsTimeOfDay = 'N' AND @BillingPeriod = @RevisedBillingPeriod THEN 1
		WHEN @IsTimeOfDay = 'N' AND @BillingPeriod <> @RevisedBillingPeriod THEN 3
		WHEN @IsTimeOfDay = 'Y' AND @BillingPeriod = @RevisedBillingPeriod THEN 2
		WHEN @IsTimeOfDay = 'Y' AND @BillingPeriod <> @RevisedBillingPeriod THEN 4
		ELSE -1
		END;
	RETURN @CoverSheetCategory;
END;

CREATE FUNCTION [Published].[DetermineAccountLevelEstimatedOrActualBilling]
(
	@PublishedBillingPeriod AS VARCHAR(6)
	,@BillingPeriod AS VARCHAR(6)
	,@OriginalAccountNumber AS VARCHAR(15)
)
RETURNS VARCHAR(3)
AS
BEGIN
	DECLARE @EstimatedReading AS VARCHAR(3);

	SELECT @EstimatedReading = MAX(EstimatedReading)
	FROM Published.TemporalAccountMeterLevelRawDataForCurrentPeriod
	WHERE EffectiveStartPeriod <= @PublishedBillingPeriod AND EffectiveEndPeriod > @PublishedBillingPeriod
	GROUP BY BillingPeriod, OriginalAccountNumber
	HAVING BillingPeriod = @BillingPeriod AND OriginalAccountNumber = @OriginalAccountNumber;

	IF(@@rowcount = 0)
	BEGIN
		SET @EstimatedReading = 'EST';
	END;

	RETURN @EstimatedReading;
END;

